CREATE TABLE  IF NOT EXISTS `sys_log` (
  `id` varchar(64) NOT NULL COMMENT STRINGDECODE('编号'),
  `type` char(1) DEFAULT '1' COMMENT STRINGDECODE('日志类型'),
  `title` varchar(255) DEFAULT '' COMMENT STRINGDECODE('日志标题'),
  `create_by` varchar(64) DEFAULT NULL COMMENT STRINGDECODE('创建者'),
  `create_date` datetime DEFAULT NULL COMMENT STRINGDECODE('创建时间'),
  `remote_addr` varchar(255) DEFAULT NULL COMMENT STRINGDECODE('操作IP地址'),
  `user_agent` varchar(255) DEFAULT NULL COMMENT STRINGDECODE('用户代理'),
  `request_uri` varchar(255) DEFAULT NULL COMMENT STRINGDECODE('请求URI'),
  `method` varchar(5) DEFAULT NULL COMMENT STRINGDECODE('操作方式'),
  `params` text COMMENT STRINGDECODE('操作提交的数据'),
  `exception` text COMMENT STRINGDECODE('异常信息'),
  `request_params` text COMMENT STRINGDECODE('请求参数'),
  `result` mediumtext COMMENT STRINGDECODE('返回结果'),
  `modules` varchar(255) DEFAULT NULL COMMENT STRINGDECODE('模组'),
  `log_level` varchar(1) DEFAULT NULL COMMENT STRINGDECODE('日志级别'),
  PRIMARY KEY (`id`)    
) ;

