  `update_by` varchar(64) NOT NULL COMMENT STRINGDECODE('更新者'),
  `update_date` datetime NOT NULL COMMENT STRINGDECODE('更新时间'),
  `remarks` varchar(255) DEFAULT NULL COMMENT STRINGDECODE('备注信息'),
  `del_flag` char(1) NOT NULL DEFAULT '0' COMMENT STRINGDECODE('删除标记'),
