  `sort` decimal(10,0) NOT NULL COMMENT STRINGDECODE('排序'),
  `area_id` varchar(64) NOT NULL COMMENT STRINGDECODE('归属区域'),
  `code` varchar(100) DEFAULT NULL COMMENT STRINGDECODE('区域编码'),
  `type` char(1) NOT NULL COMMENT STRINGDECODE('机构类型'),
  `grade` char(1) NOT NULL COMMENT STRINGDECODE('机构等级'),
  `address` varchar(255) DEFAULT NULL COMMENT STRINGDECODE('联系地址'),
  `zip_code` varchar(100) DEFAULT NULL COMMENT STRINGDECODE('邮政编码'),
  `master` varchar(100) DEFAULT NULL COMMENT STRINGDECODE('负责人'),
  `phone` varchar(200) DEFAULT NULL COMMENT STRINGDECODE('电话'),
  `fax` varchar(200) DEFAULT NULL COMMENT STRINGDECODE('传真'),
  `email` varchar(200) DEFAULT NULL COMMENT STRINGDECODE('邮箱'),
  `USEABLE` varchar(64) DEFAULT NULL COMMENT STRINGDECODE('是否启用'),
  `PRIMARY_PERSON` varchar(64) DEFAULT NULL COMMENT STRINGDECODE('主负责人'),
  `DEPUTY_PERSON` varchar(64) DEFAULT NULL COMMENT STRINGDECODE('副负责人'),
  `create_by` varchar(64) NOT NULL COMMENT STRINGDECODE('创建者'),
  `create_date` datetime NOT NULL COMMENT STRINGDECODE('创建时间'),
  `update_by` varchar(64) NOT NULL COMMENT STRINGDECODE('更新者'),
  `update_date` datetime NOT NULL COMMENT STRINGDECODE('更新时间'),
  `remarks` varchar(255) DEFAULT NULL COMMENT STRINGDECODE('备注信息'),
  `del_flag` char(1) NOT NULL DEFAULT '0' COMMENT STRINGDECODE('删除标记'),
  PRIMARY KEY (`id`)   
) ;

