CREATE TABLE  IF NOT EXISTS `sys_role` (
  `id` varchar(64) NOT NULL COMMENT STRINGDECODE('编号'),
  `office_id` varchar(64) DEFAULT NULL COMMENT STRINGDECODE('归属机构'),
