CREATE TABLE `sys_news` (
  `id` varchar(64) NOT NULL COMMENT STRINGDECODE('编号ID'),
  `subject` varchar(200) NOT NULL DEFAULT '' COMMENT STRINGDECODE('新闻标题'),
  `content` mediumtext NOT NULL COMMENT STRINGDECODE('新闻内容'),
  `click_count` int(11) NOT NULL DEFAULT '0' COMMENT STRINGDECODE('点击数'),
  `attachment_id` text COMMENT STRINGDECODE('附件ID串,多个附件|隔开'),
  `attachment_name` text COMMENT STRINGDECODE('附件名称串，多个附件名称|隔开'),
  `type_id` varchar(200) NOT NULL DEFAULT '' COMMENT STRINGDECODE('新闻类型'),
  `publish` char(1) NOT NULL DEFAULT '1' COMMENT STRINGDECODE('发布标识(0-未发布,1-已发布,2-已终止)'),
  `top` varchar(2) NOT NULL DEFAULT '0' COMMENT STRINGDECODE('是否置顶(0-否,1-是)'),
  `subject_color` varchar(8) DEFAULT NULL COMMENT STRINGDECODE('新闻标题颜色'),
  `keyword` varchar(100) DEFAULT NULL COMMENT STRINGDECODE('内容关键词'),
  `create_by` varchar(64) NOT NULL COMMENT STRINGDECODE('创建者'),
  `create_date` datetime NOT NULL COMMENT STRINGDECODE('创建时间'),
  `update_by` varchar(64) NOT NULL COMMENT STRINGDECODE('更新者'),
  `update_date` datetime NOT NULL COMMENT STRINGDECODE('更新时间'),
  PRIMARY KEY (`id`)
) ;

CREATE TABLE `sys_message` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `accepter_id` varchar(64) NOT NULL COMMENT STRINGDECODE('用户ID'),
  `type` varchar(1) DEFAULT '0' COMMENT STRINGDECODE('消息类型’0‘普通消息''1''系统消息'),
  `del_status` varchar(1) DEFAULT '1' COMMENT STRINGDECODE('消息状态''0''已读''1''未读'),
  `title` varchar(100) NOT NULL COMMENT STRINGDECODE('消息标题'),
  `content` varchar(1000) NOT NULL COMMENT STRINGDECODE('消息内容'),
  `create_by` varchar(64) NOT NULL COMMENT STRINGDECODE('消息发送人'),
  `create_date` datetime NOT NULL COMMENT STRINGDECODE('发送时间'),
  `update_by` varchar(64) NOT NULL COMMENT STRINGDECODE('更新者'),
  `update_date` datetime NOT NULL COMMENT STRINGDECODE('更新时间'),
  `source_message_id` int(11) NOT NULL COMMENT STRINGDECODE('源消息ID'),
  PRIMARY KEY (`id`)
) ;

CREATE TABLE `sys_message_send` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT STRINGDECODE('主键 自增'),
  `title` varchar(100) NOT NULL COMMENT STRINGDECODE('标题'),
  `content` varchar(256) NOT NULL COMMENT STRINGDECODE('内容'),
  `target` tinyint(4) NOT NULL COMMENT STRINGDECODE('目标对象(0全部 1按组织 2按角色 3按会员)'),
  `count` int(11) DEFAULT NULL COMMENT STRINGDECODE('目标人数'),
  `type` varchar(1) DEFAULT '0' COMMENT STRINGDECODE('消息类型''0''普通消息''1''系统消息'),
  `status` tinyint(4) DEFAULT '1' COMMENT STRINGDECODE('状态(0未发送 1已发送)'),
  `receivers` text NOT NULL COMMENT STRINGDECODE('接收人'),
  `create_by` varchar(64) NOT NULL COMMENT STRINGDECODE('创建者'),
  `create_date` datetime NOT NULL COMMENT STRINGDECODE('创建时间'),
  `update_by` varchar(64) NOT NULL COMMENT STRINGDECODE('更新者'),
  `update_date` datetime NOT NULL COMMENT STRINGDECODE('更新时间'),
  PRIMARY KEY (`id`)
) ;

