tringUtils.isNotEmpty($bodyparams.supplierNo))\n	AND srmae008 = ''$bodyparams.supplierNo''\n#end\n#if($StringUtils.isNotEmpty($bodyparams.fuzzyQuery))\n	  AND (  srmae001 LIKE ''%$bodyparams.fuzzyQuery%'' \n	       OR srmae003 LIKE ''%$bodyparams.fuzzyQuery%'' \n	       OR srmae004 LIKE ''%$bodyparams.fuzzyQuery%'' \n	       OR srmae006 LIKE ''%$bodyparams.fuzzyQuery%''\n	       OR srmae009 LIKE ''%$bodyparams.fuzzyQuery%''\n	      )\n#end   \n;\nSELECT \n   site_name       "siteName",        \n   ent             "ent",              \n   site            "site",             \n   delivery_no     "deliveryNo",      \n   delivery_date   "deliveryDate",    \n   purchase_type   "purchaseType",    \n   receipt_address "receiptAddress",  \n   status          "status",           \n   receipt_no      "receiptNo",       \n   print_count     "printCount",      \n   remark          "remark",           \n   supplier_no     "supplierNo",      \n   warehouse_no    "warehouseNo"\n  FROM (\n  SELECT site.ooefl003 site_name,\n	       srmaeent ent,\n	       srmaesite site,\n	       srmae001 delivery_no,\n	       to_char(srmae002,''yyyy-mm-dd hh24:mi:ss'') delivery_date,\n	       srmae003 purchase_type,\n	       srmae004 receipt_address,\n	       srmae005 status,\n	       srmae006 receipt_no,\n	       srmae007 print_count,\n	       srmae100 remark,\n	       srmae008 supplier_no,\n	       srmae009 warehouse_no,\n	       DENSE_RANK() OVER( ORDER BY srmaeent, srmae001 DESC, srmaesite ) AS RANK \n    FROM srmae_t \n    LEFT JOIN ooefl_t site ON site.ooeflent = srmaeent AND site.ooefl001 = srmaesite AND site.ooefl002 = ''$lang''\n   WHERE srmaeent = ''$ent''\n    #if($StringUtils.isNotEmpty($site))\n        AND srmaesite = ''$site''\n    #end\n    #if($StringUtils.isNotEmpty($bodyparams.deliveryNo))\n    	  AND srmae001 = ''$bodyparams.deliveryNo''\n    #end\n    #if($StringUtils.isNotEmpty($bodyparams.deliveryDateStart))\n	      AND srmae002 >= to_date(''$bodyparams.deliveryDateStart'',''yy