INSERT INTO `iccn_ica_service` VALUES ('6', '1001', 'delivery_detail_query', STRINGDECODE('送货单单身查询'), STRINGDECODE('{\r\n	"std_data": {\r\n		"parameter": {\r\n			"ent": "99",\r\n			"site": "DSCNJ",\r\n			"deliveryNo": "YONINA-1708280006",\r\n			"deliverySeq": "1"\r\n		}\r\n	}\r\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\r\n#set ($ent = $bodyparams.ent)\r\n#set ($site = $bodyparams.site)\r\n\r\n SELECT srmafent   as "ent",\r\n		    srmafsite  as "site",\r\n		    srmaf001   as "deliveryNo",\r\n		    srmaf002   as "deliverySeq",\r\n		    srmaf003   as "purchaseNo",\r\n		    srmaf004   as "purchaseSeq",\r\n		    srmaf0041  as "purchaseLineSeq",\r\n		    srmaf0042  as "purchaseBatchSeq",\r\n		    srmaf005   as "itemNo",\r\n		    srmaf051   as "itemName",\r\n		    srmaf036   as "warehouseNo",\r\n		    srmaf090   as "unitNo",\r\n		    srmaf006   as "invoiceNo",\r\n		    srmaf007   as "deliveryQty",\r\n		    srmaf008   as "receiptQty",\r\n		    srmaf009   as "dispatch",\r\n		    srmafud001 as "itemSpec",\r\n		    srmafud002 as "receiptNo",\r\n		    srmafud003 as "lotNo",\r\n		    srmafud004 as "itemFeatureNo",\r\n          (\r\n            pmdo006 - nvl (pmdo015, 0) + pmdo016 + pmdo017\r\n          ) AS "unpaidQty",\r\n          pmdo006 "purchaseQty",\r\n          pmdo015 "receivedQty",\r\n          pmdo016 "inspectonReturnQty", \r\n          pmdo017 "stockReturnQty",\r\n          nvl((SELECT SUM(srmaf007) srmaf07\r\n                 FROM SRMAE_t, SRMAF_t\r\n                WHERE srmae001 = srmaf001\r\n                  AND SRMAEENT = SRMAFENT\r\n                  AND SRMAESITE = SRMAFSITE\r\n                  AND (srmae005 = ''1'' OR srmae005 = ''2'')\r\n                  AND srmaf003 = pmdodocno\r\n                  AND srmaf004 = pmdoseq\r\n                  AND srmaf0041 = pmdoseq1\r\n                  AND srmaf0042 = pmdoseq2), 0) AS "actDelivery"\r\n   FROM srmaf_t, srmae_t, pmdo_t\r\n  WHERE srmaf001 = srmae001\r\n    AND pmdosite = srmafsite\r\n    AND pmdosite = srmaesite\r\n    AND pmdoent = srmafent\r\n    AND pmdoent = srmaeent\r\n    AND pmdodocno = srmaf003\r\n    AND pmdoseq = srmaf004\r\n    AND pmdoseq1 = srmaf0041\r\n    AND pmdoseq2 = srmaf0042\r\n		#if($StringUtils.isNotEmpty($bodyparams.deliveryNo))\r\n	  AND srmaf001 = ''$bodyparams.deliveryNo''\r\n		#end\r\n		#if($StringUtils.isNotEmpty($bodyparams.deliverySeq))\r\n		AND srmaf002 = ''$bodyparams.deliverySeq''\r\n		#end\r\n    AND srmafsite = ''$site''\r\n    AND srmafent = ''$ent''\r\n  ORDER BY srmafent,srmafsite,srmaf001,srmaf002'), STRINGDECODE('{\r\n    "std_data": {\r\n        "execution": {\r\n            "code": "$execution.code",\r\n            "sqlcode": "$execution.sqlcode",\r\n            "description": "$execution.description"\r\n        },\r\n        "parameter": {\r\n            "result": [\r\n                {\r\n                    "listdata": $JSON.toJSONString($sqlData[0])\r\n                }\r\n            ]\r\n        }\r\n    }\r\n}'), STRINGDECODE('发货单单身查询'), '1', '2017-09-19 16:04:46', '2017-09-22 17:46:18', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('7', '1001', 'srmae06_info_progress_01receive', STRINGDECODE(' 送货单进度跟踪收货单信息'), STRINGDECODE('{\n    "bodyMap": {\n         "std_data": {\n            "parameter": {\n                "lang":"zh_CN",\n                "ent": "99",\n                "site": "DSCNJ",\n                "supplierNo": "P1001",\n                "receiptNos": ["CNJ-P55-201708000059","CNJ-P55-201708000056","CNJ-P55-201708000058","CNJ-P55-201708000057"] \n            }\n        }\n    }\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\n#set ($ent = $bodyparams.ent)\n#set ($site = $bodyparams.site)\n#set ($lang = $bodyparams.lang)\n\n      select \n            pmdsdocno "receiptNo",                                     /*收货单号*/\n            to_char(pmdsdocdt,''yyyy-mm-dd'') "receiptDate",            /*收货日期*/      \n            pmdtseq "receiptSeq",                                       /*收货项次*/\n            pmdt006 "itemNo",                                       /*物料编码*/\n            nvl(trim(pro.imaal003),pmdt006) "itemName",      /*品名*/\n            nvl(trim(pro.imaal004),pmdt006) "itemSpec",     /*规格*/\n            pmdt019 "receiptUnitNo",                                       /*收货单位*/\n            nvl(trim(t.oocal003),pmdt019) "receiptUnitName", /*单位说明*/\n            pmdt020 "receiptQty",                                       /*收货数量*/    \n            pmdt026 "checkOut",                                       /*检验否*/\n            pmdt016 "warehouseNo",                                       /*库位*/\n            nvl(trim(inay.inayl003),pmdt016) "warehouseName",      /*库位说明*/\n            pmdt017 "storageSpacesNo",                                       /*储位*/\n            nvl(trim(loc.inab003),pmdt017) "storageSpacesName",   /*储位说明*/\n            pmdt018 "lotNo",                                       /*批号*/\n            pmdt053 "allowReceiptQty",                                       /*允收数量*/\n            pmdt054 "stockInQty",                                       /*已入库量*/\n            pmdt055 "returnQty",                                       /*验退量*/\n            pmdt001 "purchaseNo",                                       /*客户订单号*/ \n            pmdt002 "purchaseSeq",                                       /*客户订单号项次*/ \n            pmdt003 "purchaseLineSeq",                                       /*客户订单号项序*/  \n            pmdt004 "purchaseBatchSeq"                                          /*客户订单号分批序*/ \n       from pmds_t \n      inner join pmdt_t on pmdsent = pmdtent and pmdssite = pmdtsite and pmdsdocno = pmdtdocno\n       left join imaal_t pro on pro.imaal001=pmdt006 and pmdtent=pro.imaalent and pro.imaal002=''$lang''\n       left join oocal_t t on t.oocal001=pmdt019 and pmdtent=t.oocalent and t.oocal002=''$lang''\n       left join inayl_t inay on inay.inaylent = pmdtent and inay.inayl001 = pmdt016 and inay.inayl002=''$lang''\n       left join inab_t loc on loc.inab001=pmdt016 and loc.inab002=pmdt017 and pmdtent=loc.inabent and pmdtsite=loc.inabsite and loc.inabstus=''Y''       \n      where pmdsent = ''$ent''\n        and pmdssite = ''$site''\n        and pmds000  IN (1,8)\n        and pmdsstus = ''Y''\n        and pmds007 = ''$bodyparams.supplierNo''\n      #if ($bodyparams.pmdsdocno.size() != 0 )                  \n            AND pmdsdocno in $VEUtils.SqlIn($bodyparams.receiptNos)\n      #end\n      ORDER BY PMDTDOCNO, PMDTSEQ'), STRINGDECODE('{\n    "std_data": {\n        "execution": {\n            "code": "$execution.code",\n            "sqlcode": "$execution.sqlcode",\n            "description": "$execution.description"\n        },\n        "parameter": {\n            "result": [\n                {\n                    "listdata": $JSON.toJSONString($sqlData[0])\n                }\n            ]\n        }\n    }\n}'), STRINGDECODE(' 送货单进度跟踪收货单信息'), '1', '2017-09-19 16:04:46', '2017-09-19 16:04:46', '0', 't100');
