INSERT INTO `iccn_ica_service` VALUES ('17', '1001', 'srmae06_info_progress_02checkreturn', STRINGDECODE('送货单进度跟踪验退单信息'), STRINGDECODE('{\n    "bodyMap": {\n         "std_data": {\n            "parameter": {\n                "lang":"zh_CN",\n                "ent": "99",\n                "site": "DSCNJ",\n                "supplierNo": "P1001",\n                "returnDocNos": ["CNJ-P55-201708000059","CNJ-P55-201708000056","CNJ-P55-201708000058","CNJ-P55-201708000057"] \n            }\n        }\n    }\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\n#set ($ent = $bodyparams.ent)\n#set ($site = $bodyparams.site)\n#if ($lang != '''')\n#set ($lang = ''zh_CN'') \n#end\n\nSELECT \n               a.pmdsdocno "returnDocNo",                                     /*验退单号*/\n               b.pmdtseq "returnDocSeq",                                       /*项次*/ \n               to_char(a.pmdsdocdt,''YYYY-MM-DD'') "returnDocDate",     /*验退日期*/ \n               b.pmdt027 "receiptNo",                                       /*收货单号*/ \n               b.pmdt028 "receiptSeq",                                       /*收货项次*/ \n               b.pmdt006 "itemNo",                                       /*料件编号*/ \n               NVL(trim(k.imaal003),b.pmdt006) "itemName",        /*料件名称*/ \n               NVL(trim(k.imaal004),b.pmdt006) "itemSpec",        /*料件规格*/ \n               b.pmdt019 "returnUintNo",                                       /*验退单位*/ \n               NVL(n.oocal003,b.pmdt019) "returnUintName",              /*单位说明*/ \n               b.pmdt020 "returnQty",                                       /*验退数量*/ \n               b.pmdt016 "warehouseNo",                                       /*库位*/ \n               NVL(trim(inay.inayl003),b.pmdt016) "warehouseName",     /*库位说明*/ \n               b.pmdt017 "storageSpacesNo",                                       /*储位*/ \n               NVL(trim(m.inab003),b.pmdt017) "storageSpacesName",          /*储位说明*/ \n               b.pmdt018 "lotNo",                                       /*批号*/\n               b.pmdt051||''-''||q.oocql004 "returnReason",             /*退货理由*/\n               a.pmds045 "headerRemark",                                       /*单头备注*/\n               b.pmdt059 "bodyRemark",                                       /*单身备注*/\n               a.pmds081 "retrieveDate",                                       /*取回日期*/\n               b.pmdt001 "purchaseNo",                                       /*采购单号*/                   \n               b.pmdt002 "purchaseSeq",                                       /*采购项次*/      \n               b.pmdt003 "purchaseLineSeq",                                       /*采购项序*/\n               b.pmdt004 "purchaseBatchSeq"                                      /*采购分批序*/ \n         FROM pmdt_t b INNER JOIN pmds_t a ON  b.pmdtent = a.pmdsent AND b.pmdtsite = a.pmdssite AND b.pmdtdocno = a.pmdsdocno\n         LEFT OUTER JOIN imaal_t k ON k.imaalent = b.pmdtent AND k.imaal001 = b.pmdt006 AND k.imaal002 = ''$lang''\n         LEFT OUTER JOIN oocal_t n ON n.oocalent = b.pmdtent AND n.oocal001 = b.pmdt019 AND n.oocal002 = ''$lang''\n         LEFT OUTER JOIN inayl_t inay ON inay.inaylent = b.pmdtent AND inay.inayl001 = b.pmdt016 AND inay.inayl002=''$lang''     \n         LEFT OUTER JOIN inab_t m  ON m.inabent =b.pmdtent AND m.inabsite=b.pmdtsite AND m.inab001=b.pmdt016 AND m.inab002=b.pmdt017  \n         LEFT JOIN oocql_t q ON b.pmdtent = q.oocqlent AND b.pmdt051 = q.oocql002 AND q.oocql001 =''314'' AND q.oocql003 = ''$lang''   /*验退314 仓退313 */\n        WHERE a.pmdsent = ''$ent''\n          AND a.pmdssite = ''$site''   \n          AND a.pmdsstus= ''Y''\n          AND a.pmds000 IN (5,10)  /*验退:5  委外验退:10    */\n          AND a.pmds007 = ''$bodyparams.supplierNo''\n          #if ($bodyparams.returnDocNos.size() != 0 )                  \n              AND pmdt027 in $VEUtils.SqlIn($bodyparams.returnDocNos)\n          #end\n     ORDER BY PMDSDOCNO ,PMDTSEQ'), STRINGDECODE('{\n    "std_data": {\n        "execution": {\n            "code": "$execution.code",\n            "sqlcode": "$execution.sqlcode",\n            "description": "$execution.description"\n        },\n        "parameter": {\n            "result": [\n                {\n                    "listdata": $JSON.toJSONString($sqlData[0])\n                }\n            ]\n        }\n    }\n}'), STRINGDECODE('送货单进度跟踪验退单信息'), '1', '2017-09-19 16:04:46', '2017-09-19 16:04:46', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('18', '1001', 'srmae06_info_progress_03warehouse', STRINGDECODE('送货单进度跟踪入库单信息'), STRINGDECODE('{\n    "bodyMap": {\n         "std_data": {\n            "parameter": {\n                "lang":"zh_CN",\n                "ent": "99",\n                "site": "DSCNJ",\n                "supplierNo": "P1001",\n                "stockInNos": ["CNJ-P55-201708000059","CNJ-P55-201708000056","CNJ-P55-201708000058","CNJ-P55-201708000057"] \n            }\n        }\n    }\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\n#set ($ent = $bodyparams.ent)\n#set ($site = $bodyparams.site)\n#set ($lang = $bodyparams.lang)\n\nSELECT \n               a.pmdsdocno as "stockInNo",                                        /*入库单号*/\n               to_char(a.pmdsdocdt,''yyyy-mm-dd'') as "stockInDate",                                      /*入库日期*/\n               b.pmdtseq as "stockInSeq",                                         /*入库项次*/       \n               b.pmdt027 as "receiptNo",                                         /*收货单号*/\n               b.pmdt028 as "receiptSeq",                                         /*收货项次*/\n               b.pmdt006 as "itemNo",                                             /*料件编号*/ \n               NVL(trim(k.imaal003),b.pmdt006) as "itemName",                     /*料件名称*/ \n               NVL(trim(k.imaal004),b.pmdt006) as "itemSpec",                      /*料件规格*/ \n               b.pmdt019 as "stockInUnitNo",                                      /*入库单位*/ \n               NVL(n.oocal003,b.pmdt019) as "UnitName",                           /*单位说明*/ \n               b.pmdt020 as "stockInQty",                                         /*入库数量*/ \n               b.pmdt016 as "warehouseNo",                                        /*库位*/ \n               NVL(trim(inay.inayl003),b.pmdt016) as "warehouseName",             /*库位说明*/ \n               b.pmdt017 as "storAgeSpacesNo",                                    /*储位*/ \n               NVL(trim(m.inab003),b.pmdt017) as "storAgeSpacesName",             /*储位说明*/ \n               b.pmdt018 as "lotNo",                                              /*批号*/\n               b.pmdt001 as "purchaseNo",                                         /*采购单号*/\n               b.pmdt002 as "purchaseSeq",                                        /*采购项次*/\n               b.pmdt003 as "purchaseLineSeq",                                    /*采购项序*/\n               b.pmdt004 as "purchaseBatchSeq"                                    /*采购分批序*/\n          FROM pmds_t a \n         INNER JOIN pmdt_t b ON a.pmdsent = b.pmdtent AND a.pmdssite = b.pmdtsite AND a.pmdsdocno = b.PMDTDOCNO\n          LEFT OUTER JOIN imaal_t k ON k.imaalent = b.pmdtent AND k.imaal001 = b.pmdt006 AND k.imaal002 = ''$lang''\n          LEFT OUTER JOIN oocal_t n ON n.oocalent = b.pmdtent AND n.oocal001 = b.pmdt019 AND n.oocal002 = ''$lang''\n          LEFT OUTER JOIN inayl_t inay ON inay.inaylent = b.pmdtent AND inay.inayl001 = b.pmdt016 AND inay.inayl002 = ''$lang''\n          LEFT OUTER JOIN inab_t m ON m.inabent =b.pmdtent AND m.inabsite=b.pmdtsite AND m.inab001=b.pmdt016 AND m.inab002=b.pmdt017  \n         WHERE a.pmdsent = ''$ent''    /*集团编号*/\n           AND a.pmdssite = ''$site''      /*据点编号*/\n           AND a.pmdsstus = ''S''\n           AND a.pmds000 IN (''3'', ''6'', ''12'', ''13'', ''16'', ''20'', ''24'', ''25'')  /*单据性质*/       \n           AND a.pmds007 = ''$bodyparams.supplierNo''\n           #if ($bodyparams.stockInNos.size() != 0 )                  \n               AND pmdt027 in $VEUtils.SqlIn($bodyparams.stockInNos)\n           #end\n         \n     ORDER BY PMDTDOCNO, PMDTSEQ '), STRINGDECODE('{\n    "std_data": {\n        "execution": {\n            "code": "$execution.code",\n            "sqlcode": "$execution.sqlcode",\n            "description": "$execution.description"\n        },\n        "parameter": {\n            "result": [\n                {\n                    "listdata": $JSON.toJSONString($sqlData[0])\n                }\n            ]\n        }\n    }\n}'), STRINGDECODE('送货单进度跟踪入库单信息'), '1', '2017-09-19 16:04:46', '2017-09-19 16:04:46', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('19', '1001', 'srmae06_info_progress_04warehousereturn', STRINGDECODE('送货单进度跟踪仓退单信息'), STRINGDECODE('{\n    "bodyMap": {\n         "std_data": {\n            "parameter": {\n                "lang":"zh_CN",\n                "ent": "99",\n                "site": "DSCNJ",\n                "supplierNo": "P1001",\n                "stockReturnNos": ["CNJ-P55-201708000059","CNJ-P55-201708000056","CNJ-P55-201708000058","CNJ-P55-201708000057"] \n            }\n        }\n    }\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\n#set ($ent = $bodyparams.ent)\n#set ($site = $bodyparams.site)\n#set ($lang = $bodyparams.lang)\n\n      SELECT \n         a.pmdsdocno as "stockReturnNo",                                      /*仓退单号*/\n         b.pmdtseq as "stockReturnSeq",                                       /*项次*/\n         to_char(a.pmdsdocdt,''YYYY-MM-DD'') as "stockReturnDate",              /*仓退日期*/\n         b.pmdt027 as "receiptNo",                                            /*收货单号*/\n         b.pmdt028 as "receiptSeq",                                           /*收货项次*/\n         b.pmdt006 as "itemNo",                                               /*料件编号*/\n         NVL(trim(k.imaal003),b.pmdt006) as "itemName",                       /*料件名称*/\n         NVL(trim(k.imaal004),b.pmdt006) as "itemSpec",                       /*料件规格*/\n         b.pmdt019 as "stockReturnUnitNo",                                    /*仓退单位*/\n         NVL(n.oocal003,b.pmdt019) as "UnitName",                             /*单位说明*/\n         b.pmdt020 as "stockReturnUnitQty",                                   /*仓退数量*/\n         b.pmdt016 as "warehouseNo",                                          /*库位*/\n         NVL(trim(inay.inayl003),b.pmdt016) as "warehouseName",               /*库位说明*/\n         b.pmdt017 as "storAgeSpacesNo",                                      /*储位*/\n         NVL(trim(m.inab003),b.pmdt017) as "storAgeSpacesName",               /*储位说明*/\n         b.pmdt018 as "lotNo",                                                /*批号*/\n         b.pmdt051||''-''||q.oocql004 as "returnReason",                        /*退货理由*/\n         a.pmds045 as "headRemark",                                           /*单头备注*/\n         b.pmdt059 as "detailRemark",                                         /*单身备注*/\n         a.pmds081 as "returnDate",                                           /*取回日期*/\n         b.pmdt001 as "purchaseNo",                                           /*采购单号*/\n         b.pmdt002 as "purchaseSeq",                                          /*采购项次*/\n         b.pmdt003 as "purchaseLineSeq",                                      /*采购项序*/\n         b.pmdt004 as "purchaseBatchSeq"                                      /*采购分批序*/\n         FROM pmdt_t b INNER JOIN pmds_t a ON  b.pmdtent = a.pmdsent AND b.pmdtsite = a.pmdssite AND b.pmdtdocno = a.pmdsdocno\n         LEFT OUTER JOIN imaal_t k ON k.imaalent = b.pmdtent AND k.imaal001 = b.pmdt006 AND k.imaal002 = ''$lang''\n         LEFT OUTER JOIN oocal_t n ON n.oocalent = b.pmdtent AND n.oocal001 = b.pmdt019 AND n.oocal002 = ''$lang''\n         LEFT OUTER JOIN inayl_t inay ON inay.inaylent = b.pmdtent AND inay.inayl001 = b.pmdt016 AND inay.inayl002=''$lang''     \n         LEFT OUTER JOIN inab_t m  ON m.inabent =b.pmdtent AND m.inabsite=b.pmdtsite AND m.inab001=b.pmdt016 AND m.inab002=b.pmdt017  \n         LEFT JOIN oocql_t q ON b.pmdtent = q.oocqlent AND b.pmdt051 = q.oocql002 AND q.oocql001 =''313'' AND q.oocql003 = ''$lang''   /*验退314 仓退313 */ \n        WHERE a.pmdsent = ''$ent''\n          AND a.pmdssite = ''$site''   \n          AND a.pmdsstus= ''S''\n          AND a.pmds000 IN (7,14)  /*仓退:7 委外仓退:14*/      \n      AND a.pmds007 = ''$bodyparams.supplierNo''\n          #if ($bodyparams.stockReturnNos.size() != 0 )                  \n              AND pmdt027 in $VEUtils.SqlIn($bodyparams.stockReturnNos)\n          #end\n      ORDER BY PMDTDOCNO, PMDTSEQ'), STRINGDECODE('{\n    "std_data": {\n        "execution": {\n            "code": "$execution.code",\n            "sqlcode": "$execution.sqlcode",\n            "description": "$execution.description"\n        },\n        "parameter": {\n            "result": [\n                {\n                    "listdata": $JSON.toJSONString($sqlData[0])\n                }\n            ]\n        }\n    }\n}'), STRINGDECODE('送货单进度跟踪仓退单信息'), '1', '2017-09-19 16:04:46', '2017-09-19 16:04:46', '0', 't100');
