 "sumDecimal"\r\n      ,unpaidQty "unpaidQty"\r\n      ,exchangeRateDecimal "exchangeRateDecimal",\r\n        NVL(wareHouseNo,'' '') "wareHouseNo", \r\n        NVL(wareHouseName,'' '') "wareHouseName", \r\n        NVL(storageSpaceNo,'' '') "storageSpaceNo",\r\n        NVL(storageSpaceName,'' '') "storageSpaceName",\r\n        invoiceType "invoiceType",\r\n        invoiceTypeDesc "invoiceTypeDesc",\r\n        invoiceTypeWithDesc "invoiceTypeWithDesc"\r\n FROM (\r\n SELECT b.pmdnsite site, \r\n        NVL(trim(site.ooefl003), b.pmdnsite) siteName, \r\n        b.pmdndocno purchaseNo, \r\n        a.pmdl005 purchaseType, \r\n       replace(replace( NVL(trim(sc1.gzcbl004),a.pmdl005),''委外采购'',''来料加工''),''采购'',''订单'') purchaseTypeDesc,\r\n	     replace(replace( a.pmdl005 || ''-'' || NVL(trim(sc1.gzcbl004),a.pmdl005),''委外采购'',''来料加工''),''采购'',''订单'') purchaseTypeAndDesc, \r\n        a.pmdl006 multProperty, \r\n        NVL(trim(sc3.gzcbl004),a.pmdl006) multPropertyDesc, \r\n        a.pmdl001 version, \r\n        q.ooef016 localCurrencyCode, \r\n        NVL(trim(h0.ooail003),q.ooef016) localCurrencyName, \r\n        to_char(a.pmdldocdt,''yyyy-mm-dd'') purchaseDate, \r\n        a.pmdl004 supplierNo, \r\n        NVL(trim(f.pmaal003), a.pmdl004) supplierName, \r\n        NVL(trim(f.pmaal004), a.pmdl004) supplierShortName, \r\n        a.pmdl015 currencyCode, \r\n        NVL(trim(h.ooail003), a.pmdl015) currencyName, \r\n        a.pmdl016 exchangeRate, \r\n        DECODE(r.oodb005, ''Y'', ''1'',''0'') isContainTax, \r\n        a.pmdl010 tradeCondition, \r\n        NVL(trim(n.oocql004), a.pmdl010) tradeConditionName, \r\n        a.pmdl002 purchasesman, \r\n        NVL(trim(j.ooag011),a.pmdl002) purchasesmanName, \r\n        a.pmdl003 purchasesDepartmentNo, \r\n        NVL(trim(i.ooefl003),a.pmdl003) purchasesDepartmentName, \r\n        a.pmdl020 shippingType, \r\n        NVL(trim(l.oocql004),a.pmdl020) shippingTypeDesc, \r\n        a.pmdl025 address, \r\n        '''' addressDesc, \r