07 = ''$bodyparams.reply_ib_purchase_qty'',\n                       a.srmaa010 = ''$bodyparams.reply_remark''\n         WHEN NOT MATCHED THEN\n         		INSERT (srmaaent, srmaasite, srmaa001, srmaa002, srmaa003,\n                    srmaa0031, srmaa004, srmaa005, srmaa006, srmaa007,\n                    srmaa008, srmaa009, srmaa010)\n            VALUES(''$bodyparams.ent'', ''$bodyparams.site'', ''$bodyparams.purchase_no'', ''$bodyparams.purchase_seq'', ''$bodyparams.purchase_line_seq'', \n                   ''$bodyparams.purchase_batch_seq'', b.pmdo011, to_date(''$bodyparams.reply_ib_expected_delivery'',''yyyy/mm/dd''), 0, ''$bodyparams.reply_ib_purchase_qty'',\n                   '''', '''', ''$bodyparams.reply_remark'')\n                   ;\n   #end\n   #set ($count = $count + 1)\n#end \n#set ($count = $count - 1)'), STRINGDECODE('{\n    "std_data": {\n        "execution": {\n            "code": "$execution.code",\n            "sqlcode": "$execution.sqlcode",\n            "description": "$execution.description"\n        },\n        "parameter": {\n            "result": [\n                {\n                    "totolCount": $count,\n                    "status": "$execution.code"\n                }\n            ]\n        }\n    }\n}'), '  ', '1', '2017-09-19 16:04:46', '2017-09-19 16:04:46', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('27', '1001', 'srmae06_info_progress_00receive_no', STRINGDECODE('送货单进度跟踪发货单对应的收货单号'), STRINGDECODE('{\n    "bodyMap": {\n        "std_data": {\n            "parameter": {\n                "ent":"99",\n                "site":"DSCNJ",\n                "deliveryNo":"P1001-1708160001",\n                "deliverySeq":"1"  //可选\n            }\n        }\n    }\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\n#set ($ent = $bodyparams.ent)\n#set ($site = $bodyparams.site)\n\nSELECT distinct pmdsdocno AS "receiptNo"\n  FROM pmds_t\n  LEFT JOIN pmdt_t on pmdsent = pmdtent and pmdssite = pmdtsite and pmdsdocno = pmdtdocno\n WHERE pmdsent = ''$ent''\n   AND pmdssite = ''$site''\n   AND pmds000 IN (''1'',''8'')\n #if($StringUtils.isNotEmpty($bodyparams.deliveryNo)) \n   AND pmds010 = ''$bodyparams.deliveryNo''\n #end   \n #if($StringUtils.isNotEmpty($bodyparams.deliverySeq))\n   AND pmdt094 = ''$bodyparams.deliverySeq''\n #end\n'), STRINGDECODE('{\n    "std_data": {\n        "execution": {\n            "code": "$execution.code",\n            "sqlcode": "$execution.sqlcode",\n            "description": "$execution.description"\n        },\n        "parameter": {\n            "result": [\n                {\n                    "listdata": $JSON.toJSONString($sqlData[0])\n                }\n            ]\n        }\n    }\n}'), STRINGDECODE('送货单进度跟踪发货单对应的收货单号'), '1', '2017-09-19 16:04:46', '2017-09-19 16:04:46', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('28', '1001', 'supplier_site', STRINGDECODE('供应商有权限的据点公司名称'), STRINGDECODE('{\n    "bodyMap": {\n         "std_data": {\n            "parameter": {\n                "lang":"zh_CN",\n                "ent": "99",\n                "supplierNo": "P1001"\n            }\n        }\n    }\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\n#set ($ent = $bodyparams.ent)\n#set ($lang = $bodyparams.lang)\n\nSELECT OOEF001 AS "site", OOEFL003 AS "siteName"\n  FROM PMAB_T\n  LEFT JOIN PMAA_T\n    ON PMABENT = PMAAENT\n   AND PMAB001 = PMAA001\n  LEFT JOIN OOEFL_T\n    ON OOEFLENT = PMABENT\n   AND OOEFL001 = PMABSITE\n   AND OOEFL002 = ''$lang''\n  LEFT JOIN OOEF_T\n    ON OOEFL001 = OOEF001\n   AND OOEFLENT = OOEFENT\n WHERE PMAB001 = ''$bodyparams.supplierNo''\n   AND PMABENT = ''$ent''\n   AND PMAA002 in (1, 3)\n   AND PMABSITE != ''ALL''\n   AND OOEF001 IS NOT NULL\n   AND OOEFL003 IS NOT NULL'), STRINGDECODE('{\n    "std_data": {\n        "execution": {\n            "code": "$execution.code",\n            "sqlcode": "$execution.sqlcode",\n            "description": "$execution.description"\n        },\n        "parameter": {\n            "result": [\n                {\n                    "listdata": $JSON.toJSONString($sqlData[0])\n                }\n            ]\n        }\n    }\n}'), STRINGDECODE('供应商有权限的据点公司名称'), '1', '2017-09-19 16:04:46', '2017-09-19 16:04:46', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('29', '1001', 'supplier_performance', STRINGDECODE('供应商绩效管理'), STRINGDECODE('{\n    "headerMap": {\n        "content-length": 570,\n        "cookie": "JSESSIONID=B7E4289A59C9CAD58E6356DCA41CF986",\n        "digi-datakey": {\n            "CompanyId": "DSCNJ",\n            "EntId": "99"\n        },\n        "postman-token": "5e8b966e-bd6b-47c8-b81c-de7078943293",\n        "digi-service": {\n            "prod": "T100",\n            "ip": "172.31.75.183",\n            "name": "app.supplier_performance.get",\n            "id": "topprod"\n        },\n        "accept": "*/*",\n        "digi-type": "sync",\n        "host": "localhost:8080",\n        "content-type": "application/json",\n        "connection": "keep-alive",\n        "cache-control": "no-cache",\n        "digi-key": "D402A32BCAE986232EB52A36933A4B9B",\n        "digi-protocol": "raw",\n        "digi-host": {\n            "prod": "APP",\n            "ip": "172.31.75.44",\n            "lang": "zh_CN",\n            "acct": "tiptop",\n            "timestamp": "20170801160221116"\n        },\n        "accept-encoding": "gzip, deflate",\n        "user-agent": "PostmanRuntime/6.2.5"\n    },\n    "bodyMap": {\n			"std_data": {\n				"parameter": {\n					"ent": "",\n					"site": "",\n                                        "lang": "zh_CN",\n					"supplierNo": "",\n					"formula": "",\n					"year": "",\n					"month": "",\n					}\n			}\n	}\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\n#set ($ent = $bodyparams.ent) \n#set ($site = $bodyparams.site) \n#set ($lang = $bodyparams.lang)\n#if ($lang != '''')\n#set ($lang = ''zh_CN'') \n#end\n\nSELECT count(1) AS COUNT  FROM pmbq_t\n  LEFT JOIN pmbr_t ON pmbrent = pmbqent AND pmbqsite = pmbrsite AND pmbq001 = pmbr001 AND pmbq002 = pmbr002 AND pmbq003 = pmbr003 AND pmbq004 = pmbr004\n WHERE pmbqent = ''$ent''\n   AND pmbqsite = ''$site''\n   #if ($bodyparams.supplier_no != '''')\n   		AND pmbq004 = ''$bodyparams.supplierNo''\n   #end\n   #if ($bodyparams.formula != '''')\n   		AND pmbq003 = ''$bodyparams.formula''\n   #end\n   #if ($bodyparams.year != '''')\n   		AND pmbq001 = ''$bodyparams.year''\n   #end\n   #if ($bodyparams.month != '''')\n   		AND pmbq002 = ''$bodyparams.month''\n   #end\n   ;\n\nSELECT site               "site",\n       sitename           "siteName",\n       formula            "formula",\n       formula_name       "formulaName",\n       year               "year",\n       month              "month",\n       supplier_no        "supplierNo",\n       supplier_name      "supplierName",\n       supplier_shortname "supplierShortName",\n       total_score        "totalScore",\n       project_type       "projectType",\n       project_type_name  "projectTypeName",\n       project_no         "projectNo",\n       project_name       "projectName",\n       score              "score"\n  FROM ( SELECT UNIQUE pmbqsite site,                           /*据点*/\n                nvl(trim(site.ooefl003),pmbqsite) sitename,     /*据点名称*/\n                pmbq003 formula,                                /*评公式编号*/\n                nvl(trim(pmbk002),pmbq003) formula_name,        /*评公式说明*/\n                pmbq001 year,                                   /*年度*/\n                pmbq002 month,                                  /*期别*/\n                pmbq004 supplier_no,                            /*供应商编号*/\n                nvl(trim(b.pmaal003),pmbq004) supplier_name,      /*供应商全称*/\n                nvl(trim(b.pmaal004),pmbq004) supplier_shortname, /*供应商简称*/       \n                pmbq006 total_score,                              /*总得分*/\n                pmbr005 project_type,                             /*项目类型*/\n                nvl(trim(sc1.gzcbl004),pmbr005) project_type_name,                   /*项目类型说明*/\n                pmbr006 project_no,                               /*项目编号*/\n                CASE pmbr005 \n                  WHEN ''1'' THEN (SELECT gzcbl004 FROM gzcbl_t \n                                  WHERE gzcbl001 = ''3021'' \n                                    AND gzcbl002 = pmbr006 \n                                    AND gzcbl003 = ''$lang'' )\n                  ELSE (SELECT oocql004 FROM oocql_t \n                         WHERE oocqlent = pmbqent \n                           AND oocql001 = ''2052'' \n                           AND oocql002 = pmbr006 \n                           AND oocql003 = ''$lang'')\n                END project_name,                                /*项目名称*/\n                pmbr008 score,                                   /*项目得分*/\n                DENSE_RANK() OVER( ORDER BY pmbq001, pmbq002, pmbq003, pmbq004, pmbr005, pmbr006) AS RANK \n           FROM pmbq_t\n           LEFT JOIN pmbr_t ON pmbrent = pmbqent AND pmbqsite = pmbrsite AND pmbq001 = pmbr001 AND pmbq002 = pmbr002 AND pmbq003 = pmbr003 AND pmbq004 = pmbr004\n           LEFT JOIN ooefl_t site ON site.ooeflent = pmbqent AND site.ooefl001 = pmbqsite AND site.ooefl002=''$lang''  \n           LEFT JOIN pmaal_t b ON b.pmaalent = pmbqent AND b.pmaal001=pmbq004 AND b.pmaal002=''$lang''\n           LEFT JOIN pmbk_t c ON c.pmbkent= pmbqent AND c.pmbk001 = pmbq003\n           LEFT JOIN gzcb_t sc ON sc.gzcb001 = ''6002'' AND sc.gzcb002 = pmbr005\n           LEFT JOIN gzcbl_t sc1 ON sc.gzcb002 = sc1.gzcbl002 AND sc.gzcb001 = sc1.gzcbl001 AND sc1.gzcbl003 = ''$lang''\n          WHERE pmbqent = ''$ent''\n            AND pmbqsite = ''$site''\n        #if ($bodyparams.supplier_no != '''')\n        		AND pmbq004 = ''$bodyparams.supplierNo''\n        #end\n        #if ($bodyparams.formula != '''')\n        		AND pmbq003 = ''$bodyparams.formula''\n        #end\n        #if ($bodyparams.year != '''')\n        		AND pmbq001 = ''$bodyparams.year''\n        #end\n        #if ($bodyparams.month != '''')\n        		AND pmbq002 = ''$bodyparams.month''\n        #end\n          ORDER BY pmbq001, pmbq002, pmbq003, pmbq004, pmbr005, pmbr006 )\n  ORDER BY year, month, formula, supplier_no, project_type, project_no'), STRINGDECODE('{\n    "std_data": {\n        "execution": {\n            "code": "$execution.code",\n            "sqlcode": "$execution.sqlcode",\n            "description": "$execution.description"\n        },\n        "parameter": {\n            "result": [\n                {\n                    "totolCount": $JSON.toJSONString($sqlData[0][0].COUNT),\n                    "listdata": $JSON.toJSONString($sqlData[1])\n                }\n            ]\n        }\n    }\n}'), ' ', '1', '2017-09-19 16:04:46', '2017-09-19 16:04:46', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('30', '1002', 'listGet', STRINGDECODE('清单列表'), STRINGDECODE('{\r\n    "std_data": {\r\n        "parameter": {\r\n            "ent": "99",\r\n            "site": "DSCNJ",\r\n            "lang": "zh_CN",\r\n            "appmodule": "B007",\r\n            "docNo": "",\r\n            "itemNo": "",\r\n            "itemName": "",\r\n            "supplierNo": "",\r\n            "customerNo": "",\r\n            "employeeNo": "",\r\n            "departmentNo": "",\r\n            "warehouseNo": "Z1W1",\r\n            "pagesize": "10",\r\n            "dateBegin": "2015/01/01",\r\n            "dateEnd": "2018/01/01"\r\n        }\r\n    }\r\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\r\n#set ($ent = $bodyparams.ent) \r\n#set ($site = $bodyparams.site) \r\n#set ($lang = $bodyparams.lang) \r\n#set ($appmodule = $bodyparams.appmodule) \r\n#set ($docNo = $bodyparams.docNo) \r\n#set ($itemNo = $bodyparams.itemNo) \r\n#set ($itemName = $bodyparams.itemName)\r\n#set ($supplierNo = $bodyparams.supplierNo) \r\n#set ($customerNo= $bodyparams.customerNo)\r\n#set ($employeeNo= $bodyparams.employeeNo)\r\n#set ($departmentNo= $bodyparams.departmentNo)\r\n#set ($warehouseNo= $bodyparams.warehouseNo)\r\n#set ($pagesize = $bodyparams.pagesize) \r\n#set ($dateBegin = $bodyparams.dateBegin) \r\n#set ($dateEnd = $bodyparams.dateEnd)\r\n#set ($packageNo = $bodyparams.packageNo) \r\n\r\n#if ($appmodule ==''A001'')   ##采购收货\r\nSELECT pmdldocno as "docNo",to_char(pmdldocdt,''yyyy/mm/dd'')as "createDate",pmdl004 as "supplierNo",pmaal004 as "supplierName" FROM(\r\n              SELECT rownum item,t.* FROM (\r\n              SELECT distinct pmdldocno,pmdldocdt,pmdl004,pmaal004 FROM pmdl_t\r\n              INNER JOIN pmdo_t ON pmdlent=pmdoent AND pmdldocno=pmdodocno\r\n              LEFT JOIN pmaal_t ON pmaalent=pmdlent AND pmaal001=pmdl004 AND pmaal002=''$lang'' \r\n              WHERE pmdlent=''$ent''\r\n              AND pmdlsite=''$site''\r\n              AND pmdo006 > pmdo015 +pmdo016\r\n              AND pmdlstus=''Y''\r\n   #if ($docNo != '''')             ##采购单号\r\n      AND pmdldocno like ''%$docNo%''\r\n   #end   \r\n   #if ($supplierNo != '''')        ##供应商编码\r\n      AND pmdl004 like ''%$supplierNo%''\r\n   #end  \r\n   #if ($itemNo != '''')            ##料号\r\n      AND pmdo001 LIKE ''%$itemNo%''\r\n   #end  \r\n   #if ($dateBegin != '''')\r\n      AND pmdldocdt>=to_date(''$dateBegin'',''yyyy/mm/dd'')\r\n   #end  \r\n   #if ($dateEnd != '''')\r\n      AND pmdldocdt<=to_date(''$dateEnd'',''yyyy/mm/dd'')\r\n   #end  \r\n   ORDER BY pmdldocdt desc,pmdldocno) t) \r\n   where item<=''$pagesize''\r\n#end\r\n\r\n#if ($appmodule ==''A005'')   ##扫码入库\r\nselect pmdsdocno as "docNo",to_char(pmdsdocdt,''yyyy/mm/dd'') as "createDate",pmds007 as "supplierNo",pmaal004 as "supplierName" from (\r\n    select row_number() over(order by pmdsdocdt desc,pmdsdocno) item,pmdsdocno,pmdsdocdt,pmds007,pmaal004 from (\r\n    select distinct pmdsdocno,pmdsdocdt,pmds007,pmaal004 from pmds_t\r\n    left join pmaal_t on pmaalent=pmdsent and pmaal001=pmds007 and pmaal002=''$lang''\r\n    inner join pmdt_t on pmdtent=pmdsent and pmdtsite=pmdssite and pmdtdocno=pmdsdocno\r\n    where pmdsent = ''$ent''\r\n    and pmdssite = ''$site''\r\n    and pmdsstus =''Y'' \r\n    AND pmds000 in (''1'',''8'')\r\n    and pmdt053 > pmdt054   \r\n #if ($docNo != '''')                                    ##收货单号\r\n    and pmdsdocno like  ''%$docNo%''\r\n #end  \r\n #if ($supplierNo != '''')                               ##供应商编号\r\n    and pmds007 like  ''%$supplierNo%''\r\n #end   \r\n #if ($dateBegin != '''')                                ##开始日期\r\n    AND pmdsdocdt>=to_date(''$dateBegin'',''yyyy/mm/dd'')\r\n #end  \r\n #if ($dateEnd != '''')                                  ##结束日期\r\n    AND pmdsdocdt<=to_date(''$dateEnd'',''yyyy/mm/dd'')\r\n #end  \r\n ) )where item>0 AND item<=''$pagesize''                 ##最大展示笔数\r\n#end\r\n\r\n#if ($appmodule ==''A007'')   ##快速入库\r\nSELECT pmdsdocno as "docNo",to_char(pmdsdocdt,''yyyy/mm/dd'') as "createDate",pmaal004 as "supplierName" FROM(\r\nSELECT rownum item,t.* FROM (\r\nSELECT DISTINCT pmdsdocno,pmdsdocdt,pmaal004 from pmdt_t,pmds_t  \r\n        LEFT JOIN pmaal_t ON pmdsent = pmaalent AND pmds007=pmaal001 AND pmaal002 = ''$lang''\r\n        WHERE pmdsent = pmdtent AND pmdssite = pmdtsite AND pmdsdocno = pmdtdocno\r\n        AND pmdsent = ''$ent''\r\n        AND pmdssite = ''$site''\r\n        AND pmdsstus =''Y''\r\n        AND pmds000 in (''1'',''8'')\r\n        AND COALESCE(pmdt053,0) -COALESCE(pmdt054,0)  > 0 \r\n        #if ($supplierNo != '''')                            ##供应商编号不为空，\r\n           AND pmds007 LIKE ''%$supplierNo%''\r\n        #end   \r\n        #if ($itemNo!= '''')                                 ##料件编号不为空，则\r\n           AND pmdt006 LIKE ''%$itemNo%''\r\n        #end\r\n        #if ($dateBegin != '''')                             ##开始日期\r\n           AND pmdsdocdt>=to_date(''$dateBegin'',''yyyy/mm/dd'')\r\n        #end  \r\n        #if ($dateEnd != '''')                               ##结束日期\r\n           AND pmdsdocdt<=to_date(''$dateEnd'',''yyyy/mm/dd'')\r\n        #end  \r\n        #if ($itemName != '''')                              ##料件名称查询\r\n           AND EXISTS(SELECT 1 from imaal_t WHERE imaalent = pmdtent AND imaal001 = pmdt006 AND imaal003 LIKE ''%$itemName%'')\r\n        #end   \r\nORDER BY  pmdsdocdt desc) t) where item<=''$pagesize''\r\n   ORDER BY pmdsdocdt desc\r\n#end\r\n\r\n#if ($appmodule ==''A008'')   ##采购仓退\r\nselect distinct pmdsdocno as "docNo",to_char(pmdsdocdt,''yyyy/mm/dd'') as "createDate",pmds007 as "supplierNo",pmaal004 as "supplierName" from (\r\n  select row_number() over(order by pmdsdocdt desc) item,pmdsdocno,pmdsdocdt,pmds007,pmaal004 from pmds_t \r\n  LEFT JOIN pmaal_t ON pmaalent=pmdsent AND pmaal001=pmds007 AND pmaal002=''$lang'' \r\n  where pmdsent=''99'' and pmdsdocno in (\r\n  select pmdsdocno fr