r\n#macro(replace $a, $b)\r\n   #set($orderBy = $StringUtils.replace($orderBy, $b, $a)) \r\n#end\r\n\r\n#replace ("a.pmdssite", "siteNo")\r\n#replace ("a.pmds007", "supplierNo")\r\n#replace ("a.pmdsdocno", "prinSideNo")\r\n#replace ("a.pmdsdocdt", "prformDate")\r\n#replace ("a.pmds011", "purchaseType")\r\n#replace ("a.pmds037", "currency")\r\n#replace ("d.ooef016", "localCurrency")\r\n#replace ("a.pmds033", "taxType")\r\n#replace ("pmds035", "isContainTax")\r\n#replace ("a.pmds034", "taxRate")\r\n#replace ("a.pmds032", "tradeCondition")\r\n#replace ("a.pmds031", "paymentCondition")\r\n#replace ("a.pmds036", "tradeType")\r\n#replace ("a.pmds100", "returnType")\r\n#replace ("a.pmds048", "purType")\r\n#replace ("a.pmds049", "exchangeType")\r\n#replace ("a.pmds103", "discount")\r\n#replace ("a.pmds008", "accSupplierNo")\r\n#replace ("a.pmds009", "delSupplierNo")\r\n#replace ("a.pmdscrtid", "creator")\r\n\r\n#macro(FROM)\r\nFROM pmds_t a\r\n   LEFT JOIN pmaal_t b on b.pmaalent = a.pmdsent and b.pmaal001=a.pmds007 and b.pmaal002=''$lang''\r\n   LEFT JOIN pmaal_t b1 ON b1.pmaalent = a.pmdsent AND b1.pmaal001 = a.pmds007 AND b1.pmaal002=''$lang''\r\n   LEFT JOIN pmaal_t b2 ON b2.pmaalent = a.pmdsent AND b2.pmaal001 = a.pmds007 AND b2.pmaal002=''$lang''\r\n   LEFT JOIN ooail_t c ON c.ooailent = a.pmdsent and c.ooail001=a.pmds037 and c.ooail002=''$lang''\r\n   LEFT JOIN ooef_t d  ON d.ooefent = a.pmdsent and d.ooef001 = a.pmdssite \r\n   LEFT JOIN ooail_t e ON e.ooailent = a.pmdsent and e.ooail001=d.ooef016 and e.ooail002=''$lang''\r\n   LEFT JOIN oodbl_t f ON f.oodblent = a.pmdsent and f.oodbl001=d.ooef019 and f.oodbl002=a.pmds033 and f.oodbl003 =''$lang''\r\n   LEFT JOIN oocql_t g ON g.oocqlent = a.pmdsent and g.oocql001 = ''238'' and g.oocql002=a.pmds032 and g.oocql003=''$lang''\r\n   LEFT JOIN ooibl_t h ON h.ooiblent = a.pmdsent and h.ooibl002=a.pmds031 and h.ooibl003=''$lang''\r\n   LEFT JOIN gzcb_t sc ON sc.gzcb001 = ''2062'' AND sc.gzcb002 = a.pmds100\r\n   LEFT JOIN gzcbl_t sc1 ON sc.gzcb002 = sc1.gzcbl002 AND sc.gzcb001 = sc1.gzcbl001 AND sc1.gzcbl003 = ''$lang''  \r\n   LEFT JOIN gzcb_t sc2 ON sc2.gzcb001 = ''2087'' AND sc2.gzcb002 = a.pmds048\r\n   LEFT JOIN gzcbl_t sc3 ON sc2.gzcb002 = sc3.gzcbl002 AND sc2.gzcb001 = sc3.gzcbl001 AND sc3.gzcbl003 = ''$lang''  \r\n   LEFT JOIN gzcb_t sc4 ON sc4.gzcb001 = ''2086'' AND sc4.gzcb002 = a.pmds049\r\n   LEFT JOIN gzcbl_t sc5 ON sc4.gzcb002 = sc5.gzcbl002 AND sc4.gzcb001 = sc5.gzcbl001 AND sc5.gzcbl003 = ''$lang''  \r\n   LEFT JOIN gzcb_t sc6 ON sc6.gzcb001 = ''8325'' AND sc6.gzcb002 = a.pmds103\r\n   LEFT JOIN gzcbl_t sc7 ON sc6.gzcb002 = sc7.gzcbl002 AND sc6.gzcb001 = sc7.gzcbl001 AND sc7.gzcbl003 = ''$lang''  \r\n   LEFT JOIN gzcb_t sc8 ON sc8.gzcb001 = ''2026'' AND sc8.gzcb002 = a.pmds036\r\n   LEFT JOIN gzcbl_t sc9 ON sc8.gzcb002 = sc9.gzcbl002 AND sc8.gzcb001 = sc9.gzcbl001 AND sc9.gzcbl003 = ''$lang''  \r\n   LEFT JOIN ooefl_t site ON site.ooeflent = a.pmdsent AND site.ooefl001 = a.pmdssite AND site.ooefl002=''$lang''\r\n   LEFT JOIN gzcb_t sc10 ON sc10.gzcb001 = ''2052'' AND sc10.gzcb002 = a.pmds011\r\n   LEFT JOIN gzcbl_t sc11 ON sc10.gzcb002 = sc11.gzcbl002 AND sc10.gzcb001 = sc11.gzcbl001 AND sc11.gzcbl003 = ''$lang'' \r\n   LEFT JOIN ooag_t j ON j.ooagent = a.pmdsent AND j.ooag001 = a.pmdscrtid \r\n  WHERE a.pmdsent = ''$ent''\r\n   #if ($site != '''')\r\n      AND a.pmdssite LIKE ''%$site%''\r\n   #end \r\n   AND a.pmdsstus= ''S''\r\n   AND a.pmds000 in (''7'',''14'') \r\n   AND a.pmds011 IN (''1'',''2'')\r\n   #if ($dateBegin != '''')\r\n      AND a.pmdsdocdt>=to_date(''$dateBegin'',''yyyy/mm/dd'')\r\n   #end  \r\n   #if ($dateEnd != '''')\r\n      AND a.pmdsdocdt<=to_date(''$dateEnd'',''yyyy/mm/dd'')\r\n   #end  \r\n   #if ($purchaseType != '''')\r\n      AND a.pmds011 LIKE ''%$purchaseType%''\r\n   #end  \r\n   #if ($supplierNo != '''')\r\n      AND a.pmds007=''$supplierNo''\r\n   #end\r\n   #if ($prinSideNo != '''')\r\n      AND a.pmdsdocno LIKE ''%$prinSideNo%''\r\n   #end     \r\n   #if ($itemNo != '''' || $itemName != '''' || $itemSpec != '''' || $itemFeatureNo != '''' || $fuzzyQuery != '''')\r\n      AND pmdsdocno IN (select distinct pmdsdocno \r\n                         from pmds_t a\r\n                        inner join pmdt_t b on a.pmdsent = b.pmdtent and a.pmdssite = b.pmdtsite and a.pmdsdocno = b.pmdtdocno \r\n                         left join imaal_t on b.pmdt006 = imaal001 and b.pmdtent = imaalent\r\n      									where a.pmdsent = ''$ent''\r\n      								    and a.pmdsstus= ''S''\r\n                          and a.pmds000 in (''7'',''14'')    									   \r\n      									#if ($site != '''')\r\n                          and a.pmdssite LIKE ''%$site%''\r\n                       #end \r\n                       #if ($dateBegin != '''')\r\n                          and a.pmdsdocdt>=to_date(''$dateBegin'',''yyyy/mm/dd'')\r\n                       #end  \r\n                       #if ($dateEnd != '''')\r\n                          and a.pmdsdocdt<=to_date(''$dateEnd'',''yyyy/mm/dd'')\r\n                       #end  \r\n                       #if ($purchaseType != '''')\r\n                          and a.pmds011 LIKE ''%$purchaseType%''\r\n                       #end  \r\n                       #if ($supplierNo != '''')\r\n                          and a.pmds007 = ''$supplierNo''\r\n                       #end  \r\n                       #if ($itemNo != '''')\r\n                          and b.pmdt006 LIKE ''%$itemNo%''\r\n                       #end      \r\n                       #if ($itemName != '''')\r\n                          and imaal003 LIKE ''%$itemName%''\r\n                       #end    \r\n                       #if ($itemSpec != '''')\r\n                          and imaal004 LIKE ''%$itemSpec%''\r\n                       #end \r\n                       #if ($itemFeatureNo != '''')\r\n                          and b.pmdt007 LIKE ''%$itemFeatureNo%''\r\n                       #end   \r\n                       #if ($prinSideNo != '''')\r\n                          and a.pmdsdocno LIKE ''%$prinSideNo%''\r\n                       #end                                                                                                          \r\n                       #if ($bodyparams.fuzzyQuery != '''' ) \r\n                           and (  a.pmdsdocno like ''%$bodyparams.fuzzyQuery%''\r\n                               OR a.pmdssite  like ''%$bodyparams.fuzzyQuery%''\r\n                               OR a.pmds002   like ''%$bodyparams.fuzzyQuery%''\r\n                               OR a.pmds010   like ''%$bodyparams.fuzzyQuery%''\r\n                               OR b.pmdt006   like ''%$bodyparams.fuzzyQuery%''\r\n                               OR b.pmdt007   like ''%$bodyparams.fuzzyQuery%''\r\n                               OR imaal003    like ''%$bodyparams.fuzzyQuery%''\r\n                               OR imaal004    like ''%$bodyparams.fuzzyQuery%'') \r\n                       #end \r\n                      )  \r\n   #end \r\n#end\r\n\r\nSELECT COUNT(1) AS COUNT\r\n #FROM()\r\n;\r\n \r\nSELECT  siteNo "siteNo", siteName "siteName", supplierNo "supplierNo", supplierName "supplierName", supplierShortName "supplierShortName", prinSideNo "prinSideNo",\r\n        prformDate "prformDate",purchaseType "purchaseType", currency "currency", currencyName "currencyName", localCurrency "localCurrency", localCurrencyName "localCurrencyName", taxType "taxType",\r\n        taxName "taxName", taxRate "taxRate", isContainTax "isContainTax", tradeCondition "tradeCondition", conditionName "conditionName", paymentCondition "paymentCondition",\r\n        paymentName "paymentName", tradeType "tradeType", tradeTypeName "tradeTypeName", returnType "returnType", returnTypeName "returnTypeName", purType "purType",\r\n        purTypeName "purTypeName", exchangeType "exchangeType", exchangeTypeName "exchangeTypeName", discount "discount", discountName "discountName", accSupplierNo "accSupplierNo",\r\n        accSupplierName "accSupplierName", accSupplierShortName "accSupplierShortName", delSupplierNo "delSupplierNo", delSupplierName "delSupplierName", delSupplierShortName "delSupplierShortName", creator "creator", modifyTime "modifyTime", creatorName "creatorName" FROM (\r\nSELECT \r\n    a.pmdssite siteNo,                                                             /*企业据点*/\r\n    site.ooefl003 siteName,                                                        /*据点名称*/\r\n    a.pmds007 supplierNo,                                                          /*供应商编号*/    \r\n    NVL(b.pmaal004,a.pmds007) supplierName,                                        /*供应商全称*/    \r\n    nvl(trim(b.pmaal003),a.pmds007) supplierShortName,                             /*供应商简称*/    \r\n    a.pmdsdocno prinSideNo,                                                        /*仓退单号*/\r\n    to_char(a.pmdsdocdt,''YYYY-MM-DD'') prformDate,                                  /*仓退日期*/\r\n    replace(replace(a.pmds011||''-''||NVL(trim(sc11.gzcbl004),a.pmds011),''委外采购'',''来料加工''),''采购'',''订单'') purchaseType,     /*采购类型*/    \r\n    a.pmds037 currency,                                                            /*币种*/\r\n    NVL(c.ooail003,a.pmds037) currencyName,                                        /*币种说明*/\r\n    d.ooef016 localCurrency,                                                       /*本位币*/\r\n    NVL(e.ooail003,d.ooef016) localCurrencyName,                                   /*本位币说明*/\r\n    a.pmds033 taxType,                                                             /*税别*/\r\n    NVL(trim(f.oodbl004),a.pmds033) taxName,                                       /*税别说明*/\r\n    decode(a.pmds035,''Y'',1,0) isContainTax,                                        /*单价含税否*/\r\n    NVL(a.pmds034/100,0) taxRate,                                                  /*税率*/\r\n    a.pmds032 tradeCondition,                                                      /*交易条件*/\r\n    NVL(g.oocql004,a.pmds032) conditionName,                                       /*交易条件说明*/\r\n    a.pmds031 paymentCondition,                                                    /*付款条件*/\r\n    NVL(h.ooibl004,a.pmds031) paymentName,                                         /*付款条件说明*/  \r\n    a.pmds036 tradeType,                                                           /*交易类型*/         \r\n    NVL(trim(sc9.gzcbl004),a.pmds036) tradeTypeName,                               /*交易类型说明*/ \r\n    a.pmds100 returnType,                                                          /*仓退方式*/\r\n    replace(NVL(trim(sc1.gzcbl004),a.pmds100),''采购'',''订单'') returnTypeName,                              /*仓退方式说明*/\r\n    a.pmds048 purType,                                                             /*内外购*/\r\n    NVL(trim(sc3.gzcbl004),a.pmds048) purTypeName,                                 /*内外购说明*/\r\n    a.pmds049 exchangeType,                                                        /*汇率计算基准*/\r\n    NVL(trim(sc5.gzcbl004),a.pmds049) exchangeTypeName,                            /*汇率计算基准说明*/\r\n    a.pmds103 discount,                                                            /*折让证明单开立方式*/\r\n    NVL(trim(sc7.gzcbl004),a.pmds103) discountName,                                /*折让证明单开立方式说明*/\r\n    a.pmds008 accSupplierNo,                                                       /*账款供应商*/       \r\n    NVL(b1.pmaal004,a.pmds008) accSupplierName,                                    /*账款供应商全称*/   \r\n    NVL(trim(b1.pmaal003),a.pmds008) accSupplierShortName,                         /*账款供应商简称*/   \r\n    a.pmds009 delSupplierNo,                                                       /*送货供应商*/       \r\n    NVL(b2.pmaal004,a.pmds009) delSupplierName,                                    /*送货供应商全称*/   \r\n    NVL(trim(b2.pmaal003),a.pmds009) delSupplierShortName,                         /*送货供应商简称*/\r\n    a.pmdscrtid creator,                                                           /*单据建立者*/\r\n    NVL(trim(j.ooag011),a.pmdscrtid) creatorName,                                  /*单据建立者名称*/\r\n    nvl(to_char(a.pmdsmoddt,''yyyy-mm-dd hh24:mi:ss''),to_char(a.pmdscrtdt,''yyyy-mm-dd hh24:mi:ss'')) modifyTime,  /*单据修改时间*/\r\n    DENSE_RANK() OVER( ORDER BY $orderBy a.pmdsent,a.pmdssite, a.pmdsdocdt desc, a.pmdsdocno ) AS RANK \r\n   #FROM()           \r\nORDER BY $orderBy a.pmdsent,a.pmdssite, a.pmdsdocdt desc, a.pmdsdocno\r\n)\r\nWHERE RANK >= $rnstart\r\n  AND RANK <= $rnend\r\nORDER BY RANK'), STRINGDECODE('{\r\n    "std_data": {\r\n        "execution": {\r\n            "code": "$execution.code",\r\n            "sqlcode": "$execution.sqlcode",\r\n            "description": "$execution.description"\r\n        },\r\n        "parameter": {\r\n            "result": [\r\n                {\r\n                    "totolCount": $JSON.toJSONString($sqlData[0][0].COUNT),\r\n                    "listdata": $JSON.toJSONString($sqlData[1])\r\n                }\r\n            ]\r\n        }\r\n    }\r\n}'), STRINGDECODE('仓退单清单列表'), '1', '2017-09-19 16:04:46', '2017-10-19 15:58:07', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('34', '1002', 'listDetailGet', STRINGDECODE('汇总清单'), STRINGDECODE('{\r\n    "std_data": {\r\n        "parameter": {\r\n            "appmodule": "B010",\r\n            "lang": "zh_CN",\r\n            "ent": "99",\r\n            "site": "DSCNJ",\r\n            "docNo": "CNJ-P41-201709300001",\r\n            "productCode": "",\r\n            "stockCode": "",\r\n            "warehouseNo": "Z1W1",\r\n            "availableInQty": ""\r\n        }\r\n    }\r\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\r\n#set ($ent = $bodyparams.ent) \r\n#set ($site = $bodyparams.site) \r\n#set ($lang = $bodyparams.lang)\r\n#set ($appmodule= $bodyparams.appmodule) \r\n#set ($docNo = $bodyparams.docNo)\r\n#set ($warehouseNo= $bodyparams.warehouseNo)\r\n#set ($availableInQty= $bodyparams.availableInQty)\r\n\r\n\r\n#if ($appmodule==''A001'')   ##采购收货\r\nselect pmdldocno as "docNo",to_char(pmdldocdt,''yyyy/mm/dd'') as "createDate",pmdl004 as "supplierNo",pmaal004 as "supplierName",\r\npmdo001 as "itemNo",imaal003 as "itemName",imaal004 as "itemSpec",nvl(pmdo002,'' '') as "productCode",nvl(pmdn053,'' '') as "stockCode",pmdo004 as "unitNo",\r\nnvl(sum(pmdo006),0)*(imaf166/100+1)-nvl(sum(pmdo015),0)+nvl(sum(pmdo016),0)-nvl(a,0) as "applyQty",''0'' as "scanSumQty",\r\nnvl(pmdn028,'' '') as "warehouseNo",nvl(pmdn029,'' '') as "storageSpacesNo",nvl(pmdn030,'' '') as "lotNo"\r\nfrom pmdl_t\r\ninner join pmdo_t on pmdoent=pmdlent and pmdodocno=pmdldocno\r\ninner join pmdn_t on pmdnent=pmdlent and pmdndocno=pmdldocno and pmdoseq=pmdnseq\r\ninner join imaf_t on pmdoent=imafent and pmdosite=imafsite and pmdo001=imaf001\r\nleft join pmaal_t on pmaalent=pmdlent and pmaal001=pmdl004 and pmaal002=''$lang''\r\nleft join imaal_t on imaalent=pmdoent and imaal001=pmdo001 and imaal002=''$lang'' \r\nleft join \r\n(\r\nselect sum(pmdt020) a,pmdtent,pmdtsite,pmdt001,pmdt002,pmdt003,pmdt004\r\n  from pmdt_t\r\n  inner join pmds_t\r\n    on pmdtent = pmdsent\r\n   and pmdtdocno = pmdsdocno\r\n where pmdsstus = ''N''\r\n   and pmdsent = ''99''\r\n   and pmds000 in (''1'',''8'')\r\n   and pmdssite = ''DSCNJ''\r\n group by pmdtent,pmdtsite,pmdt001,pmdt002,pmdt003,pmdt004\r\n) on pmdoent = pmdtent and pmdosite = pmdtsite and pmdodocno = pmdt001 and pmdoseq = pmdt002 and pmdoseq1 = pmdt003 and pmdoseq2 = pmdt004\r\nwhere pmdlent=''$ent''\r\nand pmdldocno=''$docNo''\r\nand pmdlstus=''Y''\r\ngroup by pmdldocno,pmdldocdt,pmdl004,pmaal004,pmdo004,pmdo001,imaal003,imaal004,pmdo002,pmdn053,imaf166,a,pmdn028,pmdn029,pmdn030\r\norder by pmdo001\r\n#end\r\n\r\n#if ($appmodule==''A005'')   ##扫码入库\r\nSELECT pmdsdocno AS "docNo",to_char(pmdsdocdt,''yyyy/mm/dd'') AS "createDate",pmaal004 AS "supplierName",imaal003 AS "itemName",imaal004 AS "itemSpec",\r\nnvl(pmdt007,'' '') as "productCode",nvl(pmdt063,'' '') as "stockCode",pmdt019 AS "unitNo",pmdt006 AS "itemNo",sum(pmdt053-pmdt054) AS "applyQty",0 AS "scanSumQty",\r\nnvl(pmdt016,'' '') as "warehouseNo",nvl(pmdt017,'' '') as "storageSpacesNo",nvl(pmdt018,'' '') as "lotNo"\r\nFROM pmds_t\r\nINNER JOIN pmdt_t ON pmdsent = pmdtent  AND pmdsdocno = pmdtdocno\r\nLEFT JOIN imaal_t ON imaalent = pmdtent AND imaal002 = ''$lang'' AND imaal001 = pmdt006\r\nLEFT JOIN pmaal_t ON pmdsent = pmaalent AND pmaal002 = ''$lang'' AND pmds007=pmaal001\r\nWHERE pmdsent = ''$ent''\r\n  AND pmdsdocno = ''$docNo''\r\n  AND pmdsstus = ''Y''\r\n  AND pmdssite = ''$site''\r\n  AND pmdt053>pmdt054\r\nGROUP BY pmdsdocno,pmdsdocdt,pmaal004,imaal003,imaal004,pmdt019,pmdt006,pmdt007,pmdt063,pmdt016,pmdt017,pmdt018\r\n#end\r\n\r\n#if ($appmodule==''A007'')   ##快速入库\r\nSELECT b.pmdsdocno as "docNo",to_char(b.pmdsdocdt,''yyyy/mm/dd'') as "createDate",pmaal003 as "supplierName",a.pmdtseq as "seq",a.pmdt016 as "warehouseNo",\r\n       imaal003 as "itemName",a.pmdt019 as "unitNo",imaal004 as "itemSpec",a.pmdt007 as "productCode",a.pmdt063 as "stockCode",a.pmdt006 as "itemNo",a.pmdt053-a.pmdt054-nvl(d.pmdt020,0) as "applyQty",a.pmdt053-a.pmdt054-nvl(d.pmdt020,0) as "scanSumQty"\r\nFROM pmdt_t a\r\nLEFT JOIN pmds_t b ON a.pmdtent=b.pmdsent AND a.pmdtsite=b.pmdssite AND a.pmdtdocno=b.pmdsdocno\r\nLEFT JOIN imaal_t ON imaalent = pmdtent AND imaal001 = pmdt006 AND imaal002 = ''$lang''\r\nLEFT JOIN pmaal_t ON pmaalent = pmdtent AND pmaal001 = pmds007 AND pmaal002=''$lang''\r\nLEFT JOIN pmds_t c ON c.pmdsent=b.pmdsent AND c.pmdssite=b.pmdssite AND c.pmds006 = b.pmdsdocno AND c.pmdsstus NOT IN (''X'',''S'')\r\nLEFT JOIN pmdt_t d ON c.pmdsent=d.pmdtent AND c.pmdssite=d.pmdtsite AND c.pmdsdocno=d.pmdtdocno AND d.pmdt028=a.pmdtseq\r\nWHERE b.pmdsstus=''Y''\r\nAND b.pmdsdocno = ''$docNo''\r\nAND b.pmdsent = ''$ent''\r\nAND b.pmdssite = ''$site''\r\nand a.pmdt053-a.pmdt054-nvl(d.pmdt020,0) > 0\r\nORDER BY  b.pmdsdocno,a.pmdtseq,b.pmdsdocdt,b.pmds007,imaal003,imaal004,a.pmdt006,a.pmdt007,a.pmdt063\r\n#end\r\n\r\n#if ($appmodule==''A008'')   ##采购仓退\r\n select A01.*,nvl(sum(B01.inag008),0) as "stockQty" from (\r\n select pmdsdocno as "docNo",to_char(pmdsdocdt,''yyyy/mm/dd'') as "createDate",pmds007 as "supplierNo",pmaal004 as "supplierName",\r\n imaal003 as "itemName",imaal004 as "itemSpec",nvl(pmdt007,'' '') as "productCode",nvl(pmdt063,'' '') as "stockCode",pmdt006 as "itemNo",nvl(sum(pmdt020),0) as "applyQty",\r\n 0 as "scanSumQty",nvl(pmdt016,'' '') as "warehouseNo",nvl(pmdt017,'' '') as "storageSpacesNo",nvl(pmdt018,'' '') as "lotNo",pmdt019 as "unitNo"\r\n from pmds_t\r\n inner join pmdt_t on pmdsdocno=pmdtdocno and pmdtent = ''$ent'' and pmdtsite = ''$site''\r\n left join imaal_t on imaal001=pmdt006 and imaal002 = ''$lang'' and imaalent = ''$ent''\r\n left join pmaal_t on pmds007=pmaal001 and pmaal002 = ''$lang'' and pmaalent = ''$ent''\r\n where pmdsent = ''$ent''\r\n and pmdsdocno=''$docNo''\r\n and pmdsstus=''Y''\r\n and pmdssite = ''$site''\r\n group by pmdsdocno,pmdsdocdt,pmds007,pmaal004,imaal003,imaal004,pmdt006,pmdt007,pmdt063,pmdt016,pmdt017,pmdt018,pmdt019\r\n )A01 left join inag_t B01 \r\n on B01.inag001=A01."itemNo" \r\n and ((B01.inag002=A01."productCode" and A01."productCode"!='' '') or A01."productCode"='' '') \r\n and ((B01.inag003=A01."stockCode" and A01."stockCode"!='' '') or A01."stockCode"='' '') \r\n and ((B01.inag004=A01."warehouseNo" and A01."warehouseNo"!='' '') or A01."warehouseNo"='' '') \r\n and ((B01.inag005=A01."storageSpacesNo" and A01."storageSpacesNo"!='' '') or A01."storageSpacesNo"='' '') \r\n and ((B01.inag006=A01."lotNo" and A01."lotNo"!='' '') or A01."lotNo"='' '')\r\ngroup by "docNo","createDate","supplierNo","supplierName",\r\n"itemNo","itemName","itemSpec","productCode","stockCode","unitNo",\r\n"applyQty","scanSumQty","warehouseNo","storageSpacesNo","lotNo"\r\n#end\r\n\r\n#if ($appmodule==''B007'')   ##入库上架\r\nselect sfeadocno as "docNo",to_char(sfeadocdt,''yyyy/mm/dd'') as "createDate",ooag011 as "employeeName",ooefl003 as "departmentName",\r\n       imaal003 as "itemName",imaal004 as "itemSpec",nvl(sfeb005,'' '') as "productCode",nvl(sfeb016,'' '') as "stockCode",sfeb004 as "itemNo",sum(sfeb009) as "applyQty",0 as "scanSumQty",\r\n       sfeb007 as "unitNo",nvl(sfeb013,'' '') as "warehouseNo",nvl(sfeb014,'' '') as "storageSpacesNo",nvl(sfeb015,'' '') as "lotNo"\r\n from sfeb_t\r\n inner join sfea_t on sfeadocno=sfebdocno and sfebent = sfeaent\r\n inner join imaal_t on imaal001=sfeb004 and imaal002 = ''$lang'' and imaalent = sfebent\r\n left join ooag_t on sfea002=ooag001 and ooagent = sfebent\r\n left join ooefl_t on sfea003=ooefl001 and ooefl002 = ''$lang'' and ooeflent = sfebent\r\n where sfeastus=''Y''\r\n and sfebdocno=''$docNo''\r\n and sfebent = ''$ent''\r\n and sfebsite = ''$site''\r\n and sfeb027 > 0 \r\n group by sfeadocno,sfeadocdt,ooag011,ooefl003,imaal003,imaal004,sfeb004,nvl(sfeb005,'' ''),nvl(sfeb016,'' ''),sfeb007,nvl(sfeb013,'' ''),nvl(sfeb014,'' ''),nvl(sfeb015,'' '')\r\n#end\r\n\r\n#if ($appmodule==''B010'')   ##退料过账\r\nselect A01.*,nvl(sum(B01.inag008),0) as "stockQty" from(\r\n select sfdadocno as "docNo",to_char(sfdadocdt,''yyyy/mm/dd'') as "createDate",sfda003 as "departmentNo",ooefl003 as "departmentName",sfda004 as "employeeNo",\r\n ooag011 as "employeeName",sfdc004 as "itemNo",imaal003 as "itemName",imaal004 as "itemSpec",sfdc005 as "productCode",sfdc016 as "stockCode",sfdc006 as "unitNo",\r\n nvl(sum(sfdc007),0) as "applyQty",0 as "scanSumQty",sfdc012 as "warehouseNo",sfdc013 as "storageSpacesNo",sfdc014 as "lotNo" from (\r\n select sfdadocno,sfdadocdt,sfda003,ooefl003,sfda004,ooag011,sfdc004,imaal003,imaal004,sfdc005,sfdc016,sfdc006,sfdc012,sfdc013,sfdc014,sfdc007,0 from sfda_t\r\n inner join sfdc_t on sfdcent=sfdaent and sfdcsite=sfdasite and sfdcdocno=sfdadocno \r\n inner join sfdd_t on sfddent=sfdaent and sfddsite=sfdasite and sfdddocno=sfdadocno and sfdcseq=sfddseq        \r\n left join ooefl_t on ooeflent=sfdaent and ooefl001=sfda003 and ooefl002=''$lang''\r\n left join ooag_t on ooagent=sfdaent and ooagstus=''Y'' and ooag001=sfda004\r\n left join imaal_t on imaalent=sfdaent and imaal002=''$lang'' and imaal001=sfdd001\r\n where sfdaent=''$ent'' \r\n and sfdasite=''$site'' \r\n and sfda002 in (''21'',''22'',''23'',''24'',''25'') \r\n and sfdastus=''Y'' \r\n and sfdadocno=''$docNo'' \r\n ) group by sfdadocno,sfdadocdt,sfda003,ooefl003,sfda004,ooag011,sfdc004,imaal003,imaal004,sfdc005,sfdc016,sfdc006,sfdc012,sfdc013,sfdc014\r\n ) A01 left join inag_t B01 \r\n on B01.inag001=A01."itemNo" \r\n and ((B01.inag002=A01."productCode" and A01."productCode"!='' '') or A01."productCode"='' '') \r\n and ((B01.inag003=A01."stockCode" and A01."stockCode"!='' '') or A01."stockCode"='' '') \r\n and ((B01.inag004=A01."warehouseNo" and A01."warehouseNo"!='' '') or A01."warehouseNo"='' '') \r\n and ((B01.inag005=A01."storageSpacesNo" and A01."storageSpacesNo"!='' '') or A01."storageSpacesNo"='' '') \r\n and ((B01.inag006=A01."lotNo" and A01."lotNo"!='' '') or A01."lotNo"='' '')\r\ngroup by "docNo","createDate","departmentNo","departmentName","employeeNo",\r\n"employeeName","itemNo","itemName","itemSpec","productCode","stockCode","unitNo",\r\n"applyQty","scanSumQty","warehouseNo","storageSpacesNo","lotNo"\r\n#end\r\n\r\n#if ($appmodule==''B011'')   ##生产超领\r\nselect A01.*,nvl(sum(B01.inag008),0) as "stockQty" from(\r\n select sfdadocno as "docNo",to_char(sfdadocdt,''yyyy/mm/dd'') as "createDate",sfda003 as "departmentNo",ooefl003 as "departmentName",sfda004 as "employeeNo",ooag011 as "employeeName",\r\n sfdc004 as "itemNo",imaal003 as "itemName",imaal004 as "itemSpec",sfdc005 as "productCode",sfdc016 as "stockCode",sfdc006 as "unitNo",nvl(sum(sfdc007),0) as "applyQty",0 as "scanSumQty",\r\n sfdc012 as "warehouseNo",sfdc013 as "storageSpacesNo",sfdc014 as "lotNo" from (\r\n select sfdadocno,sfdadocdt,sfda003,ooefl003,sfda004,ooag011,sfdc004,imaal003,imaal004,sfdc005,sfdc016,sfdc006,sfdc007,sfdc012,sfdc013,sfdc014,0 from sfda_t\r\n inner join sfdc_t on sfdcent=sfdaent and sfdcsite=sfdasite and sfdcdocno=sfdadocno \r\n inner join sfdd_t on sfddent=sfdaent and sfddsite=sfdasite and sfdddocno=sfdadocno and sfdcseq=sfddseq         \r\n left join ooefl_t on ooeflent=sfdaent and ooefl001=sfda003 and ooefl002=''$lang''\r\n left join ooag_t on ooagent=sfdaent and ooagstus=''Y'' and ooag001=sfda004\r\n left join imaal_t on imaalent=sfdaent and imaal002=''$lang'' and imaal001=sfdd001\r\n where sfdaent=''$ent'' \r\n and sfdasite=''$site'' \r\n and sfda002=''12'' \r\n and sfdastus=''Y'' \r\n and sfdadocno=''$docNo'' \r\n and sfdc007>0\r\n ) group by sfdadocno,sfdadocdt,sfda003,ooefl003,sfda004,ooag011,sfdc004,imaal003,imaal004,sfdc005,sfdc016,sfdc006,sfdc012,sfdc013,sfdc014\r\n ) A01 left join inag_t B01 \r\n on B01.inag001=A01."itemNo" \r\n and ((B01.inag002=A01."productCode" and A01."productCode"!='' '') or A01."productCode"='' '') \r\n and ((B01.inag003=A01."stockCode" and A01."stockCode"!='' '') or A01."stockCode"='' '') \r\n and ((B01.inag004=A01."warehouseNo" and A01."warehouseNo"!='' '') or A01."warehouseNo"='' '') \r\n and ((B01.inag005=A01."storageSpacesNo" and A01."storageSpacesNo"!='' '') or A01."storageSpacesNo"='' '') \r\n and ((B01.inag006=A01."lotNo" and A01."lotNo"!='' '') or A01."lotNo"='' '')\r\ngroup by "docNo","createDate","departmentNo","departmentName","employeeNo",\r\n"employeeName","itemNo","itemName","itemSpec","productCode","stockCode","unitNo",\r\n"applyQty","scanSumQty","warehouseNo","storageSpacesNo","lotNo"\r\n#end\r\n\r\n#if ($appmodule==''B016'')   ##领料过账\r\n select A01.*,nvl(sum(B01.inag008),0) as "stockQty" from (\r\n select sfdadocno as "docNo",to_char(sfdadocdt,''yyyy/mm/dd'') as "createDate",sfda003 as "departmentNo",ooefl003 as "departmentName",sfda004 as "employeeNo",ooag011 as "employeeName",\r\n       sfdc004 as "itemNo",imaal003 as "itemName",imaal004 as "itemSpec",sfdc005 as "productCode",sfdc016 as "stockCode",sfdc006 as "unitNo",nvl(sum(sfdc007),0) as "applyQty",0 as "scanSumQty",nvl(sfdc012,'' '') AS "warehouseNo",nvl(sfdc013,'' '') as "storageSpacesNo",nvl(sfdc014,'' '') as "lotNo" from (\r\n select sfdadocno,sfdadocdt,sfda003,ooefl003,sfda004,ooag011,sfdc004,imaal003,imaal004,sfdc005,sfdc016,sfdc006,sfdc007,0,sfdc012,sfdc013,sfdc014\r\n  from sfda_t\r\n inner join sfdc_t on sfdcent=sfdaent and sfdcsite=sfdasite and sfdcdocno=sfdadocno \r\n left join sfdd_t on sfddent=sfdaent and sfddsite=sfdasite and sfdddocno=sfdadocno and sfdcseq=sfddseq             \r\n left join ooefl_t on ooeflent=sfdaent and ooefl001=sfda003 and ooefl002=''$lang''\r\n left join ooag_t on ooagent=sfdaent and ooagstus=''Y'' and ooag001=sfda004\r\n left join imaal_t on imaalent=sfdaent and imaal002=''$lang'' and imaal001=sfdd001\r\n where sfdaent=''$ent'' \r\n and sfdasite=''$site'' \r\n and sfda002=''11'' \r\n and sfdastus=''Y'' \r\n and sfdadocno=''$docNo''  \r\n ) group by sfdadocno,sfdadocdt,sfda003,ooefl003,sfda004,ooag011,sfdc004,imaal003,imaal004,sfdc005,sfdc016,sfdc006,sfdc012,sfdc013,sfdc014\r\n ) A01 left join inag_t B01 \r\n on B01.inag001=A01."itemNo" \r\n and ((B01.inag002=A01."productCode" and A01."productCode"!='' '') or A01."productCode"='' '') \r\n and ((B01.inag003=A01."stockCode" and A01."stockCode"!='' '') or A01."stockCode"='' '') \r\n and ((B01.inag004=A01."warehouseNo" and A01."warehouseNo"!='' '') or A01."warehouseNo"='' '') \r\n and ((B01.inag005=A01."storageSpacesNo" and A01."storageSpacesNo"!='' '') or A01."storageSpacesNo"='' '') \r\n and ((B01.inag006=A01."lotNo" and A01."lotNo"!='' '') or A01."lotNo"='' '')\r\ngroup by "docNo","createDate","departmentNo","departmentName","employeeNo",\r\n"employeeName","itemNo","itemName","itemSpec","productCode","stockCode","unitNo",\r\n"applyQty","scanSumQty","warehouseNo","storageSpacesNo","lotNo"\r\n#end\r\n\r\n#if ($appmodule==''B023'')   ##退料补料\r\nSELECT sfdcdocno as "docNo",to_char(sfdadocdt,''yyyy/mm/dd'') as "createDate",ooag011 as "employeeName",ooefl003 as "departmentName",\r\nimaal003 as "itemName",imaal004 as "itemSpec",nvl(sfdc005,'' '') as "productCode",nvl(sfdc016,'' '') as "stockCode",sfdc004 as "itemNo",\r\nsfdc006 as "unitNo",least(SUM(sfdc008),SUM(sfba013-sfba016)) as "applyQty",0 as "scanSumQty",nvl(sfdc012,'' '') as "warehouseNo",nvl(sfdc013,'' '') as "storageSpacesNo",nvl(sfdc014,'' '') as "lotNo"\r\nFROM sfdc_t                   \r\nINNER JOIN sfda_t ON sfdadocno=sfdcdocno AND sfdaent = sfdcent AND sfdasite = sfdcsite                   \r\nLEFT JOIN ooag_t ON ooag001=sfda004 AND ooagent = ''$ent''\r\nLEFT JOIN ooefl_t ON ooefl001=sfda003 AND ooefl002 = ''$site'' AND ooeflent = ''$ent''\r\nLEFT JOIN imaal_t ON imaal001=sfdc004 AND imaal002 = ''$lang'' AND imaalent = ''$ent''\r\nLEFT JOIN sfba_t ON sfba006=sfdc004 AND sfbadocno=sfdc001 AND sfbaent = ''$ent'' AND sfbasite = ''$site''\r\nWHERE sfdastus=''S''\r\n AND sfdadocno=''$docNo''\r\n AND sfdaent = ''$ent''\r\n AND sfdasite = ''$site''\r\n AND sfba013-sfba016 > 0\r\nGROUP BY  sfdcdocno,sfdadocdt ,ooag011,ooefl003,imaal003 ,imaal004,sfdc005,sfdc016,sfdc004 ,sfdc006 ,sfdc012,sfdc013,sfdc014                                 \r\nORDER BY sfdc004 \r\n#end\r\n\r\n#if ($appmodule==''C002'')   ##调拨过账\r\nselect A01.*,nvl(sum(B01.inag008),0) as "stockQty" from(\r\nselect indcdocno as "docNo",to_char(indcdocdt,''yyyy/mm/dd'') as "createDate",ooag011 as "employeeName",ooefl003 as "departmentName",imaal003 as "itemName",indd006 as "unitNo",\r\n       imaal004 as "itemSpec",nvl(indd004,'' '') as "productCode",nvl(indd102,'' '') as "stockCode",indd002 as "itemNo",indd021 as "applyQty",\r\n       0 as "scanSumQty",nvl(indd022,'' '') as "warehouseNo",nvl(indd023,'' '') "storageSpacesNo",nvl(indd024,'' '') as "lotNo"\r\n from indc_t\r\n inner join indd_t on indcent = inddent\r\n and indcsite = inddsite\r\n and indcdocno = indddocno\r\n LEFT JOIN ooag_t ON indcent = ooagent AND ooag001 = indc004\r\n LEFT JOIN ooefl_t ON ooeflent = indcent AND ooefl001 = indc005 AND ooefl002 = ''$lang''                    \r\n LEFT join imaal_t on imaalent = indcent and imaal002 = ''$lang'' and imaal001 = indd002\r\n LEFT join imaa_t on imaaent = indcent and imaa001 = indd002 \r\nWHERE indcdocno = ''$docNo''\r\n  and indcent = ''$ent''\r\n  and indcsite = ''$site''\r\n  AND indcstus = ''O''\r\nORDER BY  indd002  \r\n)A01 left join inag_t B01 \r\n on B01.inag001=A01."itemNo" \r\n and ((B01.inag002=A01."productCode" and A01."productCode"!='' '') or A01."productCode"='' '') \r\n and ((B01.inag003=A01."stockCode" and A01."stockCode"!='' '') or A01."stockCode"='' '') \r\n and ((B01.inag004=A01."warehouseNo" and A01."warehouseNo"!='' '') or A01."warehouseNo"='' '') \r\n and ((B01.inag005=A01."storageSpacesNo" and A01."storageSpacesNo"!='' '') or A01."storageSpacesNo"='' '') \r\n and ((B01.inag006=A01."lotNo" and A01."lotNo"!='' '') or A01."lotNo"='' '')\r\ngroup by "docNo","createDate","departmentName",\r\n"employeeName","itemNo","itemName","itemSpec","productCode","stockCode","unitNo",\r\n"applyQty","scanSumQty","warehouseNo","storageSpacesNo","lotNo"\r\n#end \r\n\r\n#if ($appmodule==''C007'')   ##杂项发料\r\ns