Price")\r\n#replace ("j.pmdt038", "noTaxAmount")\r\n#replace ("j.pmdt039", "taxAmount")\r\n#replace ("j.pmdt018", "lotNo")\r\n#replace ("j.pmdt059", "remark")\r\n#replace ("j.pmdt051||''-''||q.oocql004", "returnReasonNo")\r\n#replace ("nvl(g.ooaj003,0)", "priceDecimalNum")\r\n#replace ("nvl(g.ooaj004,0)", "amountDecimalNum")\r\n#replace ("j.pmdt046", "tax")\r\n#replace ("p.pmds037", "currencyCode")\r\n#replace ("j.pmdt037", "taxRate")\r\n\r\n#if($StringUtils.isNotEmpty($bodyparams.pageNumber)&&$StringUtils.isNotEmpty($bodyparams.pageSize))\r\n  #set ($rnstart = ($Integer.parseInt($bodyparams.pageNumber) - 1) * $Integer.parseInt($bodyparams.pageSize) + 1)\r\n  #set ($rnend = $Integer.parseInt($bodyparams.pageNumber) * $Integer.parseInt($bodyparams.pageSize))\r\n#else \r\n	#set ($rnstart = 1)\r\n	#set ($rnend = 5000)\r\n#end\r\n\r\n\r\n SELECT COUNT(1) AS COUNT\r\n   from pmdt_t j                                                                                                                                           \r\n   left outer join imaal_t k on k.imaalent=j.pmdtent and k.imaal001=j.pmdt006 and k.imaal002=''$lang''                                \r\n   left outer join inaa_t l  on l.inaaent =j.pmdtent and l.inaasite=j.pmdtsite and l.inaa001=j.pmdt016                        \r\n   left outer join inab_t m  on m.inabent =j.pmdtent and m.inabsite=j.pmdtsite and m.inab001=j.pmdt016 and m.inab002=j.pmdt017\r\n   left outer join oocal_t n on n.oocalent=j.pmdtent and n.oocal001=j.pmdt019 and n.oocal002=''$lang''                                \r\n   left outer join oocal_t o on o.oocalent=j.pmdtent and o.oocal001=j.pmdt023 and o.oocal002=''$lang''  \r\n   left outer join oocal_t r on r.oocalent=j.pmdtent and r.oocal001=j.pmdt021 and r.oocal002=''$lang''                              \r\n   left outer join pmds_t p  on p.pmdsent=j.pmdtent and p.pmdsdocno=j.pmdtdocno                                               \r\n   left outer join oocql_t q on j.pmdtent = q.oocqlent and j.pmdt051 = q.oocql002 and q.oocql001 =''313'' and q.oocql003 = ''$lang''    \r\n   left outer join ooail_t c on c.ooailent = p.pmdsent and c.ooail001=p.pmds037 and c.ooail002=''$lang''                              \r\n   inner join ooef_t e on (e.ooefent=j.pmdtent and e.ooef001=j.pmdtsite)                                                                  \r\n   inner join ooaj_t g on (j.pmdtent=g.ooajent and e.ooef014=g.ooaj001 and p.pmds037=g.ooaj002)                               \r\n   left outer join inayl_t inay ON inay.inaylent = j.pmdtent AND inay.inayl001 = j.pmdt016 AND inay.inayl002=''$lang''       \r\n   where j.pmdtent = ''$ent''      \r\n   #if ($site != '''')\r\n      and j.pmdtsite= ''$site''    \r\n   #end\r\n   #if ($prinsideNo.size() > 0 )\r\n   		AND j.pmdtdocno in $VEUtils.SqlIn($prinsideNo)\r\n   #end;\r\n\r\nSELECT * FROM (\r\n   SELECT j.pmdtdocno "returnDocNo",                      /*仓退单号*/\r\n   j.pmdtseq "seq",                                       /*项次*/\r\n   j.pmdt001 "purchaseNo",                                /*采购单号*/\r\n   j.pmdt002 "purchaseSeq",                               /*采购项次*/\r\n   j.pmdt003 "purchaseLineSeq",                           /*采购项序*/\r\n   j.pmdt004 "purchaseBatchSeq",                          /*采购分批序*/\r\n   j.pmdt027 "receiptNo",                                 /*收货单号*/\r\n   j.pmdt028 "receiptSeq",                                /*收货项次*/\r\n   j.pmdt006 "itemNo",                                    /*料件编号*/\r\n   NVL(trim(k.imaal003),j.pmdt006) "itemName",            /*料件名称*/\r\n   NVL(trim(k.imaal004),j.pmdt006) "itemSpec",            /*料件规格*/\r\n   j.pmdt016 "wareHouseNo",                               /*库位*/\r\n   NVL(trim(inay.inayl003),j.pmdt016) "wareHouseName",    /*库位说明*/\r\n   j.pmdt017 "storageSpaceNo",                            /*储位*/\r\n   NVL(trim(m.inab003),j.pmdt017) "storageSpaceName",     /*储位说明*/\r\n   j.pmdt020 "returnQty",                                 /*仓退数量*/\r\n   j.pmdt022 "referen