INSERT INTO `iccn_ica_service` VALUES ('38', '1001', 'purchase_type_list', STRINGDECODE('采购性质列表'), STRINGDECODE('{\r\n    "bodyMap": {\r\n        "std_data": {\r\n            "parameter": {\r\n                "lang": "zh_CN"\r\n            }\r\n        }\r\n    }\r\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\r\n#set ($lang = $bodyparams.lang)\r\n\r\nSELECT sc.gzcb002 "purchaseType",\r\n       replace(replace(NVL(trim(sc1.gzcbl004),sc.gzcb002),''委外采购'',''来料加工''),''采购'',''订单'') "purchaseTypeName",\r\n       replace(replace(sc.gzcb002||''-''||NVL(trim(sc1.gzcbl004),sc.gzcb002),''委外采购'',''来料加工''),''采购'',''订单'') "purchaseTypeWithName"\r\n  FROM gzcb_t sc LEFT JOIN gzcbl_t sc1 ON sc.gzcb002 = sc1.gzcbl002 AND sc.gzcb001 = sc1.gzcbl001 AND sc1.gzcbl003 = ''$lang'' \r\n WHERE sc.gzcb001 = ''2052''\r\n       AND sc.gzcb002 IN (''1'',''2'')'), STRINGDECODE('{\r\n    "std_data": {\r\n        "execution": {\r\n            "code": "$execution.code",\r\n            "sqlcode": "$execution.sqlcode",\r\n            "description": "$execution.description"\r\n        },\r\n        "parameter": {\r\n            "result": [\r\n                {\r\n                  "listdata": $JSON.toJSONString($sqlData[0])\r\n                }\r\n            ]\r\n        }\r\n    }\r\n}'), '', '1', '2017-09-19 16:04:46', '2017-10-20 10:07:52', '0', 't100');
