"$execution.description"\n        },\n        "parameter": {\n            "result": [\n                {\n                  "listdata": $JSON.toJSONString($sqlData[0])\n                }\n            ]\n        }\n    }\n}'), ' ', '1', '2017-09-19 16:04:46', '2017-09-19 16:04:46', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('42', '1001', 'storage_spaces_query', STRINGDECODE('VMI仓库储位查询'), STRINGDECODE('{\r\n	"bodyMap": {\r\n		"std_data": {\r\n			"parameter": {\r\n				"ent": "99",\r\n				"site": "DSCNJ",\r\n				"lang": "zh_CN",\r\n				"warehouseNo": [],\r\n				"warehouseName": "",\r\n				"storageSpacesNo": [],\r\n				"storageSpacesName": ""				\r\n			}\r\n		}\r\n	}\r\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\r\n#set ($ent = $bodyparams.ent)\r\n#set ($site = $bodyparams.site)\r\n#set ($lang = $bodyparams.lang)\r\n#if ($lang != '''')\r\n#set ($lang = ''zh_CN'') \r\n#end\r\n\r\nSELECT a.inaasite "site",                                            /*据点编号*/\r\n       NVL(trim(site.ooefl003),a.inaasite) "siteName",               /*据点名称*/\r\n       a.inaa001 "warehouseNo",                                      /*库位编号*/\r\n       c.inayl003 "warehouseName",                                   /*库位名称*/\r\n       b.inab002 "storageSpacesNo",                                  /*储位编号*/\r\n       DECODE(b.inab002,'' '',b.inab002,b.inab003) "storageSpacesName" /*储位编号*/ \r\n  FROM inaa_t a\r\n       INNER JOIN inab_t b ON b.inabent = a.inaaent AND b.inabsite = a.inaasite AND b.inab001 = a.inaa001 AND trim(inab002) IS NOT NULL\r\n        LEFT JOIN inayl_t c ON c.inaylent= a.inaaent AND c.inayl001 = a.inaa001 AND c.inayl002=''$lang''\r\n        LEFT JOIN ooefl_t site ON site.ooeflent = a.inaaent AND site.ooefl001=a.inaasite AND site.ooefl002=''$lang'' \r\n WHERE a.inaaent = ''$ent''\r\n   AND a.inaasite = ''$site''\r\n #if ($bodyparams.warehouseNo.size() > 0)\r\n 		AND a.inaa001 in $VEUtils.SqlIn($bodyparams.warehouseNo)\r\n #end \r\n #if ($bodyparams.storageSpacesNo.size() > 0)\r\n	  AND b.inab002 in $VEUtils.SqlIn($bodyparams.storageSpacesNo)\r\n #end \r\n #if($StringUtils.isNotEmpty($bodyparams.warehouseName))\r\n	  AND c.inayl003 = ''$bodyparams.warehouseName''\r\n #end \r\n #if($StringUtils.isNotEmpty($bodyparams.storageSpacesName))\r\n	  AND b.inab003 = ''$bodyparams.storageSpacesName''\r\n #end    \r\n    AND a.inaa001 IN (SELECT DISTINCT inac001 FROM inac_t \r\n                                   INNER JOIN ooab_t ON ooab001 = ''S-BAS-0043'' AND ooab002 <> ''NOT_DEFINED'' AND ooab002 IS NOT NULL\r\n                                                    AND ooabent = inacent AND ooabsite = inacsite AND inac003 = ooab002\r\n                      WHERE inacent = a.inaaent \r\n                        AND inacsite = a.inaasite \r\n                        AND inac001 = a.inaa001 )'), STRINGDECODE('{\r\n    "std_data": {\r\n        "execution": {\r\n            "code": "$execution.code",\r\n            "sqlcode": "$execution.sqlcode",\r\n            "description": "$execution.description"\r\n        },\r\n        "parameter": {\r\n            "result": [\r\n                {\r\n                  "listdata": $JSON.toJSONString($sqlData[0])\r\n                }\r\n            ]\r\n        }\r\n    }\r\n}'), '', '1', '2017-09-19 16:04:46', '2017-09-26 11:43:08', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('43', '1001', 'safety_stock_query', STRINGDECODE('安全库存查询'), STRINGDECODE('{\r\n	"bodyMap": {\r\n		"std_data": {\r\n			"parameter": {\r\n				"ent": "99",\r\n				"site": "",\r\n				"lang": "zh_CN",\r\n				"fuzzyQuery": "",\r\n				"warehouseNo": [],\r\n				"warehouseName": "",\r\n				"storageSpacesNo": [],\r\n				"storageSpacesName": "",\r\n                               "warehouseQuery" :"",\r\n                               "storageSpacesQuery": "",\r\n                               "itemNo": "",\r\n                                "itemName": "",\r\n                                "itemSpec": "",\r\n                                "itemFeatureNo": "",\r\n			       "siteWithWarehouseWithStorageSpaces": ["DSCNJ@VMI-CH@CH1","DSCTC@TANG_VMI@","DSCNJ@LX0029-01@"],\r\n			  "pageNumber": "1",\r\n			  "pageSize": "100",\r\n                          "orderBy": ""\r\n			}\r\n		}\r\n	}\r\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\r\n#set ($ent = $bodyparams.ent)\r\n#set ($site = $bodyparams.site)\r\n#set ($lang = $bodyparams.lang)\r\n#if ($lang != '''')\r\n#set ($lang = ''zh_CN'') \r\n#end\r\n\r\n#set ($orderBy= $bodyparams.orderBy) \r\n#if ($orderBy!= '''')\r\n#set ($orderBy=$orderBy+'','') \r\n#end\r\n\r\n#set ($siteWithWarehouseWithStorageSpaces= $bodyparams.siteWithWarehouseWithStorageSpaces)\r\n#set ($warehouseQuery= $bodyparams.warehouseQuery)\r\n#set ($storageSpacesQuery= $bodyparams.storageSpacesQuery)\r\n\r\n#if($StringUtils.isNotEmpty($bodyparams.pageSize)&&$StringUtils.isNotEmpty($bodyparams.pageNumber))\r\n  #set ($rnend = $Integer.parseInt($bodyparams.pageNumber) * $Integer.parseInt($bodyparams.pageSize))\r\n  #set ($rnstart = ($Integer.parseInt($bodyparams.pageNumber) - 1) * $Integer.parseInt($bodyparams.pageSize) + 1)\r\n#else \r\n	#set ($rnstart = 1)\r\n	#set ($rnend = 5000)\r\n#end\r\n\r\n#macro(select)\r\nSELECT a.inaasite site,                                /*据点编号*/\r\n       NVL(trim(site.ooefl003),a.inaasite) sitename,   /*据点名称*/\r\n       a.inaa001 warehouseNo,                          /*库位编号*/\r\n       NVL(trim(t2.inayl003),a.inaa001) warehouseName, /*库位名称*/       \r\n       b.inab002 storageSpacesNo,                      /*储位编号*/\r\n       DECODE(b.inab002,'' '',b.inab002,b.inab003) storageSpacesName, /*储位名称*/ \r\n       c.inag001 itemNo,                               /*产品编号*/\r\n       NVL(trim(t1.imaal003),c.inag001) itemName,      /*产品名称*/\r\n       NVL(trim(t1.imaal004),c.inag001) itemSpec,       /*产品规格*/\r\n       c.inag002 itemFeatureNo,                        /*产品特征*/\r\n       c.inag007 unitNo,                               /*单位*/\r\n       NVL(trim(t3.oocal003),c.inag007) unitName,      /*单位名称*/\r\n       d.inaf007 supplyPointQty,                       /*补给点数量，料件库存在此之下则需补充数量*/\r\n       d.inaf008 supplyQty,                            /*补给量*/\r\n       NVL(SUM(c.inag008),0) inventoryQty,             /*库存量*/\r\n       d.inaf006 safeQty,                              /*安全库存*/\r\n       CASE WHEN NVL(SUM(c.inag009),0) <= d.inaf007 THEN d.inaf008 ELSE 0 END AS neededSupplyQty, /*需补给量*/\r\n       DENSE_RANK() OVER( ORDER BY $orderBy  a.inaasite,a.inaa001,b.inab002,c.inag001,c.inag002,c.inag007 ) AS RANK \r\n  FROM inaa_t a\r\n  LEFT JOIN inab_t b ON b.inabent = a.inaaent AND b.inabsite = a.inaasite AND b.inab001 = a.inaa001 \r\n  INNER JOIN inag_t c ON c.inagent = a.inaaent AND c.inagsite = a.inaasite AND c.inag004 = a.inaa001 AND c.inag005 = b.inab002  \r\n  LEFT JOIN inaf_t d ON c.inagent = d.inafent AND c.inagsite = d.inafsite AND c.inag001 = d.inaf003\r\n                    AND c.inag002 = d.inaf004 AND c.inag004 = d.inaf001 AND c.inag005 = d.inaf002 AND c.inag007 = d.inaf005 AND d.inafstus = ''Y'' \r\n  LEFT JOIN imaal_t t1 ON t1.imaalent= a.inaaent AND t1.imaal001=c.inag001 AND t1.imaal002=''$lang'' \r\n  LEFT JOIN inayl_t t2 ON t2.inaylent= a.inaaent AND t2.inayl001 = a.inaa001  AND t2.inayl002=''$lang''     \r\n  LEFT JOIN oocal_t t3 ON t3.oocalent= a.inaaent AND t3.oocal001= c.inag007 AND t3.oocal002=''$lang''\r\n  LEFT JOIN ooefl_t site ON site.ooeflent = a.inaaent AND site.ooefl001 = a.inaasite AND site.ooefl002=''$lang''  \r\n   #if($siteWithWarehouseWithStorageSpaces.size() > 0)\r\n     ,(select substr(id,1,instr(id,''@'',1,1)-1) as site,   --diao的不要不要的函数，我都佩服我自己\r\n              substr(id,instr(id,''@'',1,1)+1,(instr(id,''@'',1,2)-instr(id,''@'',1,1))-1) as kw,\r\n              substr(id,instr(id,''@'',1,2)+1,(length(id)-instr(id,''@'',1,2))) as cw\r\n     -- from ( with a as (select ''!''||replace(replace(''$siteWithWarehouseWithStorageSpaces'',''",'',''!''),''"'','''') id from dual) 因为数组带''[]'',所有要去掉\r\n     from ( with a as (select ''!''||replace(replace(replace(replace(''$siteWithWarehouseWithStorageSpaces'',''['',''''),'']'',''''),''",'',''!''),''"'','''') id from dual)\r\n           select regexp_substr(id,''[^!]+'',1,rownum) id from a\r\n          connect by rownum <= length(regexp_replace(id,''[^!]+'')))) s\r\n   #end   \r\n WHERE a.inaaent= ''$ent''\r\n   #if($StringUtils.isNotEmpty($bodyparams.site))\r\n      AND a.inaasite= ''$site'' \r\n   #end \r\n   #if($siteWithWarehouseWithStorageSpaces.size() > 0)   \r\n   AND a.inaasite = s.site\r\n   AND a.inaa001 = s.kw \r\n   AND (trim(c.inag001) is not null OR trim(b.inab002) is not null )    \r\n   AND (CASE WHEN trim(s.cw) is not null then b.inab002 ELSE ''1'' END) = NVL(trim(s.cw),''1'')\r\n   #end    \r\n   #if ($bodyparams.warehouseNo.size() > 0)\r\n   		AND (a.inaa001 in $VEUtils.SqlIn($bodyparams.warehouseNo))\r\n   #end \r\n   #if ($bodyparams.storageSpacesNo.size() > 0)\r\n	    AND b.inab002 in $VEUtils.SqlIn($bodyparams.storageSpacesNo)\r\n   #end \r\n   #if($StringUtils.isNotEmpty($bodyparams.warehouseName))\r\n	    AND t2.inayl003 = ''$bodyparams.warehouseName''\r\n   #end   \r\n\r\n   #if($StringUtils.isNotEmpty($bodyparams.itemNo))\r\n	    AND c.inag001 LIKE ''%$bodyparams.itemNo%''\r\n   #end \r\n   #if($StringUtils.isNotEmpty($bodyparams.itemName))\r\n	    AND t1.imaal003 LIKE ''%$bodyparams.itemName%''\r\n   #end \r\n   #if($StringUtils.isNotEmpty($bodyparams.itemSpec))\r\n	    AND t1.imaal004 LIKE ''%$bodyparams.itemSpec%''\r\n   #end \r\n   #if($StringUtils.isNotEmpty($bodyparams.itemFeatureNo))\r\n	    AND c.inag002 LIKE ''%$bodyparams.itemFeatureNo%''\r\n   #end \r\n               \r\n\r\n   #if($StringUtils.isNotEmpty($warehouseQuery))\r\n	    AND (   a.inaa001 LIKE ''%$warehouseQuery%''\r\n	         OR t2.inayl003 LIKE ''%$warehouseQuery%''\r\n	        )\r\n   #end\r\n   #if($StringUtils.isNotEmpty($storageSpacesQuery))\r\n	    AND (   b.inab002 LIKE ''%$storageSpacesQuery%''\r\n	         OR b.inab003 LIKE ''%$storageSpacesQuery%''\r\n	        )\r\n   #end\r\n   #if($StringUtils.isNotEmpty($bodyparams.warehouseName))\r\n	    AND b.inab003 = ''$bodyparams.storageSpacesName''\r\n   #end     \r\n   #if($StringUtils.isNotEmpty($bodyparams.fuzzyQuery))\r\n	    AND (   a.inaasite LIKE ''%$bodyparams.fuzzyQuery%'' \r\n	         OR c.inag001 LIKE ''%$bodyparams.fuzzyQuery%'' \r\n	         OR t1.imaal003 LIKE ''%$bodyparams.fuzzyQuery%'' \r\n	         OR t1.imaal004 LIKE ''%$bodyparams.fuzzyQuery%''\r\n	         OR c.inag002 LIKE ''%$bodyparams.fuzzyQuery%''	         \r\n	         OR c.inag007 LIKE ''%$bodyparams.fuzzyQuery%''\r\n	         OR a.inaa001 LIKE ''%$bodyparams.fuzzyQuery%''\r\n	         OR b.inab002 LIKE ''%$bodyparams.fuzzyQuery%''\r\n	    		)\r\n   #end  \r\n --  AND a.inaa001 IN (SELECT DISTINCT inac001 FROM inac_t \r\n --                                            INNER JOIN ooab_t ON ooab001 = ''S-BAS-0043'' AND ooab002 <> ''NOT_DEFINED'' AND ooab002 IS NOT NULL\r\n --                                                             AND ooabent = inacent AND ooabsite = inacsite AND inac003 = ooab002\r\n --                     WHERE inacent = a.inaaent AND inacsite = a.inaasite AND inac001 = a.inaa001 )\r\n GROUP BY a.inaasite, site.ooefl003, a.inaa001, t2.inayl003, b.inab002,\r\n          b.inab003, c.inag001, t1.imaal003, t1.imaal004, c.inag002, \r\n          c.inag007, t3.oocal003, d.inaf007, d.inaf008,d.inaf006 \r\n ORDER BY $orderBy  a.inaasite,a.inaa001,b.inab002,c.inag001,c.inag002,c.inag007\r\n#end\r\n\r\nSELECT COUNT(1) as COUNT   \r\n  FROM ( #select() );\r\n\r\n\r\nSELECT site              "site",\r\n       sitename          "sitename",\r\n       warehouseNo       "warehouseNo",\r\n       warehouseName     "warehouseName",\r\n       storageSpacesNo   "storageSpacesNo",\r\n       storageSpacesName "storageSpacesName",\r\n       NVL(itemNo,'' '')     "itemNo",\r\n       NVL(itemName,'' '')   "itemName",\r\n       NVL(itemSpec,'' '')   "itemSpec",\r\n       NVL(itemFeatureNo,'' '')   "itemFeatureNo",\r\n       NVL(unitNo,'' '')          "unitNo",\r\n       NVL(unitName,'' '')        "unitName",\r\n       NVL(supplyPointQty,''0'')  "supplyPointQty",\r\n       NVL(supplyQty,''0'')       "supplyQty",\r\n       NVL(inventoryQty,''0'')    "inventoryQty",\r\n       NVL(neededSupplyQty,''0'') "neededSupplyQty",\r\n       NVL(safeQty,''0'') "safeQty"\r\n  FROM ( #select() )\r\n WHERE RANK >= $rnstart\r\n   AND RANK <= $rnend\r\n ORDER BY RANK'), STRINGDECODE('{\r\n    "std_data": {\r\n        "execution": {\r\n            "code": "$execution.code",\r\n            "sqlcode": "$execution.sqlcode",\r\n            "description": "$execution.description"\r\n        },\r\n        "parameter": {\r\n            "result": [\r\n                { \r\n                  "totolCount": $JSON.toJSONString($sqlData[0][0].COUNT),\r\n                  "listdata": $JSON.toJSONString($sqlData[1])\r\n                }\r\n            ]\r\n        }\r\n    }\r\n}'), '', '1', '2017-09-19 16:04:46', '2017-09-26 17:15:52', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('47', '1001', 'max_lot_no', STRINGDECODE('srmafud003批号查询'), STRINGDECODE('{\n    "bodyMap": {\n         "std_data": {\n            "parameter": {\n                "ent": "99",\n                "site": "DSCNJ",\n                "supplierNo": "",\n                "deliveryDate": "",\n                "itemNo": ""\n            }\n        }\n    }\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\n#set ($ent = $bodyparams.ent)\n#set ($site = $bodyparams.site)\n#if ($lang != '''')\n#set ($lang = ''zh_CN'') \n#end\n\n		SELECT MAX(srmafud003) as LOTNO\n		  FROM srmaf_t, srmae_t\n		 WHERE srmafent = srmaeent\n		   AND srmafsite = srmaesite\n		   AND srmaf001 = srmae001\n	 #if($StringUtils.isNotEmpty($bodyparams.supplierNo))\n		   AND srmae008 = ''$bodyparams.supplierNo''\n	 #end\n	 #if($StringUtils.isNotEmpty($bodyparams.deliveryDate))\n		   AND srmae002 = to_date(''$bodyparams.deliveryDate'',''yyyy-mm-dd hh24:mi:ss'')\n	 #end\n	 #if($StringUtils.isNotEmpty($bodyparams.itemNo))\n		   AND srmaf005 = ''$bodyparams.itemNo''\n	 #end'), STRINGDECODE('{\n    "std_data": {\n        "execution": {\n            "code": "$execution.code",\n            "sqlcode": "$execution.sqlcode",\n            "description": "$execution.description"\n        },\n        "parameter": {\n             "result":[{\n                "lotNo": $JSON.toJSONString($sqlData[0][0].LOTNO)\n         }]\n        }\n    }\n}'), '  ', '1', '2017-09-19 16:04:46', '2017-09-19 16:04:46', '0', 't100');
