INSERT INTO `iccn_ica_service` VALUES ('55', '1002', 'rate', STRINGDECODE('单位换算率'), STRINGDECODE('{\r\n    "std_data": {\r\n        "parameter": {\r\n            "appmodule": "C012",\r\n            "lang": "zh_CN",\r\n            "ent": "99.0",\r\n            "site": "DSCNJ",\r\n            "itemNo": "TESTMSN090602",\r\n            "unitNo": "M"\r\n        }\r\n    }\r\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\r\n#set ($ent = $bodyparams.ent) \r\n#set ($site = $bodyparams.site) \r\n#set ($lang = $bodyparams.lang)\r\n#set ($appmodule= $bodyparams.appmodule) \r\n#set ($itemNo= $bodyparams.itemNo)\r\n#set ($unitNo= $bodyparams.unitNo)\r\n\r\nSELECT decode("rate",1,(\r\n                        SELECT "rate" FROM(\r\n                        SELECT 1,nvl(oocc004/oocc002,1) as "rate" FROM oocc_t\r\n                        WHERE oocc001 =''$unitNo''\r\n                          AND oocc003 in(select imaf053 from imaf_t where imafent=''$ent''\r\n                          and imafsite=''$site'' and imaf001=''$itemNo'')\r\n                          AND ooccstus=''Y''\r\n                          AND ooccent =''$ent''\r\n                        union\r\n                        SELECT 2,1 as "rate" FROM dual)\r\n                        WHERE rownum=1\r\n                        ),"rate") as "rate" FROM(\r\nSELECT 1,nvl(imad005/imad003,1) as "rate" FROM imad_t\r\nWHERE imad001 =''$itemNo''\r\n  AND imad002 =''$unitNo''\r\n  AND imad004 in(select imaf053 from imaf_t where imafent=''$ent''\r\n  and imafsite=''$site'' and imaf001=''$itemNo'')\r\n  AND imadstus=''Y''\r\n  AND imadent =''$ent''\r\nunion\r\nSELECT 2,1 as "rate" FROM dual)\r\nWHERE rownum=1;\r\n\r\nselect ooca002 as "keep"\r\n  from ooca_t\r\n where oocaent = ''$ent''\r\n   and oocastus = ''Y''\r\n   and ooca001 = ''$unitNo'''), STRINGDECODE('{\r\n    "std_data": {\r\n        "execution": {\r\n            "code": "$execution.code",\r\n            "sqlcode": "$execution.sqlcode",\r\n            "description": "$execution.description"\r\n        },\r\n        "parameter": {\r\n            "result": [\r\n                {\r\n                  "rate":$JSON.toJSONString($sqlData[0][0].rate),\r\n                  "keep":$JSON.toJSONString($sqlData[1][0].keep)\r\n                }\r\n            ]\r\n        }\r\n    }\r\n}'), STRINGDECODE('单位换算'), '1', '2017-09-19 16:04:46', '2017-09-24 11:35:16', '0', 't100');
