INSERT INTO `iccn_ica_service` VALUES ('58', '1001', 'update_lot_no_single', STRINGDECODE('srmafud003批号更新'), STRINGDECODE('{\n	"bodyMap": {\n		"std_data": {\n			"parameter": {\n				"ent": "99",\n				"site": "DSCNJ",\n				"lotNo": "",\n				"deliveryNo": "",\n				"deliverySeq": ""\n			}\n		}\n	}\n\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\n\n		UPDATE srmaf_t\n		   SET srmafud003 = ''$bodyparams.lotNo''\n		 WHERE srmafent = ''$bodyparams.ent''\n		   AND srmafsite = ''$bodyparams.site''\n		   AND srmaf001 = ''$bodyparams.deliveryNo''\n    #if($StringUtils.isNotEmpty($bodyparams.deliverySeq))\n		   AND srmaf002 = ''$bodyparams.deliverySeq'';\n    #end		   \n'), STRINGDECODE('{\n    "std_data": {\n        "execution": {\n            "code": "$execution.code",\n            "sqlcode": "$execution.sqlcode",\n            "description": "$execution.description"\n        },\n        "parameter": {\n            "result": [\n                {\n                    "totolCount": $totolCount,\n                    "status": "$status"\n                }\n            ]\n        }\n    }\n}'), '  ', '1', '2017-09-19 16:04:46', '2017-09-19 16:04:46', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('60', '1001', 'supplier_list', STRINGDECODE('供应商列表'), STRINGDECODE('{\n    "bodyMap": {\n         "std_data": {\n            "parameter": {\n                "ent": "99",\n                "site": "DSCNJ",\n                "lang": "zh_CN",\n                "supplierNo": ""\n            }\n        }\n    }\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\n#set ($ent = $bodyparams.ent)\n#set ($site = $bodyparams.site)\n#if ($lang != '''')\n#set ($lang = ''zh_CN'') \n#end\n\nselect a.pmabsite "site",                                       /*据点*/\n       NVL(trim(site.ooefl003), a.pmabsite) "sitename",         /*据点名称*/\n       a.pmab001 "supplierNo",                                  /*供应商编号*/\n       NVL(trim(b.pmaal003), a.pmab001) "supplierName",         /*供应商全称*/\n       NVL(trim(b.pmaal004), a.pmab001) "supplierShortname"     /*供应商简称*/\n from pmab_t a\n inner join pmaa_t j on (a.pmabent = j.pmaaent and a.pmab001 = j.pmaa001)\n left join pmaal_t b on (a.pmab001 = b.pmaal001 and a.pmabent = b.pmaalent and b.pmaal002 = ''$lang'' )\n left join ooefl_t site ON site.ooeflent = a.pmabent AND site.ooefl001 = a.pmabsite AND site.ooefl002=''$lang''\nwhere a.pmabent = ''$ent'' \n #if($StringUtils.isNotEmpty($site))\n    and a.pmabsite = ''$site''  \n #end\n  and a.pmabsite = ''$site'' \n  and j.pmaastus = ''Y'' \n  and j.pmaa002 in (''1'',''3'')\n #if($StringUtils.isNotEmpty($bodyparams.supplierNo))\n    and a.pmab001 = ''$bodyparams.supplierNo''  \n #end'), STRINGDECODE('{\n    "std_data": {\n        "execution": {\n            "code": "$execution.code",\n            "sqlcode": "$execution.sqlcode",\n            "description": "$execution.description"\n        },\n        "parameter": {\n            "result": [\n                {\n                  "listdata": $JSON.toJSONString($sqlData[0])\n                }\n            ]\n        }\n    }\n}'), ' ', '1', '2017-09-19 16:04:46', '2017-09-19 16:04:46', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('77', '1002', 'qcListGet', STRINGDECODE('检验单清单列表'), STRINGDECODE('{\n    "std_data": {\n        "parameter": {\n            "ent": "99",\n            "site": "DSCNJ",\n            "lang": "zh_CN",\n            "appmodule": "A009",\n            "timestamp": "",\n            "deliveryNo": "",\n            "receiptNo": "",\n            "woNo": "",\n            "stockInNo": "",\n            "departmentNo": "",\n            "itemNo": "",\n            "supplierNo": "",\n            "pagesize": "5",\n            "dateBegin": "2015/01/01",\n            "dateEnd": "2017/12/31"\n        }\n    }\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\n#set ($ent = $bodyparams.ent) \n#set ($site = $bodyparams.site) \n#set ($lang = $bodyparams.lang) \n#set ($appmodule = $bodyparams.appmodule) \n#set ($timestamp = $bodyparams.timestamp)\n#set ($deliveryNo = $bodyparams.deliveryNo) \n#set ($woNo = $bodyparams.woNo) \n#set ($stockInNo = $bodyparams.stockInNo) \n#set ($departmentNo = $bodyparams.departmentNo) \n#set ($receiptNo = $bodyparams.receiptNo) \n#set ($itemNo = $bodyparams.itemNo) \n#set ($supplierNo = $bodyparams.supplierNo)\n#set ($dateBegin = $bodyparams.dateBegin) \n#set ($dateEnd = $bodyparams.dateEnd)\n#set ($pagesize = $bodyparams.pagesize) \n\n#if ($appmodule == ''A009'')  ##收货检验IQC\nselect pmds010 as "deliveryNo",qcba001 as "receiptNo",qcbadocno as "qcNo",pmds007 as "supplierNo",pmaal004 as "supplierName",qcba021 as "urgency",to_char(qcbadocdt,''yyyy/mm/dd'') as "createDate",\n to_char(pmdscrtdt,''yyyy/mm/dd'') as "receiptDate",qcba002 as "seq",qcba010 as "itemNo",imaal003 as "itemName",imaal004 as "itemSpec",qcba009 as "unitNo",qcba017 as "qty",\n floor(to_number(sysdate-to_date(to_char(pmdscrtdt,''yyyy-mm-dd hh24:mi:ss''),''yyyy-mm-dd hh24:mi:ss''))*24*60) as "waitMin" from( \n select rownum item, a.* from (\n select distinct pmds010,qcba001,qcbadocno,pmds007,pmaal004,qcba021,qcbadocdt,\n pmdscrtdt,qcba002,qcba010,imaal003,imaal004,qcba009,qcba017,\n floor(to_number(sysdate-to_date(to_char(pmdscrtdt,''yyyy-mm-dd hh24:mi:ss''),''yyyy-mm-dd hh24:mi:ss''))*24*60)\n from qcba_t\n inner join qcbd_t on qcbaent=qcbdent and qcbadocno=qcbddocno\n inner join pmds_t on qcbaent=pmdsent and qcba001=pmdsdocno\n left  join imaal_t on imaalent=qcbaent and imaal001=qcba010 and imaal002=''$lang''\n left  join pmaal_t on pmaalent=qcbaent and pmaal001=pmds007 and pmaal002=''$lang''\n where qcbaent=''$ent''\n and qcbasite=''$site''\n and qcbastus=''N''\n and qcba000=''1''\n and pmdsstus=''Y''\n#if ($deliveryNo != '''')   ##送货单号\n   and pmds010 like ''%$deliveryNo%''\n#end \n#if ($receiptNo != '''')   ##收货单号\n   and qcba001 like  ''%$receiptNo%''\n#end \n#if ($supplierNo != '''')   ##供应商编号\n   and pmds007 like  ''%$supplierNo%''\n#end \n#if ($itemNo != '''')   ##料号\n   and qcba010 like  ''%$itemNo%''\n#end \n#if ($dateBegin != '''')   ##开始日期\n   and qcbadocdt >= to_date(''$dateBegin'',''yyyy/mm/dd'')\n#end \n#if ($dateEnd != '''')   ##结束日期\n   and qcbadocdt <= to_date(''$dateEnd'',''yyyy/mm/dd'')\n#end \norder by qcbadocdt desc,qcba002) a)\nwhere item>0 and item<=''$pagesize''\n#end\n\n#if ($appmodule == ''B002'')   ##完工检验FQC\nselect qcba003 as "woNo",qcba001 as "stockInNo",qcbadocno as "qcNo",qcba901 as "departmentNo",ooefl003 as "departmentName",qcba021 as "urgency",to_char(qcbadocdt,''yyyy/mm/dd'') as "createDate",\n to_char(sfeacrtdt,''yyyy/mm/dd'') as "receiptDate",qcba002 as "seq",qcba010 as "itemNo",imaal003 as "itemName",imaal004 as "itemSpec",qcba009 as "unitNo",qcba017 as "qty",\n floor(to_number(sysdate-to_date(to_char(sfeacrtdt,''yyyy-mm-dd hh24:mi:ss''),''yyyy-mm-dd hh24:mi:ss''))*24*60) as "waitMin" from( \n select rownum item, a.* from (\n select distinct qcba003,qcba001,qcbadocno,qcba901,ooefl003,qcba021,qcbadocdt,\n sfeacrtdt,qcba002,qcba010,imaal003,imaal004,qcba009,qcba017,\n floor(to_number(sysdate-to_date(to_char(sfeacrtdt,''yyyy-mm-dd hh24:mi:ss''),''yyyy-mm-dd hh24:mi:ss''))*24*60)\n from qcba_t\n inner join qcbd_t on qcbaent=qcbdent and qcbadocno=qcbddocno\n inner join sfea_t on qcbaent=sfeaent and qcba001=sfeadocno\n left  join imaal_t on imaalent=qcbaent and imaal001=qcba010 and imaal002=''$lang''\n left  join ooefl_t on ooeflent=qcbaent and ooefl001=qcba901 and ooefl002=''$lang''\n where qcbaent=''$ent''\n and qcbasite=''$site''               \n and qcbastus=''N''\n and qcba000=''2''\n and sfeastus=''Y''\n#if ($woNo != '''')\n  and qcba003 like ''%$woNo%''\n#end\n#if ($stockInNo != '''')\n  and qcba001 like ''%$stockInNo%''\n#end\n#if ($departmentNo != '''')\n  and qcba901 like ''%$departmentNo%''\n#end\n#if ($itemNo != '''')\n  and qcba010 like ''%$itemNo%''\n#end\n#if ($dateBegin != '''')\n  and qcbadocdt >= to_date(''$dateBegin'',''yyyy/mm/dd'')\n#end\n#if ($dateEnd != '''')\n  and qcbadocdt <= to_date(''$dateEnd'',''yyyy/mm/dd'')\n#end\norder by qcbadocdt desc,qcba002) a)\nwhere item>0 and item<=''$pagesize''\n#end'), STRINGDECODE('{\n    "std_data": {\n        "execution": {\n            "code": "$execution.code",\n            "sqlcode": "$execution.sqlcode",\n            "description": "$execution.description"\n        },\n        "parameter": {\n            "result": [\n                {\n                  "listdata": $JSON.toJSONString($sqlData[0])\n                }\n            ]\n        }\n    }\n}'), STRINGDECODE('获取检验单清单列表资料'), '1', '2017-09-19 16:04:46', '2017-09-19 16:04:46', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('84', '1002', 't100_als_c008_list_get', STRINGDECODE('杂收'), STRINGDECODE('\n{\n    "std_data": {\n        "parameter": {\n            "ent": "99",\n            "site": "DSCNJ",\n            "lang": "zh_CN",\n            "appmodule": "C008",\n            "docNo": "CNJ-P71-170900000028",\n            "employeeNo":"",\n            "departmentNo":"",\n            "pagesize":"10",\n            "dateBegin": "2016/01/01",\n            "dateEnd": "2017/12/31",\n            "warehouseNo": ""\n        }\n    }\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\n#set ($ent = $bodyparams.ent) \n#set ($site = $bodyparams.site) \n#set ($lang = $bodyparams.lang) \n#set ($appmodule = $bodyparams.appmodule) \n#set ($docNo = $bodyparams.docNo) \n#set ($employeeNo= $bodyparams.employeeNo)\n#set ($departmentNo= $bodyparams.departmentNo)\n#set ($warehouseNo= $bodyparams.warehouseNo)\n#set ($pagesize = $bodyparams.pagesize) \n#set ($dateBegin = $bodyparams.dateBegin) \n#set ($dateEnd = $bodyparams.dateEnd)\n\nselect inbadocno,inbadocdt,inba004,ooefl003,inba003,ooag011 from (                \n              select rownum item,inbadocno,inbadocdt,inba004,ooefl003,inba003,ooag011 from (\n              select distinct inbadocno,inbadocdt,inba004,ooefl003,inba003,ooag011\n              from inba_t\n              inner join inbb_t on inbbdocno=inbadocno and inbbent=inbaent            \n              left join ooefl_t on ooeflent=inbaent and ooefl001=inba004 and ooefl002= ''$lang''     \n              left join ooag_t on ooagent=inbaent and ooagstus=''Y'' and ooag001=inba003\n              where inbaent=''$ent''\n              and inbasite=''$site''\n              and inba001 in(''2'',''4'')\n              and inbastus=''Y''\n              and inbb019>0\n\n\n#if ($docNo != '''')                \n    AND inbadocno like ''%$docNo%''\n#end\n\n\n#if ($employeeNo != '''')                \n    AND inba003 like ''%$employeeNo%''\n#end\n\n#if ($departmentNo != '''')                \n    AND inba004 like ''%$departmentNo%''\n#end\n\n#if ($dateBegin != '''')                \n    AND inbadocdt  >= to_date(''$dateBegin'',''yyyy/mm/dd'')\n#end\n\n#if ($dateEnd != '''')                \n    AND inbadocdt  <= to_date(''$dateEnd'',''yyyy/mm/dd'')\n#end\n\norder by inbadocdt desc,inbadocno))\n                      where item <= ''$pagesize'''), STRINGDECODE(' "std_data": {\n        "execution": {\n            "code": "$execution.code",\n            "sqlcode": "$execution.sqlcode",\n            "description": "$execution.description"\n        },\n        "parameter": {\n            "result": [\n                {\n                  "listdata": $JSON.toJSONString($sqlData[0])\n                }\n            ]\n        }\n    }\n}'), STRINGDECODE('杂收列表清单'), '1', '2017-09-19 16:04:46', '2017-09-19 16:04:46', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('85', '1004', 'ListGet_A', '11', STRINGDECODE('{\n    "std_data": {\n        "parameter": {\n            "ent": "99",\n            "site": "DSCNJ",\n            "lang": "zh_CN",\n            "appmodule": "A001",\n            "docNo": "",\n            "itemNo": "",\n            "itemName": "",\n            "supplierNo": "",\n            "customerNo": "",\n            "employeeNo": "",\n            "departmentNo": "",\n            "warehouseNo": "",\n            "pagesize": "5",\n            "dateBegin": "2016/01/01",\n            "dateEnd": "2017/12/31"\n        }\n    }\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\n#set ($ent = $bodyparams.ent) \n#set ($site = $bodyparams.site) \n#set ($lang = $bodyparams.lang) \n#set ($appmodule = $bodyparams.appmodule) \n#set ($docNo = $bodyparams.docNo) \n#set ($itemNo = $bodyparams.itemNo) \n#set ($itemName = $bodyparams.itemName)\n#set ($supplierNo = $bodyparams.supplierNo) \n#set ($customerNo= $bodyparams.customerNo)\n#set ($employeeNo= $bodyparams.employeeNo)\n#set ($departmentNo= $bodyparams.departmentNo)\n#set ($warehouseNo= $bodyparams.warehouseNo)\n#set ($pagesize = $bodyparams.pagesize) \n#set ($dateBegin = $bodyparams.dateBegin) \n#set ($dateEnd = $bodyparams.dateEnd) \n\n#if ($appmodule ==''C008'')   ##杂收\n SELECT DISTINCT TOP ''$pagesize''  TA001,TA002, rtrim(TA001)+''-''+rtrim(TA002) AS TA0012, TA003,TA004,ME002,TA032,MV002,TA014  \n FROM INVTA INVTA  \n INNER JOIN INVTB INVTB ON TB001=TA001 AND TB002=TA002  \n LEFT JOIN  CMSME CMSME ON TA004=ME001  \n LEFT JOIN  CMSMV CMSMV ON TA032=MV001  \n LEFT JOIN  CMSMQ CMSMQ ON TA001=MQ001  \n WHERE  TA006=''N'' AND TA009 =''11''   \n       AND TA008 =''$site''   \n       AND MQ010 = 1\n #if ($doc_no != '''')       ##一般交易单号\n    and TRIM(TA001)+''-''+TRIM(TA002) like ''%$doc_no%''\n #end         \n #if ($date_begin != '''')   ##起始日期 \n    AND TA003 >= ''$date_begin''\n #end\n #if ($dateEnd != '''')      ##结束日期\n   AND TA003 <= ''$dateEnd''\n #end\n #if ($employeeNo != '''')   ##员工编号\n    and TA032 like ''%$employeeNo%''\n #end\n #if ($departmentNo != '''') ##部门编号\n    and TA004 like ''%$departmentNo%''\n #end        \n #if ($warehouse_no != '''') ##仓库编号\n    and TB012 = ''%$warehouse_no%''\n #end  \n#end'), STRINGDECODE('{\n    "std_data": {\n        "execution": {\n            "code": "$execution.code",\n            "sqlcode": "$execution.sqlcode",\n            "description": "$execution.description"\n        },\n        "parameter": {\n            "result": [\n                {\n                  "listdata": $JSON.toJSONString($sqlData[0])\n                }\n            ]\n        }\n    }\n}'), 'zashou ', '1', '2017-09-19 16:04:46', '2017-09-19 16:04:46', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('86', '1002', 'qcItemGet', STRINGDECODE('检验项目清单列表'), STRINGDECODE('{\r\n    "std_data": {\r\n        "parameter": {\r\n            "ent": "99",\r\n            "site": "DSCNJ",\r\n            "lang": "zh_CN",\r\n            "appmodule": "A001",\r\n            "timestamp": "",\r\n            "qcNo": "CNJ-FQC-170900000011"\r\n        }\r\n    }\r\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\r\n#set ($ent = $bodyparams.ent) \r\n#set ($site = $bodyparams.site) \r\n#set ($lang = $bodyparams.lang) \r\n#set ($appmodule = $bodyparams.appmodule) \r\n#set ($timestamp = $bodyparams.timestamp)\r\n#set ($qcNo = $bodyparams.qcNo) \r\n\r\n SELECT qcbddocno as "qcNo",qcbdseq as "seq",qcbd001 as "inspectionItem",oocql004 as "inspectionItemName",\r\n        qcbd002 as "inspectionPosition",qcbd003 as "defectGrade",\r\n        CASE qcbd003 WHEN ''1'' THEN ''Critical'' WHEN ''2'' THEN ''Major'' WHEN ''3'' THEN ''Minor'' end as "defectGradeName",\r\n        qcbd004 as "aql",qcbd005 as "acQty",qcbd006 as "reQty",qcbd007 as "kStandard",qcbd008 as "fStandard",qcbd009 as "sampleQty",\r\n        qcbd010 as "defectQty",qcbd011 as "qcResult",qcbd012 as "usl",qcbd013 as "upperRange",qcbd014 as "standardValue",\r\n        qcbd015 as "lowerRange",qcbd016 as "slb",qcbd017 as "unitNo",qcbd018 as "inspectionSpec",qcbd019 as "remark",\r\n        qcbd020 as "samplingPlan",qcbd021 as "defectReasonQty"\r\n FROM qcbd_t\r\n INNER JOIN qcba_t ON qcbdent=qcbaent  AND qcbadocno=qcbddocno\r\n LEFT JOIN oocql_t ON qcbdent=oocqlent AND oocql001=''1051'' AND qcbd001=oocql002 AND oocql003=''$lang''\r\n WHERE qcbddocno=''$qcNo''\r\n   AND qcbdent=''$ent''\r\n   AND qcba000 in (''1'',''2'')\r\n   AND qcbastus=''N'''), STRINGDECODE('{\r\n    "std_data": {\r\n        "execution": {\r\n            "code": "$execution.code",\r\n            "sqlcode": "$execution.sqlcode",\r\n            "description": "$execution.description"\r\n        },\r\n        "parameter": {\r\n            "result": [\r\n                {\r\n                  "listdata": $JSON.toJSONString($sqlData[0])\r\n                }\r\n            ]\r\n        }\r\n    }\r\n}'), STRINGDECODE('获取检验项目清单列表'), '1', '2017-09-19 16:04:46', '2017-09-30 17:43:37', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('87', '1003', 'E10als_c008_list_get', STRINGDECODE('杂收列表清单'), STRINGDECODE('{\r\n    "std_data": {\r\n        "parameter": {\r\n            "ent": "99",\r\n            "site": "HJ01",\r\n            "lang": "zh_CN",\r\n            "appmodule": "C008",\r\n            "docNo": "",\r\n            "itemNo": "",\r\n            "itemName": "",\r\n            "supplierNo": "",\r\n            "customerNo": "",\r\n            "employeeNo": "",\r\n            "departmentNo": "",\r\n            "warehouseNo": "",\r\n            "pagesize": "10",\r\n            "dateBegin": "2016/01/01",\r\n            "dateEnd": "2017/12/31"\r\n        }\r\n    }\r\n}\r\n'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\r\n#set ($ent = $bodyparams.ent) \r\n#set ($site = $bodyparams.site) \r\n#set ($lang = $bodyparams.lang) \r\n#set ($appmodule = $bodyparams.appmodule) \r\n#set ($docNo = $bodyparams.docNo) \r\n#set ($itemNo = $bodyparams.itemNo) \r\n#set ($itemName = $bodyparams.itemName)\r\n#set ($supplierNo = $bodyparams.supplierNo) \r\n#set ($customerNo= $bodyparams.customerNo)\r\n#set ($employeeNo= $bodyparams.employeeNo)\r\n#set ($departmentNo= $bodyparams.departmentNo)\r\n#set ($warehouseNo= $bodyparams.warehouseNo)\r\n#set ($pagesize = $bodyparams.pagesize) \r\n#set ($dateBegin = $bodyparams.dateBegin) \r\n#set ($dateEnd = $bodyparams.dateEnd)\r\n\r\nSELECT   DISTINCT TOP    $pagesize\r\n TRANSACTION_DOC.DOC_NO AS doc_no,\r\n                         TRANSACTION_DOC.DOC_DATE AS create_date,\r\n                         ISNULL(ADMIN_UNIT.ADMIN_UNIT_CODE, N'' '') AS department_no,\r\n                         ISNULL(ADMIN_UNIT.ADMIN_UNIT_NAME, N'' '') AS department_name,\r\n                         ISNULL(EMPLOYEE.EMPLOYEE_CODE, N'' '') AS employee_no,\r\n                         ISNULL(EMPLOYEE.EMPLOYEE_NAME, N'' '') AS employee_name\r\nFROM     TRANSACTION_DOC AS TRANSACTION_DOC\r\n         INNER JOIN\r\n         PLANT AS PLANT\r\n         ON PLANT.PLANT_ID = TRANSACTION_DOC.Owner_Org_ROid\r\n         INNER JOIN\r\n         TRANSACTION_DOC_D AS TRANSACTION_DOC_D\r\n         ON TRANSACTION_DOC_D.TRANSACTION_DOC_ID = TRANSACTION_DOC.TRANSACTION_DOC_ID\r\n         LEFT OUTER JOIN\r\n         ADMIN_UNIT AS ADMIN_UNIT\r\n         ON ADMIN_UNIT.ADMIN_UNIT_ID = TRANSACTION_DOC.Owner_Dept\r\n         LEFT OUTER JOIN\r\n         EMPLOYEE AS EMPLOYEE\r\n         ON EMPLOYEE.EMPLOYEE_ID = TRANSACTION_DOC.Owner_Emp\r\n         LEFT OUTER JOIN\r\n         WAREHOUSE AS WAREHOUSE\r\n         ON WAREHOUSE.WAREHOUSE_ID = TRANSACTION_DOC_D.WAREHOUSE_ID\r\nWHERE      PLANT.PLANT_CODE = $site  and\r\n           TRANSACTION_DOC.ApproveStatus = ''N''  and\r\n           TRANSACTION_DOC.STOCK_ACTION = 1  and\r\n           TRANSACTION_DOC. CATEGORY = ''11'' and\r\n                   \r\n           #if ($docNo != '''')            \r\n             AND TRANSACTION_DOC.DOC_NO like ''%$docNo%''\r\n           #end \r\n           #if ($dateBegin != '''')\r\n             AND TRANSACTION_DOC.DOC_DATE >=to_date(''$dateBegin'',''yyyy/mm/dd'')\r\n           #end  \r\n           #if ($dateEnd != '''')\r\n             AND TRANSACTION_DOC.DOC_DATE <=to_date(''$dateEnd'',''yyyy/mm/dd'')\r\n           #end    \r\n           #if ($employeeNo != '''')   \r\n    AND EMPLOYEE.EMPLOYEE_CODE LIKE ''%$employeeNo%''\r\n #end\r\n #if ($departmentNo != '''')  \r\n    AND ADMIN_UNIT.ADMIN_UNIT_CODE LIKE ''%$departmentNo%''\r\n #end\r\n#if ($warehouseNo != '''')  \r\n    AND WAREHOUSE.WAREHOUSE_CODE =$warehouseNo \r\n #end\r\nORDER BY TRANSACTION_DOC.DOC_DATE DESC, TRANSACTION_DOC.DOC_NO ASC ;\r\n'), STRINGDECODE('{\r\n    "std_data": {\r\n        "execution": {\r\n            "code": "$execution.code",\r\n            "sqlcode": "$execution.sqlcode",\r\n            "description": "$execution.description"\r\n        },\r\n        "parameter": {\r\n            "result": [\r\n                {\r\n                  "listdata": $JSON.toJSONString($sqlData[0])\r\n                }\r\n            ]\r\n        }\r\n    }\r\n}'), STRINGDECODE('杂收列表清单'), '1', '2017-09-19 16:04:46', '2017-09-19 16:04:46', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('88', '1002', 'qcReasonGet', STRINGDECODE('不良原因列表'), STRINGDECODE('{\r\n    "std_data": {\r\n        "parameter": {\r\n            "ent": "99",\r\n            "site": "DSCNJ",\r\n            "lang": "zh_CN",\r\n            "appmodule": "B002",\r\n            "timestamp": "8673680230632513c:a3:48:4e:db:eaf6d3a34fbee13e4B002171009105933",\r\n            "qcNo": "CNJ-FQC-171000000001",\r\n            "seq": "1.0",\r\n            "defectReasonZm": ""\r\n        }\r\n    }\r\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\r\n#set ($ent = $bodyparams.ent) \r\n#set ($site = $bodyparams.site) \r\n#set ($lang = $bodyparams.lang) \r\n#set ($appmodule = $bodyparams.appmodule) \r\n#set ($timestamp = $bodyparams.timestamp)\r\n#set ($qcNo = $bodyparams.qcNo) \r\n#set ($seq = $bodyparams.seq)\r\n\r\nSELECT qcbedocno as "docNo",qcbeseq as "seq",qcbe001 as "defectReason",oocql004 as "defectReasonName",qcbe002 as "qty",qcbe003 as "remark",qcbe004 as "defectReasonQty"\r\n FROM qcbe_t\r\n INNER JOIN qcba_t ON qcbaent=qcbeent AND qcbadocno=qcbedocno\r\n INNER JOIN qcbd_t ON qcbdent=qcbeent AND qcbddocno=qcbedocno AND qcbdseq=qcbeseq\r\n LEFT JOIN oocql_t ON qcbeent=oocqlent AND oocql001=''1053'' AND qcbe001=oocql002 AND oocql003=''$lang''\r\nWHERE qcbedocno=''$qcNo''\r\n  AND qcbeseq  =''$seq''\r\n  AND qcbeent  =''$ent''\r\n  AND (qcba000  =''1'' OR qcba000 = ''2'')\r\n  AND qcbastus =''N'''), STRINGDECODE('{\r\n    "std_data": {\r\n        "execution": {\r\n            "code": "$execution.code",\r\n            "sqlcode": "$execution.sqlcode",\r\n            "description": "$execution.description"\r\n        },\r\n        "parameter": {\r\n            "result": [\r\n                {\r\n                  "listdata": $JSON.toJSONString($sqlData[0])\r\n                }\r\n            ]\r\n        }\r\n    }\r\n}'), STRINGDECODE('获取不良原因列表'), '1', '2017-09-19 16:04:46', '2017-10-09 11:07:28', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('89', '1002', 'qcInitialReasonGet', STRINGDECODE('不良原因(首字母筛选)'), STRINGDECODE('{\n    "std_data": {\n        "parameter": {\n            "ent": "99",\n            "site": "DSCNJ",\n            "lang": "zh_CN",\n            "appmodule": "A001",\n            "timestamp": "",\n            "defectReasonZm": "t"\n        }\n    }\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\n#set ($ent = $bodyparams.ent) \n#set ($site = $bodyparams.site) \n#set ($lang = $bodyparams.lang) \n#set ($appmodule = $bodyparams.appmodule) \n#set ($timestamp = $bodyparams.timestamp)\n#set ($defectReasonZm = $bodyparams.defectReasonZm)\n\nSELECT oocql002 as "defectReason",oocql004 as "defectReasonName"\nFROM oocql_t\nWHERE oocqlent= ''$ent''\n  AND oocql001=''1053''\n  AND oocql003=''$lang''\n  AND (oocql004 LIKE ''%$defectReasonZm%''\n  OR oocql005 LIKE ''%$defectReasonZm%'')\nORDER BY oocql002'), STRINGDECODE('{\n    "std_data": {\n        "execution": {\n            "code": "$execution.code",\n            "sqlcode": "$execution.sqlcode",\n            "description": "$execution.description"\n        },\n        "parameter": {\n            "result": [\n                {\n                  "listdata": $JSON.toJSONString($sqlData[0])\n                }\n            ]\n        }\n    }\n}'), STRINGDECODE('首字母筛选不良原因'), '1', '2017-09-19 16:04:46', '2017-09-19 16:04:46', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('90', '1002', 'qcMeasureGet', STRINGDECODE('获取测量数据'), STRINGDECODE('{\r\n    "std_data": {\r\n        "parameter": {\r\n            "ent": "99",\r\n            "site": "DSCNJ",\r\n            "lang": "zh_CN",\r\n            "appmodule": "A001",\r\n            "timestamp": "",\r\n            "qcNo": "CNJ-P04-201501230002",\r\n            "seq": ""\r\n        }\r\n    }\r\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\r\n#set ($ent = $bodyparams.ent) \r\n#set ($site = $bodyparams.site) \r\n#set ($lang = $bodyparams.lang) \r\n#set ($appmodule = $bodyparams.appmodule) \r\n#set ($timestamp = $bodyparams.timestamp)\r\n#set ($qcNo = $bodyparams.qcNo)\r\n#set ($seq = $bodyparams.seq)\r\n\r\nSELECT qcbgdocno as "docNo",qcbgseq as "seq",qcbg001 as "orderSeq",qcbg002 as "measureValue",qcbg003 as "resultType"\r\n FROM qcbg_t\r\n INNER JOIN qcba_t ON qcbaent=qcbgent AND qcbadocno=qcbgdocno\r\n INNER JOIN qcbd_t ON qcbdent=qcbgent AND qcbddocno=qcbgdocno AND qcbdseq=qcbgseq\r\nWHERE qcbgdocno=''$qcNo''\r\n  AND qcbgseq  =''$seq''\r\n  AND qcbgent  =''$ent''\r\n  AND (qcba000  =''1'' OR qcba000 = ''2'')\r\n  AND qcbastus =''N'''), STRINGDECODE('{\r\n    "std_data": {\r\n        "execution": {\r\n            "code": "$execution.code",\r\n            "sqlcode": "$execution.sqlcode",\r\n            "description": "$execution.description"\r\n        },\r\n        "parameter": {\r\n            "result": [\r\n                {\r\n                  "listdata": $JSON.toJSONString($sqlData[0])\r\n                }\r\n            ]\r\n        }\r\n    }\r\n}'), STRINGDECODE('获取测量数据'), '1', '2017-09-19 16:04:46', '2017-10-09 11:08:31', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('91', '1002', 'entSite', STRINGDECODE('获取集团据点'), STRINGDECODE('{\n    "std_data": {\n        "parameter": {\n            "ent": "99",\n            "site": "DSCNJ",\n            "lang": "zh_CN",\n            "appmodule": "A009",\n            "timestamp": "",\n            "appid": "",\n            "appversion": "",\n            "employeeNo": "00841"\n        }\n    }\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\n#set ($ent = $bodyparams.ent) \n#set ($site = $bodyparams.site) \n#set ($lang = $bodyparams.lang) \n#set ($appmodule = $bodyparams.appmodule) \n#set ($timestamp = $bodyparams.timestamp)\n#set ($appid = $bodyparams.appid)\n#set ($appversion = $bodyparams.appversion)\n#set ($employeeNo = $bodyparams.employeeNo)\n\nSELECT DISTINCT gzxaent as "ent", COALESCE(( SELECT gzoul003 \n                    FROM gzoul_t\n                   WHERE gzoul001 = gzxaent\n                     AND gzoul002 = ''$lang''), TO_CHAR(gzxaent)) as "entName", gzxc004 as "site",ooefl003 as "siteName"\n  FROM gzxa_t\n  JOIN gzou_t ON gzxaent = gzou001 AND gzoustus = ''Y''\n  LEFT OUTER JOIN gzxc_t ON gzxaent = gzxcent AND gzxa003 = gzxc001 AND gzxcstus = ''Y''\n  LEFT OUTER JOIN ooefl_t ON gzxaent=ooeflent and ooefl001=gzxc004 and ooefl002=''$lang''\n WHERE gzxa001 = ''$employeeNo'''), STRINGDECODE('{\n    "std_data": {\n        "execution": {\n            "code": "$execution.code",\n            "sqlcode": "$execution.sqlcode",\n            "description": "$execution.description"\n        },\n        "parameter": {\n            "result": [\n                {\n                  "listdata": $JSON.toJSONString($sqlData[0])\n                }\n            ]\n        }\n    }\n}'), STRINGDECODE('获取集团据点'), '1', '2017-09-19 16:04:46', '2017-09-19 16:04:46', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('92', '1002', 'b003ListDetailGet', STRINGDECODE('工单发料汇总清单'), STRINGDECODE('{\r\n    "std_data": {\r\n        "parameter": {\r\n            "woNo": "CNJ-P10-201709000140",\r\n            "warehouseNo": "Z1W1",\r\n            "ent": "99",\r\n            "site": "DSCNJ",\r\n            "lang": "zh_CN",\r\n            "appmodule": "B003",\r\n            "timestamp": "8673680230632513c:a3:48:4e:db:eaf6d3a34fbee13e4B003170928092814"\r\n        }\r\n    }\r\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\r\n#set ($ent = $bodyparams.ent) \r\n#set ($site = $bodyparams.site) \r\n#set ($lang = $bodyparams.lang) \r\n#set ($appmodule = $bodyparams.appmodule) \r\n#set ($timestamp = $bodyparams.timestamp)\r\n#set ($woNo = $bodyparams.woNo) \r\n#set ($warehouseNo = $bodyparams.warehouseNo)\r\n\r\nselect sfaadocno as "docNo",\r\nimaal003a as "itemName",\r\nimaal004a as "itemSpec",\r\nsfba006 as "lowOrderItemNo",\r\nimaal003b as "lowOrderItemName",\r\nimaal004b as "lowOrderItemSpec",\r\nnvl(sfba021,'' '') as "productCode",\r\nnvl(sfba030,'' '') as "stockCode",\r\nnvl(sfba019,'' '') as "warehouseNo",\r\nnvl(sfba020,'' '') as "storageSpacesNo",\r\nnvl(sfba029,'' '') as "lotNo",\r\nsfba014 as "unitNo",\r\nsfba016c as "applyQty",\r\nnvl(e.inag009c,0) as "stockQty",\r\n0 as "scanSumQty"\r\n from\r\n (select sfaaent,sfaasite,sfaadocno,a.imaal001 imaal001a,a.imaal003 imaal003a,a.imaal004 imaal004a,sfba006,b.imaal003 imaal003b,b.imaal004 imaal004b,sfba021,sfba030,sfba014,sfba019,sfba020,sfba029,sum(sfba013-sfba016) sfba016c   from sfaa_t \r\n inner join sfba_t on sfbaent=sfaaent and sfbasite=sfaasite and sfaadocno=sfbadocno\r\n left join imaal_t a on sfaa010=a.imaal001 and sfaaent=a.imaalent and a.imaal002=''$lang''\r\n left join imaal_t b on sfba006=b.imaal001 and sfaaent=b.imaalent and b.imaal002=''$lang''\r\n where sfaastus IN (''A'',''Y'',''F'') and sfaa003=''1'' and sfba013 > sfba016\r\n and sfaadocno=''$woNo''\r\n and sfaa004=''1''\r\n and sfba009=''N''\r\n and sfaaent=''$ent''\r\n and sfaasite=''$site''\r\n group by sfaaent,sfaasite,sfaadocno,a.imaal001,a.imaal003,a.imaal004,sfba006,b.imaal003,b.imaal004,sfba021,sfba030,sfba014,sfba019,sfba020,sfba029) d\r\n left join(\r\n                   select inagent,inagsite,inag001,sum(inag009) inag009c from inag_t              \r\n                   where inag004=''$warehouseNo''   \r\n                   and inag009>0\r\n                   group by inagent,inagsite,inag001\r\n                   ) e on sfba006=e.inag001 and d.sfaaent=e.inagent and d.sfaasite=e.inagsite     \r\norder by sfba006'), STRINGDECODE('{\r\n    "std_data": {\r\n        "execution": {\r\n            "code": "$execution.code",\r\n            "sqlcode": "$execution.sqlcode",\r\n            "description": "$execution.description"\r\n        },\r\n        "parameter": {\r\n            "result": [\r\n                {\r\n                  "listdata": $JSON.toJSONString($sqlData[0])\r\n                }\r\n            ]\r\n        }\r\n    }\r\n}'), STRINGDECODE('工单发料汇总清单'), '1', '2017-09-19 16:04:46', '2017-09-28 16:02:18', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('93', '1002', 'b001ListDetailGet', STRINGDECODE('依成品调拨汇总清单'), STRINGDECODE('{\r\n    "std_data": {\r\n        "parameter": {\r\n            "itemNo": "TESTMSN1007",\r\n            "warehouseOutNo": "Z1W1",\r\n            "warehouseInNo": "CCT1001",\r\n            "departmentNo": "",\r\n            "flag": "0",\r\n            "ent": "99",\r\n            "site": "DSCNJ",\r\n            "lang": "zh_CN",\r\n            "appmodule": "B001",\r\n            "timestamp": "8673680230632513c:a3:48:4e:db:eaf6d3a34fbee13e4B001171012161753"\r\n        }\r\n    }\r\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\r\n#set ($ent = $bodyparams.ent) \r\n#set ($site = $bodyparams.site) \r\n#set ($lang = $bodyparams.lang) \r\n#set ($appmodule = $bodyparams.appmodule) \r\n#set ($timestamp = $bodyparams.timestamp)\r\n#set ($itemNo = $bodyparams.itemNo) \r\n#set ($warehouseInNo = $bodyparams.warehouseInNo)\r\n#set ($warehouseOutNo = $bodyparams.warehouseOutNo)\r\n#set ($departmentNo = $bodyparams.departmentNo)\r\n\r\nselect ima01a as "itemNo",\r\n       ima02a as "itemName",\r\n       ima021a as "itemSpec",\r\n       nvl(sfaa017, '' '') as "departmentNo",\r\n       nvl(ooefl003, '' '') as "departmentName",\r\n       sfba014 as "unitNo",\r\n       sfba005 as "lowOrderItemNo",\r\n       ima02b as "lowOrderItemName",\r\n       ima021b as "lowOrderItemSpec",\r\n       nvl(sfba021, '' '') as "productCode",\r\n       nvl(sfba030, '' '') as "stockCode",\r\n       nvl(sfba019, '' '') as "warehouseNo",\r\n       nvl(sfba020, '' '') as "storageSpacesNo",\r\n       nvl(sfba029, '' '') as "lotNo",\r\n       sfba014 as "lowOrderItemUnitNo",\r\n       sfa06c as "applyQty",\r\n       0 as "scanSumQty",\r\n       nvl(sum,0) as "stockQty",\r\n       nvl(img10d,0) as "wStockQty"\r\n  FROM (SELECT a.imaa001 ima01a,\r\n               h.imaal003 ima02a,\r\n               h.imaal004 ima021a,\r\n               sfaa013,\r\n               sfaa017,\r\n               t.ooefl003 ooefl003,\r\n               sfba005,\r\n               i.imaal003 ima02b,\r\n               i.imaal004 ima021b,\r\n               sfba021,\r\n               sfba030,\r\n               sfba014,\r\n               sfba019,\r\n               sfba020,\r\n               sfba029,\r\n               sum(a.inag008) as sum,\r\n               sum(sfba013 - sfba016) sfa06c\r\n          FROM sfaa_t\r\n         INNER JOIN imaa_t a\r\n            ON a.imaa001 = sfaa010\r\n           AND sfaaent = imaaent\r\n          LEFT JOIN imaal_t h\r\n            ON h.imaal001 = a.imaa001\r\n           AND h.imaalent = a.imaaent\r\n           and h.imaal002 = ''$lang''\r\n          LEFT JOIN ooefl_t t\r\n            on t.ooefl001 = sfaa017\r\n           and t.ooeflent = sfaaent\r\n           and t.ooefl002 = ''$lang''\r\n         INNER JOIN sfba_t\r\n            ON sfbadocno = sfaadocno\r\n           AND sfbaent = sfaaent\r\n           AND sfbasite = sfaasite\r\n         INNER JOIN imaa_t b\r\n            ON b.imaa001 = sfba005\r\n           AND b.imaaent = sfbaent\r\n          LEFT JOIN imaal_t i\r\n            ON i.imaal001 = b.imaa001\r\n           AND i.imaalent = b.imaaent\r\n           and i.imaal002 = ''$lang''\r\n         left join inag_t a on a.inagent=sfbaent \r\n         and a.inagsite=sfbasite \r\n         and a.inag001=sfba005\r\n         and (nvl(a.inag004,'' '')=nvl(sfba019,'' '') or nvl(sfba019,'' '') = '' '' and a.inag004=''$warehouseOutNo'')\r\n         and (nvl(a.inag005,'' '')=nvl(sfba020,'' '') or nvl(sfba020,'' '') = '' '')\r\n         and (nvl(a.inag006,'' '')=nvl(sfba029,'' '') or nvl(sfba029,'' '') = '' '')\r\n         and (nvl(a.inag002,'' '')=nvl(sfba021,'' '') or nvl(sfba021,'' '') = '' '')\r\n         and (nvl(a.inag003,'' '')=nvl(sfba030,'' '') or nvl(sfba030,'' '') = '' '')\r\n         WHERE sfaa003 = ''1''\r\n           and sfaastus IN (''A'', ''F'', ''Y'')\r\n           AND sfba013 > sfba016\r\n           AND sfaa010 = ''$itemNo''\r\n         group BY a.imaa001,\r\n                  h.imaal003,\r\n                  h.imaal004,\r\n                  sfaa013,\r\n                  sfaa017,\r\n                  ooefl003,\r\n                  sfba005,\r\n                  i.imaal003,\r\n                  i.imaal004,\r\n                  sfba021,\r\n                  sfba030,\r\n                  sfba014,\r\n                  sfba019,\r\n                  sfba020,\r\n                  sfba029) d\r\n  LEFT join(\r\n  SELECT inag001,inag004,sum(inag009) img10d FROM inag_t              \r\n  WHERE inag009>0 and inagent=''$ent'' and inagsite=''$site'' \r\n  GROUP BY inag001,inag004\r\n  ) g ON sfba005=g.inag001 AND g.inag004=''$warehouseInNo'''), STRINGDECODE('{\r\n    "std_data": {\r\n        "execution": {\r\n            "code": "$execution.code",\r\n            "sqlcode": "$execution.sqlcode",\r\n            "description": "$execution.description"\r\n        },\r\n        "parameter": {\r\n            "result": [\r\n                {\r\n                  "listdata": $JSON.toJSONString($sqlData[0])\r\n                }\r\n            ]\r\n        }\r\n    }\r\n}'), STRINGDECODE('依成品调拨汇总清单'), '1', '2017-09-19 16:04:46', '2017-10-13 16:56:49', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('94', '1002', 'b014ListDetailGet', STRINGDECODE('依成品发料汇总清单'), STRINGDECODE('{\r\n    "std_data": {\r\n        "parameter": {\r\n            "itemNo": "TESTMSN0928-8",\r\n            "warehouseNo": "Z1W1",\r\n            "flag": "0",\r\n            "ent": "99",\r\n            "site": "DSCNJ",\r\n            "lang": "zh_CN",\r\n            "appmodule": "B014",\r\n            "timestamp": "8673680230632513c:a3:48:4e:db:eaf6d3a34fbee13e4B014171014104402"\r\n        }\r\n    }\r\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\r\n#set ($ent = $bodyparams.ent) \r\n#set ($site = $bodyparams.site) \r\n#set ($lang = $bodyparams.lang) \r\n#set ($appmodule = $bodyparams.appmodule) \r\n#set ($timestamp = $bodyparams.timestamp)\r\n#set ($itemNo = $bodyparams.itemNo) \r\n#set ($warehouseNo = $bodyparams.warehouseNo)\r\n\r\nSELECT  imaal003a as "itemName",imaal004a as "itemSpec",sfba006 as "lowOrderItemNo",imaal003b as "lowOrderItemName",imaal004b as "lowOrderItemSpec",\r\n"productCode","stockCode","warehouseNo","storageSpacesNo",\r\n"lotNo",sfba014 as "lowOrderItemUnitNo",apply as "applyQty",nvl(ee.inag009c,0) as "stockQty",0 as "scanSumQty" FROM   \r\n ( SELECT sfaaent,sfaasite,a.imaal003  imaal003a,a.imaal004 imaal004a,sfba006,b.imaal003 imaal003b,b.imaal004 imaal004b,nvl(sfba021,'' '') as "productCode",\r\n nvl(sfba030,'' '') as "stockCode",sfba014,nvl(sfba019,'' '') as "warehouseNo",nvl(sfba020,'' '') as "storageSpacesNo",nvl(sfba029,'' '') as "lotNo",sum(sfba013-sfba016)-nvl(a,0) apply  FROM sfaa_t                \r\n INNER JOIN sfba_t ON sfaaent = sfbaent AND sfaasite =  sfbasite AND  sfaadocno = sfbadocno    \r\n LEFT JOIN imaal_t a ON a.imaalent = sfaaent AND a.imaal001 = sfaa010 AND a. imaal002 = ''$lang''  \r\n LEFT JOIN imaal_t b ON b.imaalent = sfbaent AND b.imaal001 = sfba006 AND b.imaal002 = ''$lang''   \r\n left join (\r\n select sfdcent,sfdcsite,sfdc001,sfdc004,sum(sfdc007) a from sfdc_t \r\n inner join sfda_t on sfdaent=sfdcent \r\n                  and sfdadocno=sfdcdocno \r\n where sfdastus in (''N'',''Y'')\r\n and sfda002=''11''\r\n group by sfdcent,sfdcsite,sfdc001,sfdc004\r\n ) on sfbaent=sfdcent and sfbasite=sfdcsite and sfbadocno=sfdc001 and sfba006=sfdc004\r\n WHERE sfbaent =''$ent'' AND sfbasite = ''$site'' AND sfaa010 = ''$itemNo''              \r\n AND sfaastus =''F''\r\n AND sfaa003 = ''1''\r\n AND sfba009 = ''N''\r\n AND nvl(sfba013,0) - nvl(sfba016,0) > 0           \r\n GROUP BY sfba006,a.imaal003,a.imaal004,sfba014,sfaaent,sfaasite,b.imaal003,b.imaal004,nvl(sfba021,'' ''),nvl(sfba030,'' ''),nvl(sfba019,'' ''),nvl(sfba020,'' ''),nvl(sfba029,'' ''),a ) dd   \r\n LEFT JOIN(\r\n SELECT inagent,inagsite,inag001,nvl(sum(inag009),0) inag009c from inag_t              \r\n WHERE inag004=''$warehouseNo''\r\n GROUP BY  inagent,inagsite,inag001\r\n) ee ON dd.sfba006=ee.inag001 AND dd.sfaaent=ee.inagent AND dd.sfaasite=ee.inagsite'), STRINGDECODE('{\r\n    "std_data": {\r\n        "execution": {\r\n            "code": "$execution.code",\r\n            "sqlcode": "$execution.sqlcode",\r\n            "description": "$execution.description"\r\n        },\r\n        "parameter": {\r\n            "result": [\r\n                {\r\n                  "listdata": $JSON.toJSONString($sqlData[0])\r\n                }\r\n            ]\r\n        }\r\n    }\r\n}'), STRINGDECODE('依成品发料汇总清单'), '1', '2017-09-19 16:04:46', '2017-10-14 11:43:43', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('95', '1002', 'b022ListGet', STRINGDECODE('工单退料清单列表'), STRINGDECODE('{\n    "std_data": {\n        "parameter": {\n            "ent": "99",\n            "site": "DSCNJ",\n            "lang": "zh_CN",\n            "appmodule": "A001",\n            "docNo": "",\n            "itemNo": "",\n            "lowOrderItemNo": "",\n            "departmentNo": "",\n            "warehouseNo": "",\n            "pagesize": "5",\n            "dateBegin": "2016/01/01",\n            "dateEnd": "2017/12/31"\n        }\n    }\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\n#set ($ent = $bodyparams.ent) \n#set ($site = $bodyparams.site) \n#set ($lang = $bodyparams.lang) \n#set ($appmodule = $bodyparams.appmodule) \n#set ($docNo = $bodyparams.docNo) \n#set ($itemNo = $bodyparams.itemNo) \n#set ($lowOrderItemNo = $bodyparams.lowOrderItemNo)\n#set ($departmentNo= $bodyparams.departmentNo)\n#set ($warehouseNo= $bodyparams.warehouseNo)\n#set ($pagesize = $bodyparams.pagesize) \n#set ($dateBegin = $bodyparams.dateBegin) \n#set ($dateEnd = $bodyparams.dateEnd) \n\nselect sfaadocno as "docNo",imaal003 as "itemName",sfaa010 as "itemNo",ooefl003 as "departmentName",to_char(sfaadocdt,''yyyy/mm/dd'') as "createDate" from (\nselect rownum item,t.* from (\nSELECT DISTINCT sfaadocno ,imaal003 ,sfaa010 ,ooefl003 ,sfaadocdt FROM sfaa_t \n   LEFT JOIN sfba_t ON sfbaent = sfaaent  AND sfaadocno=sfbadocno   \n   LEFT JOIN ooefl_t ON ooeflent = sfaaent AND ooefl001=sfaa017 AND ooefl002 = ''$lang''\n   LEFT JOIN imaal_t ON imaalent = sfaaent AND imaal001 = sfaa010 AND imaal002 = ''$lang''\n  WHERE sfaaent = ''$ent'' \n    AND sfaasite = ''$site''\n    AND sfba016 > 0 \n    AND sfaastus IN (''A'',''F'',''Y'') \n#if ($docNo != '''')  ##工单单号\n  AND sfaadocno LIKE ''%$docNo%''\n#end \n#if ($itemNo != '''')  ##成品料号\n  AND sfaa010 LIKE ''%itemNo%''\n#end \n#if ($lowOrderItemNo != '''')  ##下阶料号\n  AND sfba006 LIKE  ''%lowOrderItemNo%''\n#end \n#if ($departmentNo != '''')  ##部门\n  AND sfaa017 LIKE  ''%departmentNo%''\n#end \n#if ($dateBegin != '''')  ##开始日期\n  AND sfaadocdt >= to_date(''$dateBegin'',''yyyy/mm/dd'')\n#end \n#if ($dateEnd != '''')  ##结束日期\n  AND sfaadocdt <= to_date(''$dateEnd'',''yyyy/mm/dd'')\n#end \nORDER BY sfaadocdt DESC) t)\nwhere item <= ''$pagesize'''), STRINGDECODE('{\n    "std_data": {\n        "execution": {\n            "code": "$execution.code",\n            "sqlcode": "$execution.sqlcode",\n            "description": "$execution.description"\n        },\n        "parameter": {\n            "result": [\n                {\n                  "listdata": $JSON.toJSONString($sqlData[0])\n                }\n            ]\n        }\n    }\n}'), STRINGDECODE('获取工单退料清单列表'), '1', '2017-09-19 16:04:46', '2017-09-19 16:04:46', '0', 't100');
