roductCode", "pmdt007")\r\n#replace("unitNo", "pmdt019")\r\n#replace("price", "pmdt036")\r\n#replace("invoiceNum", "pmdt050")\r\n#replace("docType", "pmds000")\r\n\r\n#if($StringUtils.isNotEmpty($bodyparams.pageNumber)&&$StringUtils.isNotEmpty($bodyparams.pageSize))\r\n  #set ($rnstart = ($Integer.parseInt($bodyparams.pageNumber) - 1) * $Integer.parseInt($bodyparams.pageSize) + 1)\r\n  #set ($rnend = $Integer.parseInt($bodyparams.pageNumber) * $Integer.parseInt($bodyparams.pageSize))\r\n#else \r\n	#set ($rnstart = 1)\r\n	#set ($rnend = 5000)\r\n#end\r\n\r\n\r\nSELECT count(1) as COUNT\r\n  FROM pmds_t\r\n  LEFT OUTER JOIN ooefl_t A1\r\n    ON A1.ooeflent = pmds_t.pmdsent\r\n   AND A1.ooefl001 = pmds_t.pmds003\r\n   AND A1.ooefl002 = ''$lang''\r\n  LEFT OUTER JOIN pmaal_t A2\r\n    ON A2.pmaalent = pmds_t.pmdsent\r\n   AND A2.pmaal001 = pmds_t.pmds007\r\n   AND A2.pmaal002 = ''$lang''\r\n  LEFT OUTER JOIN ooidl_t A3\r\n    ON A3.ooidlent = pmds_t.pmdsent\r\n   AND A3.ooidl001 = pmds_t.pmds040\r\n   AND A3.ooidl002 = ''$lang''\r\n  LEFT OUTER JOIN pmaml_t A4\r\n    ON A4.pmamlent = pmds_t.pmdsent\r\n   AND A4.pmaml001 = pmds_t.pmds039\r\n   AND A4.pmaml002 = ''$lang''\r\n  LEFT OUTER JOIN ooail_t A5\r\n    ON A5.ooailent = pmds_t.pmdsent\r\n   AND A5.ooail001 = pmds_t.pmds037\r\n   AND A5.ooail002 = ''$lang''\r\n  LEFT OUTER JOIN oocql_t A6\r\n    ON A6.oocqlent = pmds_t.pmdsent\r\n   AND A6.oocql001 = ''238''\r\n   AND A6.oocql002 = pmds_t.pmds032\r\n   AND A6.oocql003 = ''$lang''\r\n  LEFT OUTER JOIN ooibl_t A7\r\n    ON A7.ooiblent = pmds_t.pmdsent\r\n   AND A7.ooibl002 = pmds_t.pmds031\r\n   AND A7.ooibl003 = ''$lang''\r\n  LEFT OUTER JOIN pmaal_t A8\r\n    ON A8.pmaalent = pmds_t.pmdsent\r\n   AND A8.pmaal001 = pmds_t.pmds009\r\n   AND A8.pmaal002 = ''$lang''\r\n  LEFT OUTER JOIN pmaal_t A9\r\n    ON A9.pmaalent = pmds_t.pmdsent\r\n   AND A9.pmaal001 = pmds_t.pmds008\r\n   AND A9.pmaal002 = ''$lang''\r\n  LEFT OUTER JOIN ooag_t A10\r\n    ON A10.ooagent = pmds_t.pmdsent\r\n   AND A10.ooag001 = pmds_t.pmds002\r\n  LEFT OUTER JOIN ooef_t A11\r\n    ON A11.ooefent = pmds_t.pmdsent\r\n   AND A11.ooef001 = ''DSCNJ''\r\n  LEFT OUTER JOIN oodbl_t A12\r\n    ON A12.oodblent = pmds_t.pmdsent\r\n   AND A12.oodbl001 = A11.ooef019\r\n   AND A12.oodbl002 = pmds033\r\n   AND A12.oodbl003 = ''$lang''\r\n  LEFT OUTER JOIN (SELECT pmdt_t.*,\r\n                          B1.imaa127  B1_imaa127,\r\n                          B4.imaal003 B4_imaal003,\r\n                          B4.imaal004 B4_imaal004,\r\n                          B3.imaal003 imaal_t_imaal003,\r\n                          B5.oocal003 oocal_t_oocal003,\r\n                          B6.oocal003 B6_oocal003,\r\n                          B7.oocal003 B7_oocal003,\r\n                          B8.qcaol004 qcaol_t_qcaol004,\r\n                          B2.oocql004 B2_oocql004\r\n                     FROM pmdt_t\r\n                     LEFT OUTER JOIN imaa_t B1\r\n                       ON B1.imaaent = pmdt_t.pmdtent\r\n                      AND B1.imaa001 = pmdt_t.pmdt006\r\n                     LEFT OUTER JOIN oocql_t B2\r\n                       ON B2.oocqlent = pmdt_t.pmdtent\r\n                      AND B2.oocql001 = ''2003''\r\n                      AND B2.oocql002 = B1.imaa127\r\n                      AND B2.oocql003 = ''$lang''\r\n                     LEFT OUTER JOIN imaal_t B3\r\n                       ON B3.imaalent = pmdt_t.pmdtent\r\n                      AND B3.imaal001 = pmdt_t.pmdt008\r\n                      AND B3.imaal002 = ''$lang''\r\n                     LEFT OUTER JOIN imaal_t B4\r\n                       ON B4.imaalent = pmdt_t.pmdtent\r\n                      AND B4.imaal001 = pmdt_t.pmdt006\r\n                      AND B4.imaal002 = ''$lang''\r\n                     LEFT OUTER JOIN oocal_t B5\r\n                       ON B5.oocalent = pmdt_t.pmdtent\r\n                      AND B5.oocal001 = pmdt_t.pmdt023\r\n                      AND B5.oocal002 = ''$lang''\r\n 