upplierNo != '''')                   ##帐款供应商\r\n   AND pmds008 like ''%$amountSupplierNo%''\r\n#end \r\n#if ($currency != '''')                           ##币种\r\n   AND pmds037 like ''%$currency%''\r\n#end \r\n#if ($taxType != '''')                            ##税种\r\n   AND pmds033 like ''%$taxType%''\r\n#end \r\n#if ($paymentCondition != '''')                   ##付款条件\r\n   AND ooibl004 like ''%$paymentCondition%''\r\n#end \r\n#if ($site != '''')                               ##据点\r\n   AND pmdtsite like ''%$site%''\r\n#end ;\r\n\r\n\r\n\r\n\r\nselect * from (\r\nSELECT pmdssite as "site",                                ##据点编号\r\n       NVL(trim(site.ooefl003), pmdssite) "siteName",      ##据点名称\r\n       pmds008 as "amountSupplierNo",                     ##帐款供应商\r\n       A7.ooibl004 as "paymentCondition",                 ##付款条件\r\n       pmds033 as "taxType",                              ##税种\r\n       A12.oodbl004 as "taxTypeName",                     ##税种名称\r\n       pmds037 as "currency",                             ##币种\r\n       A5.ooail003 as "currencyName",                     ##人民币\r\n       pmdsdocno as "docNo",                              ##入库单号\r\n       pmdtseq as "seq",                                  ##入库项次\r\n       to_char(pmdsdocdt ,''YYYY-MM-DD'') as "createDate",                         ##单据日期\r\n       pmdt001 as "purchaseNo",                           ##采购单号\r\n       pmdt002 as "purchaseSeq",                          ##采购项次\r\n       pmdt003 as "purchaseLineSeq",                      ##采购项序\r\n       pmdt004 as "purchaseBatchSeq",                     ##采购分批序\r\n       pmdt006 as "itemNo",                               ##料号\r\n       x.B4_imaal003 as "itemName",                       ##品名\r\n       x.B4_imaal004 as "itemSpec",                       ##规格\r\n       pmdt007 as "productCode",                          ##产品特征\r\n       CASE\r\n         WHEN pmdt020 IS NULL THEN                        ##已入库量\r\n          0\r\n         ELSE\r\n          (CASE\r\n            WHEN pmdS000 = ''7'' THEN\r\n             (CASE\r\n               WHEN pmdt001 IS NOT NULL THEN\r\n                (CASE\r\n                  WHEN pmdt020 IS NULL THEN\r\n                   0\r\n                  ELSE\r\n                   (pmdt020 * (-1))\r\n                END)\r\n               ELSE\r\n                (pmdt020 * (-1))\r\n             END)\r\n            ELSE\r\n             (CASE\r\n               WHEN pmdt001 IS NOT NULL THEN\r\n                (CASE\r\n                  WHEN pmdt020 IS NULL THEN\r\n                   0\r\n                  ELSE\r\n                   pmdt020\r\n                END)\r\n               ELSE\r\n                pmdt020\r\n             END)\r\n          END)\r\n       END as "stockInQty",\r\n       pmdt019 as "unitNo", ##单位\r\n       pmdt036 as "price", ##单价\r\n       CASE\r\n         WHEN pmds000 = ''7'' THEN    ##税额\r\n          pmdt047 * (-1)\r\n         ELSE\r\n          pmdt047\r\n       END as "currTax",\r\n       CASE\r\n         WHEN pmds000 = ''7'' THEN    ##税前金额 \r\n          pmdt038 * (-1)\r\n         ELSE\r\n          pmdt038\r\n       END as "beforeTax",\r\n       CASE\r\n         WHEN pmds000 = ''7'' THEN    ##含税金额 \r\n          pmdt039 * (-1)\r\n         ELSE\r\n          pmdt039\r\n       END as "includTax",\r\n       pmdt050 as "invoiceNum", ##发票号码 \r\n       CASE\r\n         WHEN pmdt056 IS NULL THEN  \r\n          0\r\n         ELSE\r\n          (CASE\r\n            WHEN pmdS000 = ''7'' THEN\r\n             (CASE\r\n               WHEN pmdt001 IS NOT NULL THEN\r\n                (CASE\r\n                  WHEN pmdt020 IS NULL THEN\r\n                   0\r\n                  ELSE\r\n                   (pmdt056 * (-1))\r\n                END)\r\n               ELSE\r\n                (pmdt056 * (-1))\r\n             END)\r\n            ELSE\r\n             (CASE\r\n               WHEN pmdt001 IS NOT NULL THEN\r\n                (CASE\r\n                  WHEN pmdt020 IS NULL