IKE ''%$bodyparams.fuzzyQuery%''\r\n           OR a.pmdssite LIKE ''%$bodyparams.fuzzyQuery%''\r\n           OR a.pmds002  LIKE ''%$bodyparams.fuzzyQuery%''\r\n           OR a.pmds010  LIKE ''%$bodyparams.fuzzyQuery%''\r\n           OR b.pmdt006  LIKE ''%$bodyparams.fuzzyQuery%''\r\n           OR b.pmdt007  LIKE ''%$bodyparams.fuzzyQuery%''\r\n           OR k.imaal003 LIKE ''%$bodyparams.fuzzyQuery%''\r\n           OR k.imaal004 LIKE ''%$bodyparams.fuzzyQuery%'') \r\n   #end ;\r\nSELECT * FROM (\r\n   SELECT \r\n        a.pmdssite "siteNo",                                                           /*企业据点*/\r\n        site.ooefl003 "siteName",                                                      /*据点名称*/\r\n        a.pmdsdocno "prinSideNo",                                                      /*验退单号*/ \r\n        b.pmdtseq "seq",                                                               /*项次*/    \r\n        b.pmdt001 "purchaseNo",                                                        /*采购单号*/\r\n        b.pmdt002 "purchaseSeq",                                                       /*采购项次*/\r\n        b.pmdt003 "purchaseLineSeq",                                                   /*采购项序*/\r\n        b.pmdt004 "purchaseBatchSeq",                                                  /*采购分批序*/\r\n        b.pmdt027 "receiptNo",                                                         /*收货单号*/\r\n        b.pmdt028 "receiptSeq",                                                        /*收货项次*/    \r\n        to_char(a.pmdsdocdt,''YYYY-MM-DD'') "prformDate",                                /*验退日期*/\r\n        replace(replace(a.pmds011||''-''||NVL(trim(sc11.gzcbl004),a.pmds011),''委外采购'',''来料加工''),''采购'',''订单'') "purchaseType",             /*采购类型*/    \r\n        a.pmds037 "currency",                                                          /*币种*/\r\n        NVL(c.ooail003,a.pmds037) "currencyName",                                      /*币种说明*/\r\n        e.ooef016 "localCurrency