/*单据建立者*/\r\n        NVL(trim(j.ooag011),a.pmdscrtid) "creatorName",                                /*单据建立者名称*/\r\n        nvl(to_char(a.pmdsmoddt,''yyyy-mm-dd hh24:mi:ss''),to_char(a.pmdscrtdt,''yyyy-mm-dd hh24:mi:ss'')) "modifyTime",  /*单据修改时间*/   \r\n        b.pmdt006 "itemNo",                                    /*料件编号*/\r\n        NVL(trim(k.imaal003),b.pmdt006) "itemName",            /*料件名称*/\r\n        NVL(trim(k.imaal004),b.pmdt006) "itemSpec",            /*料件规格*/\r\n        b.pmdt016 "wareHouseNo",                               /*库位*/\r\n        NVL(trim(inay.inayl003),b.pmdt016) "wareHouseName",    /*库位说明*/\r\n        b.pmdt017 "storageSpaceNo",                            /*储位*/\r\n        NVL(trim(m.inab003),b.pmdt017) "storageSpaceName",     /*储位说明*/\r\n        b.pmdt020 "returnQty",                                 /*验退数量*/\r\n        b.pmdt022 "referenceQty",                              /*参考数量*/\r\n        b.pmdt024 "valuationQty",                              /*计价数量*/\r\n        b.pmdt019 "returnUnitNo",                              /*验退单位*/\r\n        NVL(n.oocal003,b.pmdt019) "returnUnitName",            /*单位说明*/\r\n        b.pmdt021 "referenceUnitNo",                           /*参考单位*/\r\n        NVL(trim(r.oocal003),b.pmdt021) "referenceUnitName",   /*单位说明*/\r\n        b.pmdt023 "valuationUnitNo",                           /*计价单位*/\r\n        NVL(o.oocal003,b.pmdt023) "valuationUnitName",         /*单位说明*/\r\n        b.pmdt005 "sonProdFeature",                            /*子件特性*/\r\n        b.pmdt007 "prodFeature",                               /*产品特征*/\r\n        b.pmdt046 "tax",                                       /*税别*/\r\n        NVL(trim(ol.oodbl004), b.pmdt046) "taxName",           /*税别说明*/\r\n        NVL(b.pmdt037/100,0) "taxRate",                        /*税率*/        \r\n        decode(re.oodb005,''Y'', round(b.pmdt036 / (1 + nvl(a.pmds034, 0) / 100), g.ooaj003), b.pmdt036) "price",     /*采购单价*/\r\n        decode(re.oodb005, ''Y'', b.pmdt036, round(b.pmdt036 * (1 + nvl(a.pmds034, 0) / 100), g.ooaj003)) "taxPrice", /*采购含税单价*/\r\n        b.pmdt038 "noTaxAmount",                               /*未税金额*/\r\n        b.pmdt039 "taxAmount",                                 /*含税金额*/\r\n        b.pmdt018 "lotNo",                                     /*批号*/\r\n        b.pmdt051||''-''||q.oocql004 "returnReasonNo",           /*退货理由*/\r\n        a.pmds045 "remarkHead",                                /*单头备注*/\r\n        b.pmdt059 "remarkBody",                                 /*单身备注*/\r\n        NVL(a.pmds010,'' '') "deliveryNo",/*发货单号*/\r\n        DENSE_RANK() OVER( ORDER BY $orderBy a.pmdssite, a.pmdsdocno desc, b.pmdtseq) AS RANK \r\n   FROM pmdt_t b \r\n   INNER JOIN pmds_t a  ON a.pmdsent=b.pmdtent AND a.pmdsdocno=b.pmdtdocno    \r\n   INNER JOIN ooef_t e ON (e.ooefent=b.pmdtent AND e.ooef001=b.pmdtsite) \r\n   LEFT JOIN oodb_t re ON e.ooefent = re.oodbent AND e.ooef019 = re.oodb001 AND b.pmdt046 = re.oodb002                                                                      \r\n   INNER JOIN ooaj_t g ON (b.pmdtent=g.ooajent AND e.ooef014=g.ooaj001 AND a.pmds037=g.ooaj002)                                                                                                                                           \r\n   LEFT JOIN imaal_t k ON k.imaalent=b.pmdtent AND k.imaal001=b.pmdt006 AND k.imaal002=''$lang''                                \r\n   LEFT JOIN inaa_t l  ON l.inaaent =b.pmdtent AND l.inaasite=b.pmdtsite AND l.inaa001=b.pmdt016                        \r\n   LEFT JOIN inab_t m  ON m.inabent =b.pmdtent AND m.inabsite=b.pmdtsite AND m.inab001=b.pmdt016 AND m.inab002=b.pmdt017\r\n   LEFT JOIN oocal_t n ON n.oocalent=b.pmdtent AND n.oocal001=b.pmdt019 AND n.oocal002=''$lang''                                \r\n   LEFT JOIN oocal_t o ON o.oocalent=b.pmdtent AND o.oocal001=b.pmdt023 AND o.oocal002=''$lang''  \r\n   LEFT JOIN oocal_t r ON r.oocalent=b.pmdtent AND r.oocal001=b.pmdt021 AND r.oocal002=''$lang''                                                                        \r\n   LEFT JOIN oocql_t q ON b.pmdtent = q.oocqlent AND b.pmdt051 = q.oocql002 AND q.oocql001 =''314'' AND q.oocql003 = ''$lang''                                \r\n   LEFT JOIN inayl_t inay ON inay.inaylent = b.pmdtent AND inay.inayl001 = b.pmdt016 AND inay.inayl002=''$lang''\r\n   LEFT JOIN oodbl_t ol ON ol.oodblent = b.pmdtent AND ol.oodbl001 = e.ooef019 AND ol.oodbl002=b.pmdt046 AND ol.oodbl003=''$lang''\r\n   LEFT JOIN pmaal_t b1 ON b1.pmaalent = a.pmdsent AND b1.pmaal001 = a.pmds007 AND b1.pmaal002=''$lang''\r\n   LEFT JOIN pmaal_t b2 ON b2.pmaalent = a.pmdsent AND b2.pmaal001 = a.pmds007 AND b2.pmaal002=''$lang''\r\n   LEFT JOIN ooail_t c ON c.ooailent = a.pmdsent AND c.ooail001=a.pmds037 AND c.ooail002=''$lang''\r\n   LEFT JOIN ooail_t c1 ON c1.ooailent = a.pmdsent AND c1.ooail001=e.ooef016 AND c1.ooail002=''$lang''\r\n   LEFT JOIN oocql_t i ON i.oocqlent = a.pmdsent AND i.oocql001 = ''238'' AND i.oocql002=a.pmds032 AND i.oocql003=''$lang''\r\n   LEFT JOIN ooibl_t h ON h.ooiblent = a.pmdsent AND h.ooibl002=a.pmds031 AND h.ooibl003=''$lang''\r\n   LEFT JOIN gzcb_t sc2 ON sc2.gzcb001 = ''2087'' AND sc2.gzcb002 = a.pmds048\r\n   LEFT JOIN gzcbl_t sc3 ON sc2.gzcb002 = sc3.gzcbl002 AND sc2.gzcb001 = sc3.gzcbl001 AND sc3.gzcbl003 = ''$lang''  \r\n   LEFT JOIN gzcb_t sc4 ON sc4.gzcb001 = ''2086'' AND sc4.gzcb002 = a.pmds049\r\n   LEFT JOIN gzcbl_t sc5 ON sc4.gzcb002 = sc5.gzcbl002 AND sc4.gzcb001 = sc5.gzcbl001 AND sc5.gzcbl003 = ''$lang''  \r\n   LEFT JOIN gzcb_t sc8 ON sc8.gzcb001 = ''2026'' AND sc8.gzcb002 = a.pmds036\r\n   LEFT JOIN gzcbl_t sc9 ON sc8.gzcb002 = sc9.gzcbl002 AND sc8.gzcb001 = sc9.gzcbl001 AND sc9.gzcbl003 = ''$lang''  \r\n   LEFT JOIN ooefl_t site ON site.ooeflent = a.pmdsent AND site.ooefl001 = a.pmdssite AND site.ooefl002=''$lang''\r\n   LEFT JOIN gzcb_t sc10 ON sc10.gzcb001 = ''2052'' AND sc10.gzcb002 = a.pmds011\r\n   LEFT JOIN gzcbl_t sc11 ON sc10.gzcb002 = sc11.gzcbl002 AND sc10.gzcb001 = sc11.gzcbl001 AND sc11.gzcbl003 = ''$lang'' \r\n   LEFT JOIN ooag_t j ON j.ooagent = a.pmdsent AND j.ooag001 = a.pmdscrtid \r\n  WHERE a.pmdsent = ''$ent''\r\n   #if ($site != '''')\r\n      AND a.pmdssite LIKE ''%$site%''\r\n   #end \r\n   AND a.pmdsstus= ''Y''\r\n   AND a.pmds000 in(''5'',''10'') \r\n   AND a.pmds011 IN (''1'',''2'')\r\n   #if ($dateBegin != '''')\r\n      AND a.pmdsdocdt>=to_date(''$dateBegin'',''yyyy/mm/dd'')\r\n   #end  \r\n   #if ($dateEnd != '''')\r\n      AND a.pmdsdocdt<=to_date(''$dateEnd'',''yyyy/mm/dd'')\r\n   #end  \r\n   #if ($purchaseType != '''')\r\n      AND a.pmds011 LIKE ''%$purchaseType%''\r\n   #end  \r\n   #if ($supplierNo != '''')\r\n      AND a.pmds007=''$supplierNo''\r\n   #end\r\n   #if ($prinSideNo != '''')\r\n      AND a.pmdsdocno LIKE ''%$prinSideNo%''\r\n   #end     \r\n   #if ($itemNo != '''')\r\n      AND b.pmdt006 LIKE ''%$itemNo%''\r\n   #end      \r\n   #if ($itemName != '''')\r\n      AND k.imaal003 LIKE ''%$itemName%''\r\n   #end    \r\n   #if ($itemSpec != '''')\r\n      AND k.imaal004 LIKE ''%$itemSpec%''\r\n   #end \r\n   #if ($itemFeatureNo != '''')\r\n      AND b.pmdt007 LIKE ''%$itemFeatureNo%''\r\n   #end \r\n   #if ($bodyparams.fuzzyQuery != '''' ) \r\n      AND (  a.pmdsdocno LIKE ''%$bodyparams.fuzzyQuery%''\r\n           OR a.pmdssite LIKE ''%$bodyparams.fuzzyQuery%''\r\n           OR a.pmds002  LIKE ''%$bodyparams.fuzzyQuery%''\r\n           OR a.pmds010  LIKE ''%$bodyparams.fuzzyQuery%''\r\n           OR b.pmdt006  LIKE ''%$bodyparams.fuzzyQuery%''\r\n           OR b.pmdt007  LIKE ''%$bodyparams.fuzzyQuery%''\r\n           OR k.imaal003 LIKE ''%$bodyparams.fuzzyQuery%''\r\n           OR k.imaal004 LIKE ''%$bodyparams.fuzzyQuery%'') \r\n   #end \r\n  ORDER BY $orderBy a.pmdssite, a.pmdsdocno desc, b.pmdtseq \r\n   )\r\nWHERE RANK >= $rnstart\r\n  AND RANK <= $rnend\r\nORDER BY RANK'), STRINGDECODE('{\r\n    "std_data": {\r\n        "execution": {\r\n            "code": "$execution.