INSERT INTO `iccn_ica_service` VALUES ('119', '1001', 'round_qty', STRINGDECODE('单位小数位转换'), STRINGDECODE('{\r\n	"std_data": {\r\n		"parameter": {\r\n			"ent": "99",\r\n			"site": "DSCNJ",\r\n			"receivedQty": "101.787",\r\n			"unitNo": "KG"\r\n		}\r\n	}\r\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\r\n#set ($ent = $bodyparams.ent)\r\n#set ($receivedQty= $bodyparams.receivedQty)\r\n#set ($unitNo = $bodyparams.unitNo)\r\n\r\n\r\nSELECT round(nvl(''$receivedQty'', 0), ooca002) as "convertQty",\r\n       DECODE(''$receivedQty'',round(nvl(''$receivedQty'', 0), ooca002),0,1) as "check", \r\n       ooca002 as "decimalPlace"\r\n  FROM ooca_t \r\n WHERE oocaent = ''$ent''\r\n   AND ooca001 = ''$unitNo'''), STRINGDECODE('{\r\n    "std_data": {\r\n        "execution": {\r\n            "code": "$execution.code",\r\n            "sqlcode": "$execution.sqlcode",\r\n            "description": "$execution.description"\r\n        },\r\n        "parameter": {\r\n            "result": [\r\n                {\r\n                    "listdata": $JSON.toJSONString($sqlData[0])\r\n                }\r\n            ]\r\n        }\r\n    }\r\n}'), '', '1', '2017-09-26 10:51:51', '2017-10-19 10:17:54', '0', 't100');
