de": "$execution.sqlcode",\r\n            "description": "$execution.description"\r\n        },\r\n        "parameter": {\r\n            "result": [\r\n                {\r\n                  "listdata": $JSON.toJSONString($sqlData[0])\r\n                }\r\n            ]\r\n        }\r\n    }\r\n}'), '', '1', '2017-09-26 11:38:49', '2017-09-28 17:11:37', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('121', '1001', 'purchase_storageSpaces_list', STRINGDECODE('采购所有仓库储位查询'), STRINGDECODE('{\r\n	"bodyMap": {\r\n		"std_data": {\r\n			"parameter": {\r\n				"ent": "99",\r\n				"site": "DSCNJ",\r\n				"lang": "zh_CN",\r\n				"warehouseNo": "001",\r\n				"warehouseFuzzy": "",\r\n				"storageSpacesNo": [],\r\n				"storageSpacesFuzzy": "",\r\n                                "supplierNo": ""				\r\n			}\r\n		}\r\n	}\r\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\r\n#set ($ent = $bodyparams.ent)\r\n#set ($site = $bodyparams.site)\r\n#set ($lang = $bodyparams.lang)\r\n#set ($warehouseFuzzy = $bodyparams.warehouseFuzzy)\r\n#set ($storageSpacesFuzzy = $bodyparams.storageSpacesFuzzy)\r\n#if ($lang != '''')\r\n#set ($lang = ''zh_CN'') \r\n#end\r\n\r\nSELECT DISTINCT a.inaasite "site",                                            /*据点编号*/\r\n       NVL(trim(site.ooefl003),a.inaasite) "siteName",               /*据点名称*/\r\n       a.inaa001 "warehouseNo",                                      /*库位编号*/\r\n       c.inayl003 "warehouseName",                                   /*库位名称*/\r\n       b.inab002 "storageSpacesNo",                                  /*储位编号*/\r\n       DECODE(b.inab002,'' '',b.inab002,b.inab003) "storageSpacesName" /*储位编号*/ \r\n  FROM pmdl_t \r\n  INNER JOIN pmdn_t ON pmdnent = pmdlent AND pmdnsite = pmdlsite AND pmdndocno = pmdldocno\r\n  INNER JOIN inaa_t a ON a.inaaent = pmdlent AND a.inaasite = pmdlsite AND a.inaa001 = pmdn028\r\n  INNER JOIN inab_t b ON b.inabent = a.inaaent AND b.inabsite = a.inaasite AND b.inab001 = a.inaa001 AND trim(inab002) IS NOT NULL\r\n   LEFT JOIN inayl_t c ON c.inaylent= a.inaaent AND c.inayl001 = a.inaa001 AND c.inayl002=''$lang''\r\n   LEFT JOIN ooefl_t site ON site.ooeflent = a.inaaent AND site.ooefl001=a.inaasite AND site.ooefl002=''$lang'' \r\n WHERE pmdnent = ''$ent'' -- 企业编号\r\n   AND pmdlsite = ''$site''  -- 据点\r\n   AND pmdlstus = ''Y''\r\n #if ($StringUtils.isNotEmpty($bodyparams.supplierNo))\r\n   AND pmdl004 =   ''$bodyparams.supplierNo''\r\n #end \r\n #if ($StringUtils.isNotEmpty($bodyparams.warehouseNo))\r\n 		AND a.inaa001 = ''$bodyparams.warehouseNo''\r\n #end \r\n #if ($bodyparams.storageSpacesNo.size() > 0)\r\n	  AND b.inab002 in $VEUtils.SqlIn($bodyparams.storageSpacesNo)\r\n #end \r\n #if($StringUtils.isNotEmpty($warehouseFuzzy))\r\n	 AND (    a.inaa001 LIKE ''$warehouseFuzzy%'' \r\n	       OR c.inayl003 LIKE ''%$warehouseFuzzy%'')\r\n #end \r\n #if($StringUtils.isNotEmpty($storageSpacesFuzzy))\r\n	 AND (    b.inab002 LIKE ''$storageSpacesFuzzy%'' \r\n	       OR b.inab003 LIKE ''%$storageSpacesFuzzy%'')\r\n #end'), STRINGDECODE('{\r\n    "std_data": {\r\n        "execution": {\r\n            "code": "$execution.code",\r\n            "sqlcode": "$execution.sqlcode",\r\n            "description": "$execution.description"\r\n        },\r\n        "parameter": {\r\n            "result": [\r\n                {\r\n                  "listdata": $JSON.toJSONString($sqlData[0])\r\n                }\r\n            ]\r\n        }\r\n    }\r\n}'), '', '1', '2017-09-26 11:48:37', '2017-09-28 16:58:41', '0', 't100');
