INSERT INTO `iccn_ica_service` VALUES ('124', '1002', 'stockInfoGet', STRINGDECODE('获取料件库存信息'), STRINGDECODE('{\r\n    "std_data": {\r\n        "parameter": {\r\n            "ent": "99",\r\n            "site": "DSCNJ",\r\n            "lang": "zh_CN",\r\n            "appmodule": "D004",\r\n            "timestamp": "",\r\n            "itemNo": "''TESTMSN082106'',''TESTMSN082107''",\r\n            "productCode": "",\r\n            "warehouseNo":"CCT1001",\r\n            "storageSpacesNo":"''001'',''002''"\r\n        }\r\n    }\r\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\r\n#set ($ent = $bodyparams.ent) \r\n#set ($site = $bodyparams.site) \r\n#set ($lang = $bodyparams.lang)\r\n#set ($appmodule= $bodyparams.appmodule) \r\n#set ($timestamp = $bodyparams.timestamp) \r\n#set ($itemNo = $bodyparams.itemNo)\r\n#set ($productCode = $bodyparams.productCode )\r\n#set ($warehouseNo= $bodyparams.warehouseNo)\r\n#set ($storageSpacesNo= $bodyparams.storageSpacesNo)\r\n\r\n\r\nselect inagent as "ent",inagsite as "site",inag001 as "itemNo",imaal003 as "itemName",\r\n       imaal004 as "itemSpec",nvl(inag002,'' '') as "productCode",nvl(inag003,'' '') as "stockCode",\r\n       nvl(inag004,'' '') as "warehouseNo",nvl(inag005,'' '') as "storageSpacesNo",nvl(inag006,'' '') as "lotNo",\r\n       inag008 as "qty",inag007 as "unitNo"\r\n  from inag_t\r\n  left join imaal_t on imaalent=inagent and imaal001=inag001 and imaal002=''zh_CN''\r\n where inagent = ''$ent''\r\n   and inagsite = ''$site''\r\n#if($itemNo != '''')\r\n   and inag001 in ($itemNo)\r\n#end\r\n#if($warehouseNo != '''')\r\n   and inag004 = ''$warehouseNo''\r\n#end\r\n#if($storageSpacesNo != '''')\r\n   and inag005 in ($storageSpacesNo)\r\n#end\r\n#if($productCode!= '''')\r\n   and inag002 in ($productCode) \r\n#end'), STRINGDECODE('{\r\n    "std_data": {\r\n        "execution": {\r\n            "code": "$execution.code",\r\n            "sqlcode": "$execution.sqlcode",\r\n            "description": "$execution.description"\r\n        },\r\n        "parameter": {\r\n            "result": [\r\n                {\r\n                  "listdata": $JSON.toJSONString($sqlData[0])\r\n                }\r\n            ]\r\n        }\r\n    }\r\n}'), STRINGDECODE('获取料件库存信息'), '1', '2017-09-27 15:07:38', '2017-10-12 20:40:41', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('126', '1002', 'b017ListGet', STRINGDECODE('工单入库上架清单列表'), STRINGDECODE('{\r\n    "std_data": {\r\n        "parameter": {\r\n            "ent": "99",\r\n            "site": "DSCNJ",\r\n            "lang": "zh_CN",\r\n            "appmodule": "B017",\r\n            "docNo": "",\r\n            "itemNo": "",\r\n            "departmentNo": "",\r\n            "pagesize": "10",\r\n            "dateBegin": "2017/10/18",\r\n            "dateEnd": "2017/10/19"\r\n        }\r\n    }\r\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\r\n#set ($ent = $bodyparams.ent) \r\n#set ($site = $bodyparams.site) \r\n#set ($lang = $bodyparams.lang) \r\n#set ($appmodule = $bodyparams.appmodule) \r\n#set ($docNo = $bodyparams.docNo) \r\n#set ($itemNo = $bodyparams.itemNo) \r\n#set ($departmentNo= $bodyparams.departmentNo)\r\n#set ($pagesize = $bodyparams.pagesize) \r\n#set ($dateBegin = $bodyparams.dateBegin) \r\n#set ($dateEnd = $bodyparams.dateEnd)\r\n\r\nselect sfaadocno as "docNo",imaal003 as "itemName",sfaa010 as "itemNo",imaal004 as "itemSpec",nvl(sfaa017,'' '') as "departmentNo",\r\nnvl(ooefl003,'' '') as "departmentName",to_char(sfaadocdt,''yyyy/mm/dd'') as "createDate",sfaa012 as "applyQty",sfaa050 as "stockInQty" from (\r\nselect rownum item,t.* from (\r\nSELECT sfaadocno,imaal003,sfaa010,imaal004,sfaa017,ooefl003,sfaadocdt,sfaa012,sfaa050 FROM sfaa_t \r\n   LEFT JOIN imaal_t ON imaalent = sfaaent AND imaal001 = sfaa010 AND imaal002 = ''$lang''\r\n   LEFT JOIN ooefl_t ON ooeflent = sfaaent AND ooefl001=sfaa017 AND ooefl002 = ''$lang''\r\n   LEFT JOIN (\r\n              select sfacent,sfacdocno,sum(sfac003)-sum(sfac005) t01 from sfac_t\r\n              group by sfacent,sfacdocno\r\n   ) on sfacent=sfaaent and sfacdocno=sfaadocno\r\n   LEFT JOIN (\r\n              select sfebent,sfeb001,sum(sfeb008) t02\r\n              from sfeb_t\r\n              inner join sfea_t\r\n              on sfeaent = sfebent\r\n              and sfeadocno = sfebdocno\r\n              where sfeastus in (''Y'',''N'')\r\n              group by sfebent,sfeb001\r\n   ) on sfebent=sfaaent and sfeb001=sfaadocno\r\n  WHERE sfaaent = ''$ent'' \r\n    AND sfaasite = ''$site''\r\n    AND sfaastus = ''F''\r\n    AND t01-nvl(t02,0) > 0\r\n  #if ($docNo != '''')     ##工单单号\r\n    AND sfaadocno LIKE ''%$docNo%''\r\n  #end \r\n  #if ($itemNo != '''')     ##成品料号\r\n    AND sfaa010 LIKE ''%$itemNo%''\r\n  #end \r\n  #if ($departmentNo != '''')     ##部门\r\n    AND sfaa017 LIKE  ''%$departmentNo%''\r\n  #end \r\n  #if ($dateBegin != '''')    ##开始日期\r\n    AND sfaadocdt >= to_date(''$dateBegin'',''yyyy/mm/dd'')\r\n  #end \r\n  #if ($dateEnd != '''')     ##结束日期\r\n    AND sfaadocdt <= to_date(''$dateEnd'',''yyyy/mm/dd'')\r\n  #end\r\nORDER BY sfaadocdt DESC,sfaadocno) t\r\n) where item <= ''$pagesize'''), STRINGDECODE('{\r\n    "std_data": {\r\n        "execution": {\r\n            "code": "$execution.code",\r\n            "sqlcode": "$execution.sqlcode",\r\n            "description": "$execution.description"\r\n        },\r\n        "parameter": {\r\n            "result": [\r\n                {\r\n                  "listdata": $JSON.toJSONString($sqlData[0])\r\n                }\r\n            ]\r\n        }\r\n    }\r\n}'), STRINGDECODE('工单入库上架清单列表'), '1', '2017-10-11 20:58:42', '2017-10-19 17:23:20', '0', 't100');
INSERT INTO `iccn_ica_service` VALUES ('129', '1005', 'purchase_header_query_1005', STRINGDECODE('采购单单头'), STRINGDECODE('{\r\n	"std_data": {\r\n		"parameter": {\r\n			"ent": "99",\r\n			"site": "DSCNJ",\r\n			"fuzzyQuery": "",\r\n			"datetimeS": "2017-08-01",\r\n			"datetimeE": "2017-09-22",\r\n			"purchaseNo": [],\r\n			"purchaseType": "1",\r\n			"itemNo": [],\r\n			"purchaseNoLike": "",\r\n			"itemNoLike": "TEST",\r\n			"itemName": "",\r\n			"itemSpec": "",\r\n			"itemFeatureNo": "",\r\n			"purchaseDeliveryDateS": "2017/09/15",\r\n			"purchaseDeliveryDateE": "2017/09/22",\r\n			"purchasesman": "",\r\n			"supplierNo": "",\r\n			"replyPurchaseOpinions": "",\r\n			"pageNumber": "1",\r\n			"pageSize": "5",\r\n			"orderBy": "site,modifyDatetime"\r\n		}\r\n	}\r\n}'), STRINGDECODE('#set ($bodyparams = $bodyMap.std_data.parameter)\r\n#set ($ent = $bodyparams.ent) \r\n#set ($lang = $bodyparams.lang) \r\n#if ($lang != '''')\r\n#set ($lang = ''zh_CN'') \r\n#end\r\n#set ($datetimeS = $bodyparams.datetimeS) \r\n#set ($datetimeE = $bodyparams.datetimeE) \r\n#set ($purchaseNoLike = $bodyparams.purchaseNoLike)\r\n#set ($itemNoLike = $bodyparams.itemNoLike)\r\n#set ($itemName = $bodyparams.itemName)\r\n#set ($itemSpec = $bodyparams.itemSpec)\r\n#set ($itemFeatureNo = $bodyparams.itemFeatureNo)\r\n#set ($purchaseDeliveryDateS = $bodyparams.purchaseDeliveryDateS) \r\n#set ($purchaseDeliveryDateE = $bodyparams.purchaseDeliveryDateE) \r\n#set ($site = $bodyparams.site) \r\n\r\n\r\n#if($StringUtils.isNotEmpty($bodyparams.pageSize)&&$StringUtils.isNotEmpty($bodyparams.pageNumber))\r\n  #set ($rnend = $Integer.parseInt($bodyparams.pageNumber) * $Integer.parseInt($bodyparams.pageSize))\r\n  #set ($rnstart = ($Integer.parseInt($bodyparams.pageNumber) - 1) * $Integer.parseInt($bodyparams.pageSize) + 1)\r\n#else \r\n	#set ($rnstart = 1)\r\n	#set ($rnend = 5000)\r\n#end\r\n\r\n\r\n\r\n#set ($orderBy= $bodyparams.orderBy) \r\n#if ($orderBy!= '''')\r\n#set ($orderBy=$orderBy+'','') \r\n#end\r\n