CREATE TABLE  IF NOT  EXISTS `iccn_rfa_service` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT STRINGDECODE('主键'),
  `url_id` varchar(50) DEFAULT NULL COMMENT STRINGDECODE('url链接id'),
  `service_code` varchar(255) DEFAULT NULL COMMENT STRINGDECODE('服务编码'),
  `service_name` varchar(255) DEFAULT NULL COMMENT STRINGDECODE('服务名称'),
