  `description` varchar(255) DEFAULT NULL COMMENT STRINGDECODE('描述'),
  `url` varchar(255) DEFAULT NULL COMMENT STRINGDECODE('请求地址'),
  `headers` varchar(1024) DEFAULT NULL COMMENT STRINGDECODE('请求头信息'),
  `method` varchar(255) DEFAULT NULL COMMENT STRINGDECODE('请求方式'),
  `content_type` varchar(11) DEFAULT NULL COMMENT STRINGDECODE('解析类型'),
  `status` int(11) DEFAULT NULL COMMENT STRINGDECODE('状态  0:关闭 1:启用'),
  `create_time` datetime DEFAULT NULL COMMENT STRINGDECODE('创建时间'),
  `update_time` datetime DEFAULT NULL COMMENT STRINGDECODE('更新时间'),
  `del_flag` char(1) NOT NULL DEFAULT '0' COMMENT STRINGDECODE('删除标记'),
  PRIMARY KEY (`id`)  
) ;

